contract Guardian is AccessControl {
    using SafeMath for uint256;
    using ECDSA for bytes32;
    using Counters for Counters.Counter;

    IERC721 NFT;

    Counters.Counter private tokenIdTracker;

    uint256 private tokenIdTrackerReserved;
    uint256 private perWalletCap;
    uint256 private totalSupply;
    uint256 private totalWhitelistSupply;
    uint256 private whitelistMintedSupply;
    uint256 private totalReservedSupply;
    uint256 private reservedMintedSupply;

    bool private isWhitelistSaleActive;
    
    address private reservedMintSigner;
    address private whitelistMintSigner;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    mapping(bytes => bool) private signatures;
    mapping(address => uint256) private userWalletCap;

    event WhitelistMint(address indexed _beneficiary, uint256 indexed _tokenId);
    event ReservedMint(address indexed _beneficiary, uint256 indexed _tokenId);
    event ReservedMintSignerUpdated(address indexed _singerAddress);
    event WhitelistMintSignerUpdated(address indexed _singerAddress);
    event UpdatePerWalletCap(uint256 indexed _perWalletCap);
    event UpdateNFTContractAddress(address indexed _nftAddress);

    constructor(
        uint256 _totalSupply,
        uint256 _totalWhitelistSupply,
        uint256 _totalReservedSupply,
        uint256 _perWalletCap,
        address _reservedMintSigner,
        address _whitelistMintSigner,
        address _nftContractAddress
    ) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(ADMIN_ROLE, _msgSender());

        tokenIdTracker.increment();
        tokenIdTrackerReserved = 990;
        perWalletCap = _perWalletCap;

        totalSupply = _totalSupply; // 999
        totalWhitelistSupply = _totalWhitelistSupply;
        totalReservedSupply = _totalReservedSupply;

        isWhitelistSaleActive = false;
        reservedMintSigner = _reservedMintSigner;
        whitelistMintSigner = _whitelistMintSigner;
        NFT = IERC721(_nftContractAddress);
    }

    function reservedMint(
        uint256 _timestamp,
        address _contractAddress,
        bytes32 _msgHash,
        bytes memory _signature
    ) public {
        require(
            _contractAddress != address(0),
            "Guardian Contract: Invalid Address!"
        );
        require(
            !signatures[_signature],
            "Guardian Contract: Signature already used!"
        );

        require(
            reservedMintedSupply.add(1) <= totalReservedSupply,
            "Guardian Contract: Mint amount exceeds reserved supply!"
        );

        bytes32 msgHash = getMessageHash(msg.sender, _timestamp,_contractAddress); // create msghash with address and a number
        bytes32 signedMsgHash = msgHash.toEthSignedMessageHash();

        require(
            _msgHash == signedMsgHash,
            "Guardian Contract: Invalid message hash!"
        );
        require(
            _msgHash.recover(_signature) == reservedMintSigner,
            "Guardian Contract: Invalid signer!"
        );

        signatures[_signature] = true;
        reservedMintedSupply++;

        NFT.mint(msg.sender, tokenIdTrackerReserved);

        emit ReservedMint(msg.sender, tokenIdTrackerReserved);

        tokenIdTrackerReserved++;
        
    }

    function whitelistMint(
        uint256 _timestamp,
        address _contractAddress,
        bytes32 _msgHash,
        bytes memory _signature
    ) public {
        require(
            _contractAddress != address(0),
            "Guardian Contract: Invalid Address"
        );
        require(
            isWhitelistSaleActive,
            "Guardian Contract: Whitelist Sale is not active"
        );
        require(
            userWalletCap[msg.sender].add(1) <= perWalletCap,
            "Guaridan Contract: Wallet cap limit reached"
        );
        require(
            !signatures[_signature],
            "Guardian Contract: Signatures already used!"
        );
        require(
            whitelistMintedSupply.add(1) <= totalWhitelistSupply,
            "Guardian Contract: Max supply minted!"
        );
        bytes32 msgHash = getMessageHash(msg.sender, _timestamp, _contractAddress);
        bytes32 signedMsgHash = msgHash.toEthSignedMessageHash();

        require(
            _msgHash == signedMsgHash,
            "Guardian Contract: Invalid message hash!"
        );
        require(
            _msgHash.recover(_signature) == whitelistMintSigner,
            "Guardian Contract: Invalid signer!"
        );

        signatures[_signature] = true;
        whitelistMintedSupply++;
        userWalletCap[msg.sender]++;

        NFT.mint(msg.sender, tokenIdTracker.current());

        emit WhitelistMint(msg.sender, tokenIdTracker.current());

        tokenIdTracker.increment();

    }

    function toggleIsWhitelistSaleActive() public {
        require(
            hasRole(ADMIN_ROLE, _msgSender()),
            "Guardian Contract: Must have admin role to toggle!"
        );
        isWhitelistSaleActive = !isWhitelistSaleActive;
    }

    function updateReservedMintSigner(address _signer) public {
        require(
            hasRole(ADMIN_ROLE, _msgSender()),
            "Guardian Contract: Must have admin role to update signer!"
        );
        require(_signer != address(0), "Guardian Contract: Invalid Address!");
        require(_signer != reservedMintSigner, "Guardian Contract: Signer already set!");

        reservedMintSigner = _signer;

        emit ReservedMintSignerUpdated(_signer);
    }

    function updateWhitelistMintSigner(address _signer) public {
        require(
            hasRole(ADMIN_ROLE, _msgSender()),
            "Guardian Contract: Must have admin role to update signer!"
        );
        require(_signer != address(0), "Guardian Contract: invalid Address!");
        require(_signer != whitelistMintSigner, "Guardian Contract: Signer already set!");

        whitelistMintSigner = _signer;

        emit WhitelistMintSignerUpdated(_signer);
    }

    function updatePerWalletCap(uint256 _perWalletCap) public {
        require(
            hasRole(ADMIN_ROLE, _msgSender()),
            "Guardian Contract: Must have admin role to update!"
        );
        require(
            perWalletCap != _perWalletCap,
            "Guardian Contract: Invalid per wallet cap!"
        );
        require(
            _perWalletCap != 0,
            "Guardian Contract: Wallet cap shopuld not be equal to 0!"
        );

        perWalletCap = _perWalletCap;

        emit UpdatePerWalletCap(_perWalletCap);
    }

    function updateNFTContractAddress(address _contractAddress) public {
        require(
            hasRole(ADMIN_ROLE, _msgSender()),
            "Guardian Contract: Must have admin role to update!"
        );
        require(
            _contractAddress != address(0),
            "Guardian Contract: Invalid Contract Address!"
        );
        require(
            IERC721(_contractAddress) != NFT,
            "Guardian Contract: Address already exist!" 
        );

        NFT = IERC721(_contractAddress);

        emit UpdateNFTContractAddress(_contractAddress);
    }

    function getMessageHash(
        address _to,
        uint256 _timestamp,
        address _contractAddress
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(_to, _timestamp, _contractAddress));
    }

    function getperWalletCap() public view returns (uint256) {
        return perWalletCap;
    }

    function getTotalSupply() public view returns (uint256) {
        return totalSupply;
    }

    function getMintedSupply() public view returns (uint256) {
        return whitelistMintedSupply.add(reservedMintedSupply);
    }

    function getTotalWhitelistSupply() public view returns (uint256) {
        return totalWhitelistSupply;
    }

    function getWhitelistMintedSupply() public view returns (uint256) {
        return whitelistMintedSupply;
    }

    function getReservedSupply() public view returns (uint256) {
        return totalReservedSupply;
    }

    function getReservedMintedSupply() public view returns (uint256) {
        return reservedMintedSupply;
    }

    function getWhitelistMintSignerAddress() public view returns (address) {
        return whitelistMintSigner;
    }

    function getReservedMintSignerAddress() public view returns (address) {
        return reservedMintSigner;
    }

    function getIsWhitelistSaleActive() public view returns (bool) {
        return isWhitelistSaleActive;
    }

    function getNFTContractAddress() public view returns (IERC721) {
        return NFT;
    }

    function getUserWalletCap(address _beneficiary) public view returns(uint256){
        return userWalletCap[_beneficiary];
    }

    function getSignatureStatus(bytes memory _signature) public view returns(bool){
        return signatures[_signature];
    }
