contract Rats is ERC20, ERC20Burnable, ERC165, Ownable {
    using EnumerableSet for EnumerableSet.AddressSet;

    bool public allowEmergencyAccess;
    address payable public donationAddress;
    EnumerableSet.AddressSet private allowedErc721Contracts;
    EnumerableSet.AddressSet private allowedErc1155Contracts;

    bytes4 immutable onErc721SuccessfulResult =
        bytes4(keccak256("onERC721Received(address,address,uint256,bytes)"));
    bytes4 immutable onErc1155SuccessfulResult =
        bytes4(
            keccak256(
                "onERC1155Received(address,address,uint256,uint256,bytes)"
            )
        );

    event DepositedToken(address from);
    event DepositedERC721(address from, address tokenAddress, uint256 tokenId);
    event DepositedERC1155(
        address from,
        address tokenAddress,
        uint256 tokenId,
        uint256 amount
    );
    event WithdrewToken(address from);
    event WithdrewERC721(address from, address tokenAddress, uint256 tokenId);
    event WithdrewERC1155(
        address from,
        address tokenAddress,
        uint256 tokenId,
        uint256 amount
    );

    constructor() public ERC20("RATS", "RATS") ERC20Burnable() Ownable() {
        allowEmergencyAccess = true;
        donationAddress = (payable(owner()));

        // from ERC1155Receiver.sol
        _registerInterface(
            ERC1155Receiver(0).onERC1155Received.selector ^
                ERC1155Receiver(0).onERC1155BatchReceived.selector
        );
    }

    function revokeEmergencyAccess() public onlyOwner {
        allowEmergencyAccess = false;
    }

    function changeDonationAddress(address payable newAddress)
        public
        onlyOwner
    {
        donationAddress = newAddress;
    }

    modifier onlyEmergencyAccess() {
        require(msg.sender == owner(), "must be owner");
        require(allowEmergencyAccess, "emergency access not allowed");
        _;
    }

    // ---- receive callbacks ----
    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4) {
        address receivingErc721ContractAddress = msg.sender;
        IERC721 receivingErc721Contract =
            IERC721(receivingErc721ContractAddress);

        // ensure address is whitelisted
        require(
            allowedErc721Contracts.contains(receivingErc721ContractAddress),
            "contract not whitelisted"
        );
        // ensure we have the nft
        require(
            receivingErc721Contract.ownerOf(tokenId) == address(this),
            "dont own erc721"
        );
        // emit event
        emit DepositedERC721(from, receivingErc721ContractAddress, tokenId);
        // take action
        _giveTokenOrWithdrawArt(from, data);
        return onErc721SuccessfulResult;
    }

    function onERC1155Received(
        address operator,
        address from,
        uint256 tokenId,
        uint256 amountTokens,
        bytes calldata data
    ) external returns (bytes4) {
        address receivingErc1155ContractAddress = msg.sender;
        IERC1155 receivingErc1155Contract =
            IERC1155(receivingErc1155ContractAddress);
        // ensure address is whitelisted
        require(
            allowedErc1155Contracts.contains(receivingErc1155ContractAddress),
            "contract not whitelisted"
        );
        // ensure we have the nft and didn't have prior
        require(amountTokens > 0, "must have non-zero amountTokens");
        uint256 ourCurrentBalance =
            receivingErc1155Contract.balanceOf(address(this), tokenId);
        require(ourCurrentBalance == amountTokens, "Already had ERC1155");

        // emit event
        emit DepositedERC1155(
            from,
            receivingErc1155ContractAddress,
            tokenId,
            amountTokens
        );
        // take action
        _giveTokenOrWithdrawArt(from, data);
        return onErc1155SuccessfulResult;
    }

    function swapTokensForERC721Art(
        address contractAddressToSend,
        uint256 tokenIdToSend
    ) external payable {
        // burn erc20 from user
        emit DepositedToken(msg.sender);
        _burn(msg.sender, 1 ether);

        // send art
        _sendERC721Art(contractAddressToSend, tokenIdToSend, msg.sender);
    }

    function swapTokensForERC1155Art(
        address contractAddressToSend,
        uint256 tokenIdToSend
    ) external payable {
        // burn erc20 from user
        emit DepositedToken(msg.sender);
        _burn(msg.sender, 1 ether);

        // send art
        _sendERC1155Art(contractAddressToSend, tokenIdToSend, msg.sender);
    }

    function withdrawDonations() public {
        Address.sendValue(donationAddress, address(this).balance);
    }

    function maxSupply() public pure virtual returns (uint256) {
        return 1000 ether;
    }

    function maxTokensCreated() public view returns (bool) {
        return totalSupply() >= maxSupply();
    }

    function _sendERC721Art(
        address contractAddressToSend,
        uint256 tokenIdToSend,
        address recipient
    ) private {
        IERC721 sendingErc721Contract = IERC721(contractAddressToSend);
        sendingErc721Contract.safeTransferFrom(
            address(this),
            recipient,
            tokenIdToSend
        );

        emit WithdrewERC721(recipient, contractAddressToSend, tokenIdToSend);
    }

    function _sendERC1155Art(
        address contractAddressToSend,
        uint256 tokenIdToSend,
        address recipient
    ) private {
        IERC1155 sendingErc1155Contract = IERC1155(contractAddressToSend);
        uint256 balance =
            sendingErc1155Contract.balanceOf(address(this), tokenIdToSend);
        require(balance > 0, "doesnt hold any balance");

        sendingErc1155Contract.safeTransferFrom(
            address(this),
            recipient,
            tokenIdToSend,
            balance,
            ""
        );

        emit WithdrewERC1155(
            recipient,
            contractAddressToSend,
            tokenIdToSend,
            balance
        );
    }

    function _giveTokenOrWithdrawArt(address from, bytes calldata data)
        private
    {
        if (data.length == 0) {
            // send tokens
            if (maxTokensCreated()) {
                revert("Max tokens created");
            }
            _mint(from, 1 ether);
            emit WithdrewToken(from);
        } else {
            // do swap
            (
                address contractAddressToSend,
                uint256 tokenIdToSend,
                bool isErc1155
            ) = abi.decode(data, (address, uint256, bool));
            if (isErc1155) {
                _sendERC1155Art(contractAddressToSend, tokenIdToSend, from);
            } else {
                _sendERC721Art(contractAddressToSend, tokenIdToSend, from);
            }
        }
    }

    // ------ allowedContracts fns -------
    function getAllowedContractsLength(bool isErc721)
        public
        view
        returns (uint256)
    {
        EnumerableSet.AddressSet storage allowedAddresses =
            isErc721 ? allowedErc721Contracts : allowedErc1155Contracts;
        return allowedAddresses.length();
    }

    function getAllowedContracts(
        bool isErc721,
        uint256 start,
        uint256 end
    ) public view returns (address[] memory) {
        EnumerableSet.AddressSet storage allowedAddresses =
            isErc721 ? allowedErc721Contracts : allowedErc1155Contracts;
        address[] memory re = new address[](end - start);
        for (uint256 i = start; i < end; i++) {
            re[i - start] = allowedAddresses.at(i);
        }
        return re;
    }

    function addAllowedContracts(
        bool isErc721,
        address[] memory contractAddresses
    ) public onlyOwner {
        EnumerableSet.AddressSet storage allowedAddresses =
            isErc721 ? allowedErc721Contracts : allowedErc1155Contracts;
        for (uint256 i = 0; i < contractAddresses.length; ++i) {
            allowedAddresses.add(contractAddresses[i]);
        }
    }

    // ----- emergency functions ------
    function emergencyRemoveAllowedContracts(
        bool isErc721,
        address[] memory contractAddresses
    ) public onlyEmergencyAccess {
        EnumerableSet.AddressSet storage allowedAddresses =
            isErc721 ? allowedErc721Contracts : allowedErc1155Contracts;
        for (uint256 i = 0; i < contractAddresses.length; ++i) {
            allowedAddresses.remove(contractAddresses[i]);
        }
    }

    function emergencyMint(address account, uint256 amount)
        public
        onlyEmergencyAccess
    {
        _mint(account, amount);
    }

    function emergencyBurn(address account, uint256 amount)
        public
        onlyEmergencyAccess
    {
        _burn(account, amount);
    }

    function emergencyExecute(
        address targetAddress,
        bytes calldata targetCallData
    ) public onlyEmergencyAccess returns (bool) {
        (bool success, ) = targetAddress.call(targetCallData);
        return success;
    }
}
