    "contracts/managers/calder/TCQSeasonOneManager.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.2;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import "../../access/SignerRoleUpgradeable.sol";
import "../project-token-uri-manager/ProjectTokenURIManager.sol";
import "../helper/WithTreasuryUpgradeable.sol";

interface ISimpleERC721AProject {
    function managerMint(address to, uint256 quantity) external;

    function ownerOf(uint256 tokenId) external returns (address);

    /**
     * @dev set approve transfer switch
     */
    function managerSetApproveTransfer(bool enabled) external;

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) external;
}

/**
 * @dev ERC721 token receiver interface.
 */
interface ERC721A__IERC721ReceiverUpgradeable {
    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4);
}

contract TCQSeasonOneManager is
    ERC721A__IERC721ReceiverUpgradeable,
    Initializable,
    ProjectTokenURIManager,
    OwnableUpgradeable,
    WithTreasuryUpgradeable,
    SignerRoleUpgradeable,
    PausableUpgradeable,
    UUPSUpgradeable
{
    using StringsUpgradeable for uint256;

    address public tcqSeasonOne;

    uint8 public constant TOTAL_TCQ_TYPES = 5;
    uint8 public constant EDITIONS_EACH_TCQ = 32;
    uint8 public constant MAX_SUPPLY = TOTAL_TCQ_TYPES * EDITIONS_EACH_TCQ;
    // initially each position stores the available tokenId [1, 2, 3, ... 32, | 33, 34, 35, ... 36], index is edition for each TCQ type, value is tokenId
    uint256[MAX_SUPPLY] private tcqEditionToTokenId;
    bool private initMinted;
    bool private reserveTransferred;
    uint256 public basePrice;
    string public baseURI;
    // a byte separated recording number, each byte represents available editions index for each TCQ type in tcqEditionToTokenId
    uint256 private editionRecord;
    //    mapping(uint256 => TCQRecord) public tokenIdToRecord;
    mapping(address => uint256) public userEERecord;

    event LogInitMint(uint8 total);
    event LogBought(address indexed user, bool isEE, uint8 tcqType, uint256 tokenId);
    event LogMoonPayBought(address indexed user, uint8 tcqType, uint256 tokenId);
    event LogTransferReserve(address indexed user, uint8 tcqType, uint8[] editions, uint256[] tokenIds);
    event LogSetBasePrice(uint256 basePrice);
    event LogSetTCQSeasonOne(address tcqSeasonOne);
    event LogSetBaseURI(string baseURI);
    event LogBatchSale(address indexed user, uint8 tcqType, uint256 tokenId);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {}

    /// @dev Require that the caller must be an EOA account
    modifier onlyEOA() {
        require(msg.sender == tx.origin, "not eoa");
        _;
    }

    function initialize(
        address payable _treasuryAddress,
        address _tcqSeasonOne,
        uint256 _basePrice,
        string calldata _baseURI
    ) public initializer {
        __Ownable_init();
        __SignerRole_init();
        __Pausable_init();
        __UUPSUpgradeable_init();

        setTreasury(_treasuryAddress);
        setTCQSeasonOne(_tcqSeasonOne);
        setBasePrice(_basePrice);
        setBaseURI(_baseURI);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(ProjectTokenURIManager) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function onERC721Received(
        address, /*operator*/
        address, /*from*/
        uint256, /*tokenId*/
        bytes calldata /*data*/
    ) external view override returns (bytes4) {
        require(_msgSender() == tcqSeasonOne, "can only receive tcqSeasonOne NFT");
        return ERC721A__IERC721ReceiverUpgradeable.onERC721Received.selector;
    }

    // mint all TCQ NFTs to this contract, tokenId 1 to TOTAL_TCQ_TYPES * EDITIONS_EACH_TCQ
    function initMint() public onlyOwner {
        require(!initMinted, "can only mint once");
        initMinted = true;
        ISimpleERC721AProject(tcqSeasonOne).managerMint(address(this), uint256(MAX_SUPPLY));
        unchecked {
            for (uint8 i = 0; i < MAX_SUPPLY; i++) {
                tcqEditionToTokenId[i] = i + 1;
            }
        }
        emit LogInitMint(MAX_SUPPLY);
    }

    function transferReserveTCQ(
        address user,
        uint8 tcqType,
        uint8[] calldata editions
    ) external onlyOwner {
        require(tcqType >= 1 && tcqType <= TOTAL_TCQ_TYPES, "bad tcqType");
        uint256[] memory tokenIds = new uint256[](editions.length);
        unchecked {
            for (uint8 i = 0; i < editions.length; i++) {
                uint8 tcqEdition = editions[i];
                require(tcqEdition >= 1 && tcqEdition <= EDITIONS_EACH_TCQ, "bad tcqEdition");
                uint8 index = uint8((tcqType - 1) * EDITIONS_EACH_TCQ) + tcqEdition - 1;
                uint256 tokenId = tcqEditionToTokenId[index];
                require(tokenId != 0, "this edition is sold out");
                tcqEditionToTokenId[index] = 0;
                ISimpleERC721AProject(tcqSeasonOne).transferFrom(address(this), user, tokenId);
                tokenIds[i] = tokenId;
            }
        }
        reserveTransferred = true;
        emit LogTransferReserve(user, tcqType, editions, tokenIds);
    }

    ///
    /// @notice Get the uri for a given project/tokenId
    ///
    function tokenURI(address project, uint256 tokenId) public view override returns (string memory) {
        require(project == tcqSeasonOne, "TCQSeasonOneManager: bad project");
        (uint8 tcqType, uint8 tcqEdition) = _tokenIdToTcqTypeAndEdition(tokenId);
        return string(abi.encodePacked(baseURI, uint256(tcqType).toString(), "/", uint256(tcqEdition).toString()));
    }

    function tokensLeft(uint8 tcqType) public view returns (uint8) {
        require(tcqType >= 1 && tcqType <= TOTAL_TCQ_TYPES, "bad tcqType");
        uint8 offset = (tcqType - 1) * 8;
        uint8 availableEditionIndex = uint8(editionRecord >> offset);
        uint8 indexOffset = uint8((tcqType - 1) * EDITIONS_EACH_TCQ);
        uint8 count = 0;
        while (availableEditionIndex < EDITIONS_EACH_TCQ) {
            // if the edition is available, tokenId is > 0
            if (tcqEditionToTokenId[indexOffset + availableEditionIndex] > 0) {
                count++;
            }
            availableEditionIndex++;
        }
        return count;
    }

    function userPresaleRecord(address user, uint8 tcqType) public view returns (uint8 record) {
        require(tcqType >= 1 && tcqType <= TOTAL_TCQ_TYPES, "bad tcqType");
        uint8 offset = (tcqType - 1) * 8;
        uint256 eeMintRecord = userEERecord[user];
        record = uint8(eeMintRecord >> offset);
    }

    function buy(
        bool isEE,
        uint8 tcqType,
        bytes calldata sig
    ) external payable onlyEOA whenNotPaused {
        uint256 price = isEE ? basePrice : basePrice * 2;
        require(msg.value >= price, "not enough ETH");
        bytes32 messageHash = keccak256(abi.encode(block.chainid, address(this), _msgSender(), isEE, tcqType));
        require(_verifySignedMessage(messageHash, sig), "proper signature is required");

        uint256 tokenId = _transferTCQ(_msgSender(), tcqType);
        _sendETHToTreasury(msg.value);
        emit LogBought(_msgSender(), isEE, tcqType, tokenId);
    }

    function buyWithMoonPay(
        address user,
        uint8 tcqType
    ) external onlySigner whenNotPaused {
        uint256 tokenId = _transferTCQ(user, tcqType);
        emit LogMoonPayBought(user, tcqType, tokenId);
    }

    function batchSale(address user, uint8[] calldata tcqTypes) external onlySigner whenNotPaused {
        uint256 len = tcqTypes.length;
        for (uint256 i = 0; i < len;) {
            uint8 tcqType = tcqTypes[i];
            uint256 tokenId = _transferTCQ(user, tcqType);
            emit LogBatchSale(user, tcqType, tokenId);
            unchecked {
                i++;
            }
        }
    }

    function _transferTCQ(
        address user,
        uint8 tcqType
    ) internal returns (uint256 tokenId) {
        require(reserveTransferred, "sell not started yet");
        require(tcqType >= 1 && tcqType <= TOTAL_TCQ_TYPES, "bad tcqType");
        uint8 offset = (tcqType - 1) * 8;

        uint8 availableEditionIndex = uint8(editionRecord >> offset);
        uint8 indexOffset = uint8((tcqType - 1) * EDITIONS_EACH_TCQ);
        uint8 count = 0;
        while (tokenId == 0 && availableEditionIndex < EDITIONS_EACH_TCQ) {
            tokenId = tcqEditionToTokenId[indexOffset + availableEditionIndex];
            availableEditionIndex++;
            count++;
        }
        require(tokenId > 0, "artwork is sold out");
        editionRecord += (count << offset);
        // set tokenId to 0 to indicate it's sold
        tcqEditionToTokenId[indexOffset + availableEditionIndex - 1] = 0;

        ISimpleERC721AProject(tcqSeasonOne).transferFrom(address(this), user, tokenId);
    }

    function setTCQSeasonOne(address _tcqSeasonOne) public onlyOwner {
        tcqSeasonOne = _tcqSeasonOne;
        emit LogSetTCQSeasonOne(_tcqSeasonOne);
    }

    function setBasePrice(uint256 _basePrice) public onlyOwner {
        basePrice = _basePrice;
        emit LogSetBasePrice(_basePrice);
    }

    function setBaseURI(string calldata _baseURI) public onlyOwner {
        baseURI = _baseURI;
        emit LogSetBaseURI(_baseURI);
    }

    function _tokenIdToTcqTypeAndEdition(uint256 tokenId) internal pure returns (uint8 tcqType, uint8 tcqEdition) {
        require(tokenId <= MAX_SUPPLY, "bad tokenId");
        tcqType = uint8((tokenId - 1) / EDITIONS_EACH_TCQ + 1);
        tcqEdition = uint8(((tokenId - 1) % EDITIONS_EACH_TCQ) + 1);
    }

    /**
     * @dev Set whether or not the project will check the manager for approval of token transfer
     */
    function setApproveTransfer(address project, bool enabled) external onlyOwner {
        require(project == tcqSeasonOne, "bad project");
        ISimpleERC721AProject(tcqSeasonOne).managerSetApproveTransfer(enabled);
    }

    /**
     * @dev Called by project contract to approve a transfer
     */
    function approveTransfer(
        address, /*from*/
        address, /*to*/
        uint256 /*tokenId*/
    ) external view returns (bool) {
        // block transfer if paused;
        return !paused();
    }

    /// @dev pause the contract
    function pause() external onlyOwner {
        _pause();
    }

    /// @dev unpause the contract
    function unpause() external onlyOwner {
        _unpause();
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}
"
