pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "./interfaces/IBoringBrewBags.sol";
import "./PriceConverter.sol";
import "./Tag.sol";
import "./BBTag.sol";

/// @title Sale
/// @author Atlas C.O.R.P.
contract Sale is AccessControlEnumerable {
    struct TokenData {
        uint256 supply;
        uint256 price;
    }
    using PriceConverter for uint256;
    using EnumerableSet for EnumerableSet.AddressSet;

    IBoringBrewBags public immutable boringBrewBagsContract;

    bool public active;

    bytes32 public constant SALES_MANAGER = keccak256("SALES_MANAGER");

    mapping(uint256 => TokenData) public tokenData;

    event Withdraw(uint256 _amount);

    constructor(IBoringBrewBags _boringBrewBagsContract) {
        boringBrewBagsContract = _boringBrewBagsContract;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(SALES_MANAGER, msg.sender);
        setTokenData(3000, 1900, 30 ether);
        setTokenData(3001, 1900, 30 ether);
        setTokenData(3002, 1900, 30 ether);
        setTokenData(3003, 1900, 30 ether);
        setTokenData(3004, 1900, 30 ether);
        setTokenData(3005, 1900, 30 ether);
        setTokenData(3006, 1900, 30 ether);
        setTokenData(3007, 1900, 30 ether);
        setTokenData(3008, 1900, 30 ether);
        setTokenData(3009, 1900, 30 ether);
        setTokenData(3010, 1900, 30 ether);
        setTokenData(3011, 300, 30 ether);
        setTokenData(4000, 1900, 30 ether);
        setTokenData(4001, 1900, 30 ether);
        setTokenData(4002, 1900, 30 ether);
        setTokenData(4003, 1900, 30 ether);
        setTokenData(5000, 1900, 30 ether);
        setTokenData(7000, 1900, 30 ether);
        setTokenData(7001, 1900, 30 ether);
        setTokenData(8000, 1900, 30 ether);
        setTokenData(9000, 1900, 30 ether);
        setTokenData(9001, 1900, 30 ether);
        setTokenData(10000, 1900, 30 ether);
        setTokenData(13000, 300, 30 ether);
        setTokenData(13001, 300, 30 ether);
    }

    /// @param _ids collection ids
    /// @param _amounts amounts of each collection id
    function buyBrew(
        uint256[] calldata _ids,
        uint256[] calldata _amounts
    ) external payable {
        require(active, "buyBrew: minting contract is not live");

        require(
            _ids.length == _amounts.length,
            "buyBrew: length of ids & amounts array mismatch"
        );

        uint256 i = 0;
        uint256 totalCost = 0;
        uint256 idLength = _ids.length;
        for (; i < idLength; ) {
            require(
                tokenData[_ids[i]].supply >= _amounts[i],
                "buyBrew: not enough supply left"
            );
            tokenData[_ids[i]].supply -= _amounts[i];
            totalCost += tokenData[_ids[i]].price * _amounts[i];

            unchecked {
                ++i;
            }
        }

        require(
            msg.value.getConversionRate() >= totalCost,
            "buyBrew: SEND MORE ETHEREUM"
        );

        boringBrewBagsContract.mintMultiple(msg.sender, _ids, _amounts);
    }

    /// @param _collectionId is the ID of the bags of brew coffee
    /// @param _supply is the max supply for each bag of coffee
    /// @param _price is how much the bag of coffee costs
    function setTokenData(
        uint256 _collectionId,
        uint256 _supply,
        uint256 _price
    ) public onlyRole(SALES_MANAGER) {
        tokenData[_collectionId].supply = _supply;
        tokenData[_collectionId].price = _price;
    }

    /// @param _active true is active false is inactive
    function toggleActive(bool _active) external onlyRole(SALES_MANAGER) {
        active = _active;
    }

    /// @notice withdraws tokens here
    /// @param _destination is the address the SALES_MANAGER sends contract balance
    function withdraw(address _destination) external onlyRole(SALES_MANAGER) {
        uint256 balance = address(this).balance;

        require(payable(_destination).send(balance));
        emit Withdraw(balance);
    }

    function priceInEth(
        uint256 priceInDollars
    ) external view returns (uint256) {
        return priceInDollars.getEthPrice();
    }
}
"
