    "contracts/lib/Invoke.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;
import {IVault} from  "../interfaces/internal/IVault.sol";

library Invoke {
    // using SafeMath for uint256;
    function invokeApprove(IVault _vault,address _token,address _spender,uint256 _amount) internal {
         bytes memory _calldata = abi.encodeWithSignature("approve(address,uint256)", _spender, _amount);
         _vault.execute(_token, 0, _calldata);
    }

    function invokeTransfer(IVault _vault,address _token,address _to,uint256 _amount) internal{
        if(_amount>0){
          bytes memory _calldata = abi.encodeWithSignature("transfer(address,uint256)", _to, _amount);
          _vault.execute(_token, 0, _calldata);
        }
    }
    function invokeTransferFrom(IVault _vault,address _token, address from, address _to,uint256 _amount) internal{
        if(_amount>0){
          bytes memory _calldata = abi.encodeWithSignature("transferFrom(address,address,uint256)", from, _to, _amount);
          _vault.execute(_token, 0, _calldata);
        }
    }
    
    function invokeUnwrapWETH(IVault _vault,address _weth,uint256 _amount) internal {
        bytes memory  _calldata = abi.encodeWithSignature("withdraw(uint256)", _amount);
        _vault.execute(_weth, 0, _calldata);
    }

    function invokeWrapWETH(IVault _vault,address _weth,uint256 _amount) internal{
        bytes memory  _calldata = abi.encodeWithSignature("deposit()");
        _vault.execute(_weth, _amount, _calldata);
    }

    function invokeTransferEth(IVault _vault,address _to, uint256 _amount) internal {
        // invokeWrapWETH(_vault,_weth,_amount);       
        // invokeTransfer(_vault,_weth,address(this),_amount);
        //  bytes memory  _calldata = abi.encodeWithSignature("withdraw(uint256)", _amount);
        //  (bool success, )=address(this).call(_calldata);
        //  require(success,"vault:withdraw eth fail");
        //  (success,)= _to.call{value:_amount}("");
        //  require(success,"vault:tranfer eth fail");
        bytes memory func;
        _vault.execute(_to,_amount, func);
    }
}"
    },
    "contracts/lib/ModuleBase.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import {IVault} from  "../interfaces/internal/IVault.sol";
import {IPlatformFacet} from "../interfaces/internal/IPlatformFacet.sol";
import {IVaultFacet} from "../interfaces/internal/IVaultFacet.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
contract ModuleBase{ 
    address public diamond;
    modifier onlyVault(address _vault) {
        require(msg.sender==address(_vault),"ModuleBase:caller must be vault");
        require(IPlatformFacet(diamond).getIsVault(_vault),"ModuleBase:vault must in platform");

        _;
    }

    modifier onlyVaultManager(address _vault){
        require(msg.sender==IVault(_vault).owner(),"ModuleBase:caller must be vault manager");
        require(IPlatformFacet(diamond).getIsVault(_vault),"ModuleBase:vault must in platform");
        _; 
    }
    function updatePosition(address _vault,address _component,uint256 _positionType,uint16 _debtType) internal {
            uint256 balance;
            if(_component==IPlatformFacet(diamond).getEth()) {
              balance= _vault.balance;    
            }else{
              balance=  IERC20(_component).balanceOf(_vault);
            }      
            uint16 option=balance>0 ? 1 : 0;
            uint16[3] memory sendAssetAppend=[uint16(_positionType),_debtType,option];
            IVaultFacet(diamond).setVaultPosition(_vault,_component,sendAssetAppend);
    }
}"
    },
    "contracts/modules/IssuanceModule.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "../lib/ModuleBase.sol";
import {IOwnable} from "../interfaces/internal/IOwnable.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {Invoke} from "../lib/Invoke.sol";
import {IIssuanceModule} from "../interfaces/internal/IIssuanceModule.sol";

contract IssuanceModule is ModuleBase, IIssuanceModule, Initializable, UUPSUpgradeable, ReentrancyGuardUpgradeable {
	using Invoke for IVault;
	using SafeERC20 for IERC20;
	modifier onlyOwner() {
		require(
			msg.sender == IOwnable(diamond).owner(),
			"TradeModule:only owner"
		);
		_;
	}
	
	/// @custom:oz-upgrades-unsafe-allow constructor
	constructor() {
		_disableInitializers();
	}
	
	function initialize(address _diamond) public initializer {
		__UUPSUpgradeable_init();
		diamond = _diamond;
	}
	
	function _authorizeUpgrade(
		address newImplementation
	) internal override onlyOwner {}

    function issueFromOwner(address _vault,address[] memory _assets,uint256[] memory _positionType, uint256[] memory _amounts) payable  external nonReentrant onlyVaultManager(_vault){
        IPlatformFacet platformFacet=IPlatformFacet(diamond);
        for (uint256 i; i < _assets.length; i++) {
			//check asset
			validAsset(_vault, _assets[i], _positionType[i]);
			//transferForm
			if (_assets[i] !=platformFacet.getEth()) {
				 IVault(_vault).invokeTransferFrom(_assets[i], msg.sender, _vault, _amounts[i]);
			}else{
				 (bool success,)=_vault.call{value:msg.value}("");	
		         require(success,"IssuanceModule:tranfer error");		 
			}
			//update Postion
			updatePosition(_vault, _assets[i], _positionType[i], 0);
		}
		emit Issue(_vault,msg.sender, _assets, _positionType, _amounts);
	}

	function issue(address _vault, address _from, address[] memory _assets, uint256[] memory _positionType, uint256[] memory _amounts) external nonReentrant onlyVault(_vault) {
		IPlatformFacet platformFacet=IPlatformFacet(diamond);
		if(platformFacet.getIsVault(_from)){
			IVaultFacet vaultFacet= IVaultFacet(diamond);
          	require(!vaultFacet.getVaultLock(_from),"IssuanceModule:vault is locked");  
		}
		for (uint256 i; i < _assets.length; i++) {
			//check asset
			validAsset(_vault, _assets[i], _positionType[i]);
			//transferForm
			if (_assets[i] !=platformFacet.getEth()) {
				IVault(_vault).invokeTransferFrom(_assets[i], _from, _vault, _amounts[i]);
			}
			//update Postion
			updatePosition(_vault, _assets[i], _positionType[i], 0);
		}
		emit Issue(_vault,_from, _assets, _positionType, _amounts);
	}

    function issue(address _vault, address[] memory _assets, uint256[] memory _positionType, uint256[] memory _amounts) external nonReentrant onlyVault(_vault) {
		address owner=IVault(_vault).owner();
        IPlatformFacet platformFacet=IPlatformFacet(diamond);
		if(platformFacet.getIsVault(owner)){
			IVaultFacet vaultFacet= IVaultFacet(diamond);
          	require(!vaultFacet.getVaultLock(owner),"IssuanceModule:vault is locked");  
		}
		for (uint256 i; i < _assets.length; i++) {
			//check asset
			validAsset(_vault, _assets[i], _positionType[i]);
			//transferForm
			if (_assets[i] != platformFacet.getEth()) {
				IERC20(_assets[i]).safeTransferFrom(owner,_vault, _amounts[i]);
			}
			//update Postion
			updatePosition(_vault, _assets[i], _positionType[i], 0);
		}
		emit Issue(_vault,owner, _assets, _positionType, _amounts);
	}
	
	function issueFromVault(address _vault, address _from, address[] memory _assets, uint256[] memory _positionType, uint256[] memory _amounts) external nonReentrant onlyVault(_vault) {
		require(IVault(_vault).owner() == IVault(_from).owner(), "IssuanceModule:only same owner vault");
		//check vault lock
		IVaultFacet vaultFacet= IVaultFacet(diamond);
		require(!vaultFacet.getVaultLock(_from),"IssuanceModule:vault is locked");  
		for (uint256 i; i < _assets.length; i++) {
			//check asset
			validAsset(_vault, _assets[i], _positionType[i]);
			//transferForm
			if (_assets[i] != IPlatformFacet(diamond).getEth()) {
				IVault(_from).invokeTransfer(_assets[i], _vault,  _amounts[i]);
			}
			//update Postion
			updatePosition(_vault, _assets[i], _positionType[i], 0);
			updatePosition(_from, _assets[i], _positionType[i], 0);
		}
		emit IssueFromVault(_vault,_from, _assets, _positionType, _amounts);
	}
	
	function redeem(address _vault, address payable _to, address[] memory _assets, uint256[] memory _positionType, uint256[] memory _amounts) external nonReentrant onlyVault(_vault) {
	    IPlatformFacet platformFacet=IPlatformFacet(diamond);
		for (uint256 i; i < _assets.length; i++) {
			uint256 amount = _amounts[i];
			//check asset
			validAsset(_vault, _assets[i], _positionType[i]);
			//transfer to metamask
			if (_assets[i] !=platformFacet.getEth()) {
				if (amount == 0) {
					amount = IERC20(_assets[i]).balanceOf(_vault);
				}
				IVault(_vault).invokeTransfer(_assets[i], _to, amount);
			} else {
				if (amount == 0) {
					amount = _vault.balance;
				}
				IVault(_vault).invokeTransferEth(_to, amount);
			}
			//update Postion
			updatePosition(_vault, _assets[i], _positionType[i], 0);
			if(platformFacet.getIsVault(_to)){
               	updatePosition(_to, _assets[i], _positionType[i], 0);    
			}
		}
		emit Redeem(_vault, _assets, _positionType, _amounts);
	}
	
	
	function validAsset(address _vault, address _asset, uint256 _debtType) internal view {
		IVaultFacet vaultFacet = IVaultFacet(diamond);
		IPlatformFacet platformFacet = IPlatformFacet(diamond);
		//check asset in platform
		uint256 assetType = platformFacet.getTokenType(_asset);
		require(assetType != 0 && assetType == _debtType, "IssuanceModule:asset must be platform allowed");
		//check asset in vault
		assetType = vaultFacet.getVaultTokenType(_vault, _asset);
		require(assetType != 0 && assetType == _debtType, "IssuanceModule:asset must be vault allowed");
	}	
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
