    "contracts/vesting/KreatorhoodLockWithFeeSharing.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./interfaces/IFeeSharing.sol";
import "./Vestable.sol";

import { IERC20, SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract KreatorhoodLockWithFeeSharing is IFeeSharing, Vestable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    event UserAmountsAllocated(address[] users, uint256[] amounts, uint8 tier);

    error AllocationCompleted();
    error AmountTooLarge(uint256 maxAvailable);
    error CliffNotReached();
    error IncompatibleArrays();
    error InvalidAmount();
    error InvalidBlocksPerDay();
    error InvalidTier();
    error NothingToHarvest();
    error OverAllocated();
    error UserAlreadyAllocated();
    error UserIsImmutable();
    error UserIsNotAllocated();
    error UserAlreadyWithdrawn();

    struct TierInfo {
        uint256 maxAmount;
        uint256 performanceAmount;
        uint256 totalAmount;
        uint8 extraCliffMonths;
        bool isVesting;
    }

    struct UserInfo {
        uint256 withdrawnAmount;
        uint256 amount;
        // Amount harvested (only by the harvest operation)
        uint256 harvestedAmount;
        //0 - team      -  6mo cliff - 18mo vest
        //1 - non-US    -  6mo cliff -  0mo vest
        //2 - US        - 12mo cliff -  0mo vest
        uint8 tier;
        bool isImmutable;
    }

    uint256 public constant WEI_MULTIPLIER = 10 ** 18;

    uint8 private constant MAX_TIERS = 4;

    uint256 public constant TIER_0_ALLOCATION = 102_700_000 * WEI_MULTIPLIER;

    uint256 public constant TIER_1_ALLOCATION = 109_848_300 * WEI_MULTIPLIER;

    uint256 public constant TIER_2_ALLOCATION = 10_500_000 * WEI_MULTIPLIER;

    uint256 public constant TIER_3_ALLOCATION = 13_000_000 * WEI_MULTIPLIER;

    uint256 public constant T1_PERFORMANCE_ALLOCATION = 15_000_000 * WEI_MULTIPLIER;

    uint256 public constant T3_PERFORMANCE_ALLOCATION = 7_800_000 * WEI_MULTIPLIER;

    IERC20 public immutable khoodToken;

    IERC20 public immutable rewardToken;

    address public immutable treasury;

    mapping(address => UserInfo) public userInfo;

    // Total amount of KHOOD tokens ever withdrawn
    uint256 public totalWithdrawn;

    // Total amount of reward tokens harvested (only by the harvest operation)
    uint256 public totalHarvested;

    TierInfo[] public tierInfo;

    constructor(
        address _khoodToken,
        address _rewardToken,
        address _vestingAdmin,
        address _treasury,
        uint256 _numberOfBlocksPerDay
    ) {
        checkZeroAddress(_khoodToken);
        checkZeroAddress(_rewardToken);
        checkZeroAddress(_vestingAdmin);
        checkZeroAddress(_treasury);
        if (_numberOfBlocksPerDay == 0) revert InvalidBlocksPerDay();
        khoodToken = IERC20(_khoodToken);
        rewardToken = IERC20(_rewardToken);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(VESTING_ADMIN_ROLE, _vestingAdmin);
        numberOfBlocksPerDay = _numberOfBlocksPerDay;
        treasury = _treasury;
        tierInfo.push(TierInfo(TIER_0_ALLOCATION, 0, 0, 1, true));
        tierInfo.push(TierInfo(TIER_1_ALLOCATION, T1_PERFORMANCE_ALLOCATION, 0, 3, false));
        tierInfo.push(TierInfo(TIER_2_ALLOCATION, 0, 0, 9, false));
        tierInfo.push(TierInfo(TIER_3_ALLOCATION, T3_PERFORMANCE_ALLOCATION, 0, 0, false));
    }

    /* @dev Must start vesting with the amount already in the balance
     */
    function startVesting() public override beforeVestingStarted onlyRole(VESTING_ADMIN_ROLE) {
        if (khoodToken.balanceOf(address(this)) == 0) revert InvalidTokenAmount();
        // equalize the totalAmount of the second and 4-th tiers to enable earning feeSharing for the full amount
        tierInfo[1].totalAmount = tierInfo[1].maxAmount;
        tierInfo[3].totalAmount = tierInfo[3].maxAmount;
        super.startVesting();
    }

    /* @notice  Returns the total amount vested for an account
     *  @param   _account an address for which we verify the total allocated amount
     */
    function getTokensAmount(address _account) public view override returns (uint256 tokenAmount) {
        tokenAmount = userInfo[_account].amount;
    }

    function getCliffDuration() public view override returns (uint256 cliffDurationInMonths) {
        cliffDurationInMonths = standardCliffDurationInMonths + tierInfo[userInfo[msg.sender].tier].extraCliffMonths;
    }

    function getAvailableAmountForFeeSharing() external view override returns (uint256 availableAmountForFeeSharing) {
        UserInfo memory user = userInfo[msg.sender];
        availableAmountForFeeSharing = user.amount - user.withdrawnAmount;
    }

    function getAvailableTokensForWithdraw(address user) external view returns (uint256 availableTokens) {
        UserInfo memory _userInfo = userInfo[user];
        if (tierInfo[_userInfo.tier].isVesting) {
            availableTokens = getMaxAvailableVested(user) - _userInfo.withdrawnAmount;
        } else {
            availableTokens = getTokensAmount(user) - _userInfo.withdrawnAmount;
        }
    }

    function withdraw(address to, uint256 amount) external override afterVestingStarted nonReentrant {
        checkZeroAddress(to);
        if (amount == 0) revert InvalidAmount();
        if (getMonth() < getCliffDuration()) revert CliffNotReached();
        UserInfo storage user = userInfo[msg.sender];
        uint256 _userAmount = user.amount;
        uint256 _withdrawnAmount = user.withdrawnAmount;
        if (_userAmount == 0) revert UserIsNotAllocated();
        if (_withdrawnAmount == _userAmount) revert UserAlreadyWithdrawn();
        uint256 availableTokens;
        if (tierInfo[user.tier].isVesting) {
            availableTokens = getMaxAvailableVested(msg.sender) - _withdrawnAmount;
        } else {
            availableTokens = getTokensAmount(msg.sender) - _withdrawnAmount;
        }
        if (amount > availableTokens) revert AmountTooLarge(availableTokens);
        internalHarvest(msg.sender, to, amount);
        user.withdrawnAmount = _withdrawnAmount + amount;
        totalWithdrawn = totalWithdrawn + amount;
        khoodToken.safeTransfer(to, amount);
        emit Withdraw(msg.sender, amount);
    }

    function harvest(address to) public override afterVestingStarted nonReentrant {
        checkZeroAddress(to);
        if (internalHarvest(msg.sender, to, 0) == 0) revert NothingToHarvest();
    }

    /*
     * @param user - account used to compute the reward amount
     */
    function getMaxHarvestAmount(address user) external view returns (uint256 maxHarvest) {
        uint256 currentRewardTokens = rewardToken.balanceOf(address(this));
        uint256 totalRewardTokens = currentRewardTokens + totalHarvested;
        UserInfo storage _userInfo = userInfo[user];
        uint256 userKhoodTokens = (_userInfo.amount - _userInfo.withdrawnAmount);
        maxHarvest = ((totalRewardTokens * userKhoodTokens) / getTotalKhoodForFeeSharing()) - _userInfo.harvestedAmount;
    }

    /*
     * @param from - account used to compute the reward amount
     * @param to - account which will receive the rewards
     * @param amount -  = 0 in case of harvesting (rewards are computed based of the entire user amount)
     *               - != 0 in case of withdrawing/stop vesting flows (rewards are computed based of this amount)
     */
    function internalHarvest(address from, address to, uint256 amount) private returns (uint256 maxHarvest) {
        uint256 currentRewardTokens = rewardToken.balanceOf(address(this));
        uint256 _totalHarvested = totalHarvested;
        uint256 totalRewardTokens = currentRewardTokens + _totalHarvested;
        UserInfo storage user = userInfo[from];
        uint256 userKhoodTokens = (user.amount - user.withdrawnAmount);
        uint256 _harvestedAmount = user.harvestedAmount;
        maxHarvest = ((totalRewardTokens * userKhoodTokens) / getTotalKhoodForFeeSharing()) - _harvestedAmount;
        if (amount != 0) maxHarvest = (maxHarvest * amount) / userKhoodTokens;
        else {
            user.harvestedAmount = _harvestedAmount + maxHarvest;
            totalHarvested = _totalHarvested + maxHarvest; // total WETH not withdrawn
        }
        if (maxHarvest != 0) {
            rewardToken.safeTransfer(to, maxHarvest);
            emit Harvest(from, maxHarvest);
        }
    }

    function getTotalKhoodForFeeSharing() public view returns (uint256 totalAmount) {
        uint256 _tierInfoLength = tierInfo.length;
        for (uint256 i = 0; i < _tierInfoLength; i++) {
            totalAmount += tierInfo[i].totalAmount;
        }
        totalAmount -= totalWithdrawn;
    }

    function setTierInfo(
        uint8 _tier,
        bool _isVesting,
        uint8 _extraCliffPeriods
    ) external beforeVestingStarted onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_tier >= MAX_TIERS) revert InvalidTier();
        TierInfo storage tier = tierInfo[_tier];
        tier.isVesting = _isVesting;
        tier.extraCliffMonths = _extraCliffPeriods;
    }

    /**
     * @notice Whitelist a list of user addresses for a given tier
     * @param _accounts array of user addresses
     * @param _approvedAmounts array of amounts approved in KHOOD tokens
     * @param _tier tier for the array of users
     */
    function allocateUserAmounts(
        address[] calldata _accounts,
        uint256[] calldata _approvedAmounts,
        bool[] calldata _immutables,
        uint8 _tier
    ) external beforeVestingStarted onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_accounts.length != _approvedAmounts.length) revert IncompatibleArrays();
        if (_accounts.length != _immutables.length) revert IncompatibleArrays();
        if (_tier >= MAX_TIERS) revert InvalidTier();

        for (uint256 i = 0; i < _accounts.length; i++) {
            address account = _accounts[i];
            uint256 amount = _approvedAmounts[i];
            UserInfo storage user = userInfo[account];
            if (user.amount != 0) {
                //overriding the amount if the user is already whitelisted
                tierInfo[user.tier].totalAmount -= user.amount;
            } else {
                // cannot set zero amount if not previously allocated
                if (amount == 0) revert InvalidTokenAmount();
            }
            tierInfo[_tier].totalAmount += amount;
            user.tier = _tier;
            user.amount = amount;
            user.isImmutable = _immutables[i];
        }
        TierInfo storage tier = tierInfo[_tier];
        if ((tier.totalAmount > tier.maxAmount - tier.performanceAmount)) revert OverAllocated();
        emit UserAmountsAllocated(_accounts, _approvedAmounts, _tier);
    }

    function allocatePerformance(
        address[] calldata _accounts,
        uint256[] calldata _approvedAmounts,
        uint8 _tier
    ) external afterVestingStarted onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_accounts.length != _approvedAmounts.length) revert IncompatibleArrays();
        TierInfo storage tier = tierInfo[_tier];
        uint256 _performanceAmount = tier.performanceAmount;
        if (_performanceAmount == 0) revert InvalidTier();
        uint256 totalPerformance;
        for (uint256 i = 0; i < _accounts.length; i++) {
            address account = _accounts[i];
            uint256 amount = _approvedAmounts[i];
            UserInfo storage user = userInfo[account];
            if (user.amount != 0) revert UserAlreadyAllocated();
            user.tier = _tier;
            user.amount = amount;
            totalPerformance += amount;
        }

        // Calculate unallocated amount - throws on underflow
        uint256 unallocated = _performanceAmount - totalPerformance;

        // harvest for unallocated amount to the treasury
        uint256 currentRewardTokens = rewardToken.balanceOf(address(this));
        uint256 totalRewardTokens = currentRewardTokens + totalHarvested;
        uint256 maxHarvest = ((totalRewardTokens * unallocated) / getTotalKhoodForFeeSharing());
        if (maxHarvest != 0) {
            rewardToken.safeTransfer(treasury, maxHarvest);
            emit Harvest(treasury, maxHarvest);
        }

        // transfer the unallocated amount to the treasury
        uint256 _totalAmount = tier.totalAmount;
        if (tier.maxAmount > _totalAmount) revert AllocationCompleted();
        tier.totalAmount = _totalAmount - unallocated;
        khoodToken.safeTransfer(treasury, unallocated);

        emit UserAmountsAllocated(_accounts, _approvedAmounts, _tier);
    }

    function stopVesting(address _account) external afterVestingStarted onlyRole(DEFAULT_ADMIN_ROLE) {
        UserInfo storage user = userInfo[_account];
        uint256 _amount = user.amount;
        uint256 _withdrawnAmount = user.withdrawnAmount;
        if (_amount == 0) revert UserIsNotAllocated();
        if (_withdrawnAmount == _amount) revert UserAlreadyWithdrawn();
        if (user.isImmutable) {
            revert UserIsImmutable();
        } else {
            uint256 unclaimedTokens = getMaxAvailableVested(_account) - _withdrawnAmount;
            uint256 lockedTokens = _amount - _withdrawnAmount - unclaimedTokens;
            if (unclaimedTokens != 0) {
                internalHarvest(_account, _account, unclaimedTokens);
                khoodToken.safeTransfer(_account, unclaimedTokens);
                emit Withdraw(_account, unclaimedTokens);
            }
            if (lockedTokens != 0) {
                khoodToken.safeTransfer(treasury, lockedTokens);
                emit Withdraw(treasury, lockedTokens);
            }
            user.withdrawnAmount = _amount;
        }
    }

    receive() external payable {}
}
"
    },
    "contracts/vesting/Vestable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { IVestable } from "./interfaces/IVestable.sol";

abstract contract Vestable is IVestable, AccessControl {
    error VestingStarted();

    error VestingNotStarted();

    error InvalidDurations();

    error InvalidTokenAmount();

    error ZeroAddressNotAllowed();

    error InvalidWithdrawAmount();

    uint16 public constant BPS_DIVISOR = 10000;

    // number of days in a month.
    // @dev A month is defined by a period with this size of days
    uint16 public constant MONTH_DAY_SIZE = 30;

    uint8 public standardCliffDurationInMonths = 3;

    uint8 public totalVestingDurationInMonths = 24;

    // average number of blocks mined in a day
    uint256 public numberOfBlocksPerDay;

    // starting block for vesting period
    // @dev This will be set at vesting start
    uint256 public vestingStartBlock;

    // role which has access to start the linear vesting period
    bytes32 public constant VESTING_ADMIN_ROLE = keccak256("VESTING_ADMIN_ROLE");

    modifier beforeVestingStarted() {
        _beforeVesting();
        _;
    }

    modifier afterVestingStarted() {
        _afterVesting();
        _;
    }

    function _afterVesting() private view {
        if (vestingStartBlock == 0) revert VestingNotStarted();
    }

    function _beforeVesting() private view {
        if (vestingStartBlock != 0) revert VestingStarted();
    }

    function getTokensAmount(address _account) public view virtual returns (uint256 tokenAmount);

    function getCliffDuration() public view virtual returns (uint256 cliffDurationInMonths) {
        cliffDurationInMonths = standardCliffDurationInMonths;
    }

    function startVesting() public virtual beforeVestingStarted {
        vestingStartBlock = block.number;

        emit StartVesting(block.number);
    }

    function setDurations(
        uint8 _cliffDurationInMonths,
        uint8 _vestingDurationInMonths
    ) public beforeVestingStarted onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_cliffDurationInMonths == 0 || _vestingDurationInMonths == 0) {
            revert InvalidDurations();
        }
        standardCliffDurationInMonths = _cliffDurationInMonths;
        totalVestingDurationInMonths = _vestingDurationInMonths;
    }

    function getCurrentDay() public view afterVestingStarted returns (uint256 currentDay) {
        currentDay = ((block.number - vestingStartBlock) / numberOfBlocksPerDay) + 1;
    }

    function getMonth() public view afterVestingStarted returns (uint256 month) {
        month = getCurrentDay() / MONTH_DAY_SIZE;
    }

    /* @notice  Will show how many tokens were vested so far
     * @param   _account is the address to be verified
     * - note that this param MAY be ignored when vesting is done at the contract level
     */
    function getMaxAvailableVested(address _account) public view returns (uint256 maxAmount) {
        uint256 month = getMonth();
        uint8 _totalVestingDurationInMonths = totalVestingDurationInMonths;
        if (month < getCliffDuration()) {
            maxAmount = 0;
        } else if (month < _totalVestingDurationInMonths) {
            maxAmount = (getTokensAmount(_account) * (month)) / _totalVestingDurationInMonths;
        } else {
            // in the last quarter the amount is whatever it's left is the contract
            maxAmount = getTokensAmount(_account);
        }
    }

    function checkZeroAddress(address toCheck) internal pure returns (bool success) {
        assembly {
            if iszero(toCheck) {
                let ptr := mload(0x40)
                // selector for `ZeroAddressNotAllowed()`
                mstore(ptr, 0x8579befe00000000000000000000000000000000000000000000000000000000)
                revert(ptr, 0x4)
            }
        }
        return true;
    }
}
"
