      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";


contract MultiSendV2 is Context {
    event Send(address token, address to, uint256 value);

    constructor() {}

    function multiSend(
        address token,
        address[] memory accounts,
        uint256[] memory values
    ) public returns (bool)  {
        require(accounts.length == values.length, "Length not same.");
        for (uint256 i = 0; i < accounts.length; i++) {
            require(IERC20(token).transferFrom(_msgSender(), accounts[i], values[i]), "Fail transferFrom");
            emit Send(token, accounts[i], values[i]);
        }
        return true;
    }
}
