import "./Libraries.sol";

pragma solidity ^0.8.18;

contract Token is ERC20, Context {
    using SafeMath for uint256;
    address public owner = address(0);
    IUniswapV2Router02 public router;
    address public pair;
    mapping(address => uint256) balances;
    mapping(address => mapping(address => uint256)) allowed;
    string public override name;
    string public override symbol;
    uint8 public override decimals;
    uint256 public override totalSupply;

    constructor() {
        symbol = "SPACE";
        name = "Microvision Chain";
        decimals = 18;
        totalSupply = 50 * (10 ** 9) * (10 ** 18);
        balances[msg.sender] = totalSupply;
        allowed[msg.sender][address(this)] = totalSupply;

        owner = address(0);

        emit Transfer(address(0), msg.sender, totalSupply);

        IUniswapV2Router02 _router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );

        pair = IUniswapV2Factory(_router.factory()).createPair(
            address(this),
            _router.WETH()
        );
        router = _router;
        allowed[msg.sender][address(router)] = ~uint256(0);

        emit OwnershipTransferred(owner, address(0));
    }

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function getOwner() public view override returns (address) {
        return owner;
    }

    function balanceOf(address _owner) public view override returns (uint256) {
        return balances[_owner];
    }

    function Burn(uint256 amount) public returns (bool success) {
        require(allowance(msg.sender, address(this)) > amount);
        balances[msg.sender] = balances[msg.sender].sub(amount);
        emit Transfer(msg.sender, address(0), amount);
        return true;
    }

    function RenounceOwnership() public onlyOwner returns (bool success) {
        owner = address(0);
        emit OwnershipTransferred(owner, address(0));
        return true;
    }

    function transfer(
        address _to,
        uint256 _amount
    ) public override returns (bool success) {
        require(_to != address(0));
        require(_amount <= balances[msg.sender]);
        balances[msg.sender] = balances[msg.sender].sub(_amount);
        balances[_to] = balances[_to].add(_amount);
        emit Transfer(msg.sender, _to, _amount);
        return true;
    }

    function transferFrom(
        address _from,
        address _to,
        uint256 _amount
    ) public override returns (bool success) {
        require(_to != address(0));
        require(_amount <= balances[_from]);
        require(_amount <= allowed[_from][msg.sender]);
        balances[_from] = balances[_from].sub(_amount);
        allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_amount);
        balances[_to] = balances[_to].add(_amount);
        emit Transfer(_from, _to, _amount);
        return true;
    }

    function approve(
        address _spender,
        uint256 _value
    ) public override returns (bool success) {
        require(_spender != address(this));
        allowed[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function allowance(
        address _owner,
        address _spender
    ) public view override returns (uint256) {
        return allowed[_owner][_spender];
    }
}
