pragma solidity ^0.8.0;

interface ILootfinityToken {
    struct SaleRange {
        uint256 start;
        uint256 end;
        uint256 tokenIdCounter;
        bool isSaleOpen;
    }

    enum SaleType {
        GenesisSale,
        AdvisorSale,
        NonGenesisSale
