    event TokenBought(address indexed buyer, uint256 tokenId);

    function buy() external payable;

    function wrapTrait(uint256 _collectibleId, string memory _trait) external;

    function unwrapTrait(uint256 _collectibleId, string memory _trait) external;

    function isTraitWrapped(
        uint256 _collectibleId,
        string memory _trait
    ) external view returns (bool isWrapped);

    function getTraits(
        uint256 _collectibleId
    ) external view returns (string[] memory);
}
