    "contracts/ZookeyBridge.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import {Commands} from "./libraries/Commands.sol";
import {ZookeyConstants} from "./libraries/constants/Common.sol";
import {BytesLib} from "./libraries/BytesLib.sol";
import {ZookeyBridgeErrors} from "./libraries/errors/ZookeyBridgeErrors.sol";
import {IZookeyBridge} from "./interface/IZookeyBridge.sol";
import "./base/LockAndMsgSender.sol";

contract ZookeyBridge is IZookeyBridge, OwnableUpgradeable, LockAndMsgSender {
    using BytesLib for bytes;
    using SafeERC20 for IERC20;
    /**
     * @dev sets initials supply and the owner
     */
    function initialize(address _owner) external initializer {
        __Ownable_init(_owner);
        _initLockedBy();
    }

    /**
     * @dev sets initials supply and the owner
     */

    function bridgeTo(
        address toToken,
        uint256 bridgeAmount,
        bytes1 command,
        bytes calldata input
    ) external payable isNotLocked {
        _dispatch(toToken, bridgeAmount, command, input);
    }

    function _dispatch(
        address _toToken,
        uint256 bridgeAmount,
        bytes1 _commandType,
        bytes calldata _input
    ) internal {
        uint256 command = uint8(_commandType & Commands.COMMAND_TYPE_MASK);
        require(
            Commands.SWAP_IF_BOUNDARY < command &&
                command <= Commands.BRIDGE_IF_BOUNDARY,
            ZookeyBridgeErrors.BRIDGE_COMMAND_ERR
        );

        if (command == Commands.BRIDGE_ORBITER) {
            _orbiterBridge(_toToken, bridgeAmount, _input);
        }
    }

    function _replaceLastUintInBytes(
        bytes memory _data,
        uint256 _newValue
    ) internal pure returns (bytes memory) {
        assembly {
            // 68 is because the start of the data is at 32, then 4 is taken by the function signature,
            // the first 32-bit parameter position is between 36-68, so the second parameter starts at the 68th bit.
            mstore(add(_data, 100), _newValue)
        }
        return _data;
    }

    function _orbiterBridge(
        address _toToken,
        uint256 _bridgeAmount,
        bytes calldata _input
    ) internal {
        address orbiterV3;
        bool success;
        assembly {
            orbiterV3 := calldataload(_input.offset)
        }
        bytes memory orbiterData = _input.toBytes(1);
        if (_toToken == ZookeyConstants.NATIVE_TOKEN) {
            (success, ) = orbiterV3.call{value: _bridgeAmount}(orbiterData);
        } else {
            _tokenRelayer(
                _toToken,
                msg.sender,
                address(this),
                orbiterV3,
                _bridgeAmount
            );
            assembly {
                mstore(add(orbiterData, 100), _bridgeAmount)
            }
            (success, ) = orbiterV3.call(orbiterData);
        }

        require(success, ZookeyBridgeErrors.BRIDGE_CALL_ERR);
        emit BridgeLog(_toToken, orbiterV3, _bridgeAmount, orbiterData);
    }

    function _tokenRelayer(
        address _token,
        address _transferFrom,
        address _receiver,
        address _approveTo,
        uint256 _amount
    ) internal {
        TransferHelper.safeTransferFrom(
            _token,
            _transferFrom,
            _receiver,
            _amount
        );
        TransferHelper.safeApprove(_token, _approveTo, _amount);
    }

    /// @notice To receive ETH from WETH and refund
    receive() external payable {}
    uint256[50] internal reserve;
}
