     */
    modifier isChief() {
        require(
            chief[msg.sender] ||
                msg.sender == IndexInterface(doughIndex).master(),
            "not-an-chief"
        );
        _;
    }

    /**
     * @dev Toggle a Chief. Enable if disable & vice versa
     * @param _chiefAddress Chief Address.
     */
    function toggleChief(address _chiefAddress) external {
        require(
            msg.sender == IndexInterface(doughIndex).master(),
            "toggleChief: not-master"
