pragma solidity ^0.8.0;   //Just specifies that the code needs a Solidity compiler version >= 0.8





contract KongsDistributionContract is Ownable, ReentrancyGuard {    // Ownable - access control machanism that allows the contract deployer to have exclusive access        
    IERC721Enumerable public kongsNFT;                              // ReentrancyGuard - protects from reentrant calls to a function (most common way for exploitation)
    IERC20 public usdcToken;                                        // lines 10-15 declaring variables to be used |  kongsNFT - ERC-721 token contract variable
    mapping(uint256 => bool) public isBlocked;                      // usdcToken - ERC-token contract variable  |  isBlocked - mapping variable to indicate blocked token             
    uint256 public blockedCount = 0;                                // blockedCount - total # of blocked tokens  
    uint256 public currentBatchStartIndex = 0;                      // currentBatchStartIndex - starting index for processing token batches
    uint256 public batchSize = 100;                                 // batchSize - set a reasonable batch size for distributing funds

    constructor(address _kongsNFTAddress, address _usdcAddress) Ownable(msg.sender) {   // Constructor that initializes kongsNFT and usdcToken variables with their addresses
        kongsNFT = IERC721Enumerable(_kongsNFTAddress);             // addresses should be passed when contract is deployed
        usdcToken = IERC20(_usdcAddress);
    }

    function blockTokenID(uint256 tokenId) external onlyOwner {     // Function for the contract owner (onlyOwner) to add token ID's to the blocked ID's array
        require(!isBlocked[tokenId], "Token already blocked");      // checks if tokenID is already blocked and adds to array if not
        isBlocked[tokenId] = true;
        blockedCount++;
    }
 
    function unblockTokenID(uint256 tokenId) external onlyOwner {   // Function for the contract owner (onlyOwner) to remove token ID's to the blocked ID's array
        require(isBlocked[tokenId], "Token already unblocked");     // checks if tokenID is already unblocked and removes from blocked array if not
        isBlocked[tokenId] = false;
        blockedCount--;
    }


    function getBlockedTokenIDs() external view returns (uint256[] memory) {   // Function to retrieve the array of blocked token IDs
        uint256[] memory blockedIDs = new uint256[](blockedCount);
        uint256 counter = 0;
        uint256 totalSupply = kongsNFT.totalSupply();

        for (uint256 i = 0; i < totalSupply; i++) {                  // Itterates through all tokenID's to count total # of blocked tokenID's for front end display
            if (isBlocked[i]) {
                blockedIDs[counter] = i;
                counter++;
            }
        }

        return blockedIDs;    // Outputs blockedIDs array for front end display
    }
    
    function fundContract(uint256 amount) external onlyOwner {      // Function to allow contract owner to fund the contract with USDC ERC-20 tokens
        require(usdcToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");   // msg.sender - address that sent the transaction
    }                                                                                            // address(this) - send tokens to this smart contract address | amount - token amount
                                                                                                 
    function effectiveTotalSupply() internal view returns (uint256) {  //  function to calculate total effective KONG TokenID's to distribute funds to
        return kongsNFT.totalSupply() - blockedCount;                  // (total supply - blocked count)
    }

    function setBatchSize(uint256 _newBatchSize) external onlyOwner {    // function that allows contract owner to set new batch size if needed
        require(_newBatchSize > 0, "Batch size must be greater than 0");
        batchSize = _newBatchSize;
    }

    function distributeFundsBatch() external onlyOwner nonReentrant {       // Function for contract owner to distribute funds in batches to NFT token owners.
        uint256 effectiveSupply = effectiveTotalSupply();                   // calls function above to calculate tokenID amount
        require(effectiveSupply > 0, "No effective tokens to distribute");  // makes sure tokenID amount > 0
        uint256 contractBalance = usdcToken.balanceOf(address(this));       // looks up USDC token balance in contract address
        require(contractBalance > 0, "No funds to distribute");             // makes sure USDC token balance > 0
        uint256 amountPerToken = contractBalance / effectiveSupply;         // divides contract balance by effective TokenID supply to calculate amount to distribute per tokenID

        uint256 lastTokenIndex = (currentBatchStartIndex + batchSize > kongsNFT.totalSupply()) ?  // assigns index for last token in current batch
                                  kongsNFT.totalSupply() :
                                  currentBatchStartIndex + batchSize;

        for (uint256 i = currentBatchStartIndex; i < lastTokenIndex; i++) {  // itterates through batch and distributes USDC amountPerToken to each TokenID
            if (!isBlocked[i]) {
                require(usdcToken.transfer(kongsNFT.ownerOf(i), amountPerToken), "Transfer failed");
            }
        }



        currentBatchStartIndex = lastTokenIndex;   // assigns last token index of batch to current batch start index to be used for next batch
    }
}
