    "src/Types.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.13;

struct CollectionConfig {
    bytes16 uuid;
    string name;
    string token;
    string slug;
    bytes16[] dependencies;
    string[] metadataKeys;
    string[] metadataValues;
}

enum SaleType {
    FixedPrice,
    DeprecatedReserved,
    ExponentialDutchAuction,
    FixedPriceTimeLimited
}

struct SaleConfig {
    SaleType saleType; // uint8 for packing
    uint32 maxSalePieces; // Maximum number of pieces to sell (including reserves)
    uint32 numReserved; // Number of pieces to reserve for specific wallets
    uint16 numRetained; // Number of pieces to retain for the artist
    uint16 numAlba; // Number of pieces to retain for Alba
    uint40 startTime; // Sale start time
    uint40 auctionEndTime; // Sale doesn't stop here, but price decay stops. Needed for rebate if non-sellout.
    bool hasRebate; // Whether or not to give a rebate to resting price
    uint256 initialPrice; // Starting price for Dutch Auction
    uint256 finalPrice; // Ending price for Dutch Auction
}

struct PaymentConfig {
    address[] primaryPayees; // Addresses for primary payment.
    uint256[] primaryShareBasisPoints; // Share of primary sales for each address (basis points).
    address[] secondaryPayees; // Addresses for secondary payment.
    uint256[] secondaryShareBasisPoints; // Share of secondary sales for each address (basis points).
    uint16 royaltyBasisPoints; // Total royalty basis points.
}

struct StoredScript {
    string fileName;
    uint256 wrappedLength;
}
"
    },
    "src/libraries/Pricing.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.20;

import {SaleType, SaleConfig} from "../Types.sol";
import {Math} from "./Math.sol";

library AlbaCollectionPricing {
    /**
     * @notice Returns the current price of the sale.
     * @dev Wrapper for different pricing strategies based on sale type.
     */
    function getPrice(SaleConfig storage saleConfig) public view returns (uint256) {
        if (saleConfig.saleType == SaleType.FixedPrice || saleConfig.saleType == SaleType.FixedPriceTimeLimited) {
            return saleConfig.initialPrice;
        }
        if (block.timestamp <= saleConfig.startTime) {
            return saleConfig.initialPrice;
        }
        if (saleConfig.saleType == SaleType.ExponentialDutchAuction) {
            return _getPriceExponentialDA(saleConfig);
        }
        return 0;
    }

    /**
     * @notice Returns the current price of the sale using a continuous dutch auction pricing strategy.
     * @dev This works by computing the time elapsed since the start of the auction, and then interpolating
     * the price based on the initial price and the final price. The interpolation is linear.
     */
    function _getPriceExponentialDA(SaleConfig storage saleConfig) internal view returns (uint256) {
        if (block.timestamp >= saleConfig.auctionEndTime) {
            return saleConfig.finalPrice;
        }

        uint256 timeElapsed = block.timestamp - saleConfig.startTime;
        uint40 totalDuration = saleConfig.auctionEndTime - saleConfig.startTime;

        // This choice guarantees that `elapsed/tau < 5` meaning that the approximant is applicable (with rel error < 1%)
        // and that the price difference has sufficiently decayed (to <0.007) at the end of the auction.
        uint40 tau = totalDuration / 5;

        (uint256 expNumerator, uint256 expDenominator) =
            Math.expPadeApprox5(int48(int256(timeElapsed)), -int48(uint48(tau)));

        uint256 priceDifference = saleConfig.initialPrice - saleConfig.finalPrice;
        return saleConfig.finalPrice + (priceDifference * expNumerator) / expDenominator;
    }
}
"
    },
    "src/libraries/Splitters.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.20;

import {PaymentConfig} from "../Types.sol";
import {IAlbaDelegate} from "../IAlbaDelegate.sol";
import {IPaymentSplitter} from "../IPaymentSplitter.sol";

library Splitters {
    error InvalidConfiguration();

    /**
     * Setup the sales splitters.
     * @dev We expect an array of length 1 for a single user (if there's no Alba fee). This is less efficient
     * than just using the artist's address rather than a splitter, but it provides consistent UX for claiming.
     */

    function setupPaymentSplitters(PaymentConfig calldata conf, IAlbaDelegate albaDelegate)
        public
        returns (IPaymentSplitter, IPaymentSplitter)
    {
        if (conf.primaryPayees.length == 0 || conf.primaryPayees.length != conf.primaryShareBasisPoints.length) {
            revert InvalidConfiguration();
        }
        IPaymentSplitter paymentSplitter;
        IPaymentSplitter paymentSplitterRoyalties;

        address primary = albaDelegate.paymentSplitterFactory().deploy(conf.primaryPayees, conf.primaryShareBasisPoints);
        paymentSplitter = IPaymentSplitter(primary);

        // Only deploy a contract for secondary payments if there are secondary payees.
        if (conf.secondaryPayees.length > 0) {
            if (conf.secondaryPayees.length != conf.secondaryShareBasisPoints.length) {
                revert InvalidConfiguration();
            }

            address secondary =
                albaDelegate.paymentSplitterFactory().deploy(conf.secondaryPayees, conf.secondaryShareBasisPoints);
            paymentSplitterRoyalties = IPaymentSplitter(secondary);
        }

        return (paymentSplitter, paymentSplitterRoyalties);
    }
}
"
    },
