        address signer,
        bytes32 hash,
        bytes memory signature
    ) internal view returns (bool) {
        (address recovered, ECDSA.RecoverError error) = ECDSA.tryRecover(
            hash,
            signature
        );
        if (error == ECDSA.RecoverError.NoError && recovered == signer) {
            return true;
        }

        (bool success, bytes memory result) = signer.staticcall(
            abi.encodeWithSelector(
                IBEP1271.isValidSignature.selector,
                hash,
                signature
            )
        );
        return (success &&
            result.length == 32 &&
            abi.decode(result, (bytes4)) == IBEP1271.isValidSignature.selector);
    }
}

contract plinko is Ownable, ReentrancyGuard, Pausable {
    using SignatureChecker for address;
    using SafeERC20 for IERC20;

    address public signerAddress;

    bytes32 public constant SIGNATURE_PERMIT_TYPEHASH =
        keccak256("address user,address token,uint256 amount,uint256 expiry");

    struct userdata {
        uint256[] depositamount;
        uint256[] deposittime;
        uint256[] withdrawamount;
        uint256[] withdrawtime;
        uint256 depositcount;
        uint256 withdrawcount;
    }

    mapping(address => bool) public tokenstatus;
    mapping(address => mapping(address => userdata)) public userdetails;
    mapping(bytes => bool) private signStatus;

    event AddTokenaddress(address tokenaddress, bool status, uint256 time);
    event Deposit(
        address user,
        address tokenaddress,
        uint256 amount,
        uint256 time
    );
    event Withdraw(
        address user,
        address tokenaddress,
        uint256 amount,
        uint256 time
    );
    event UpdateSigneraddress(address signer, uint256 time);

    constructor(address _signer) {
        require(
            _signer != address(0) && !(Address.isContract(_signer)),
            "invalid address"
        );
        signerAddress = _signer;
    }

    function addtokenlist(
        address _token,
        bool _status
    ) external onlyOwner returns (bool) {
        require(
            _token != address(0) && Address.isContract(_token),
            "invalid token address"
        );
        require(tokenstatus[_token] != _status, "invalid boolean status");
        tokenstatus[_token] = _status;
        emit AddTokenaddress(_token, _status, block.timestamp);
        return true;
    }

    function deposit(
        address _token,
        uint256 _amount
    ) external payable whenNotPaused nonReentrant {
        address _user = msg.sender;
        require(
            _user != address(0) && !(Address.isContract(_user)),
            "invalid useraddress"
        );
        userdata storage Depositdata = userdetails[_user][_token];
        if (_token == address(0)) {
            uint256 _depositamount = msg.value;
            require(_amount == 0 && _depositamount > 0, "invalid Ethamount");

            Depositdata.depositamount.push(_depositamount);

            emit Deposit(_user, _token, _depositamount, block.timestamp);
        } else {
            require(tokenstatus[_token] == true, "InValid Token");
            require(_amount > 0 && msg.value == 0, "invalid Tokenamount");
            Depositdata.depositamount.push(_amount);

            IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);
            emit Deposit(_user, _token, _amount, block.timestamp);
        }
        Depositdata.depositcount++;
        Depositdata.deposittime.push(block.timestamp);
    }

    function withdraw(
        address _token,
        uint256 _amount,
        uint256 _expiry,
        bytes memory _signature
    ) external whenNotPaused nonReentrant {
        address _user = msg.sender;
        require(
            _user != address(0) && !(Address.isContract(_user)),
            "invalid useraddress"
        );
        require(_expiry > block.timestamp, "invalidtime");
        require(!signStatus[_signature], "already sign used");
        userdata storage Withdrawdata = userdetails[_user][_token];
        bytes32 hash_ = keccak256(
            abi.encodePacked(
                SIGNATURE_PERMIT_TYPEHASH,
                _user,
                _token,
                _amount,
                _expiry,
                address(this)
            )
        );
        require(
            signCheck(toSignedMessageHash(hash_), _signature),
            "Sign Error"
        );

        if (_token == address(0)) {
            require(
                _amount > 0 && address(this).balance >= _amount,
                "Insufficient amount"
            );

            (bool sent, ) = _user.call{value: _amount}("");
            require(sent, "Failed to send Ether");
        } else {
            require(tokenstatus[_token] == true, "InValid Token");
            require(
                _amount > 0 && checkcontractbalance(_token) >= _amount,
                "Insufficient amount"
            );
            IERC20(_token).safeTransfer(_user, _amount);
        }

        signStatus[_signature] = true;
        Withdrawdata.withdrawamount.push(_amount);
        Withdrawdata.withdrawtime.push(block.timestamp);
        Withdrawdata.withdrawcount++;
        emit Withdraw(_user, _token, _amount, block.timestamp);
    }

    function signCheck(
        bytes32 hash,
        bytes memory signature
    ) public view returns (bool) {
        return signerAddress.isValidSignatureNow(hash, signature);
    }

    function toSignedMessageHash(bytes32 hash) public pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", hash)
            );
    }

    function pause() external onlyOwner nonReentrant {
        _pause();
    }

    function unpause() external onlyOwner nonReentrant {
        _unpause();
    }

    function checkcontractbalance(
        address _token
    ) public view returns (uint256) {
        return IERC20(_token).balanceOf(address(this));
    }

    function adminEmergency(
        address addr,
        address account,
        uint256 amount
    ) public onlyOwner {
        if (addr == address(0)) {
            payable(account).transfer(amount);
        } else {
            IERC20(addr).safeTransfer(account, amount);
        }
    }

   

    function addamount(
        address _token,
        uint256 _amount
    ) external payable onlyOwner whenNotPaused nonReentrant {

        if(_token != address(0)){
          require(_amount == 0 && msg.value > 0, "invalid Ethamount");
          emit Deposit(msg.sender, _token, msg.value, block.timestamp);
        }
        else{
            require(_amount > 0 && msg.value == 0, "invalid Ethamount");
            IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount); 
            emit Deposit(msg.sender, _token, _amount, block.timestamp);
        }
      
    }

    function userwithdrawinfo(
        address _token,
        address _user
    ) public view returns (uint256[] memory , uint256[] memory ) {   // Need to change
        userdata memory info = userdetails[_user][_token];
        uint256[] memory withdrawAmt = new uint256[](info.withdrawcount);
        uint256[] memory withdrawTime = new uint256[](info.withdrawcount);
        for (uint256 i = 0; i < info.withdrawcount; i++) {
            withdrawAmt[i] = info.withdrawamount[i];
            withdrawTime[i] = info.withdrawtime[i];
        }

        return (withdrawAmt,withdrawTime);
    }

    function userdepositinfo(
        address _token,
        address _user
    ) public view returns (uint256[] memory, uint256[] memory) {  // Need to change
        userdata memory info = userdetails[_user][_token];
        uint256[] memory depAmt = new uint256[](info.depositcount);
        uint256[] memory depTime = new uint256[](info.depositcount);
        for (uint256 i = 0; i < info.depositcount; i++) {
            depAmt[i] = info.depositamount[i];
            depTime[i] = info.deposittime[i];
        }

        return (depAmt,depTime);
    }

    function updateSignerAddress(
        address _signeraddress
    ) external onlyOwner returns (bool) {
        require(
            !(Address.isContract(_signeraddress)),
            "Address:Invalid address"
        );

        signerAddress = _signeraddress;
        emit UpdateSigneraddress(_signeraddress, block.timestamp);
        return true;
    }
}
