contract MekaRim is Ownable, ERC721A, ERC721AQueryable, PaymentSplitter, ReentrancyGuard {


    using Strings for uint;
        using Counters for Counters.Counter;


    enum Step {
        Before,
        Presale,
        PublicSale,
        PublicSalePhase2,
        PublicSalePhase3,
        SoldOut,
        Reveal
    }
  
    string public baseURI;

    string public notRevealedURI;

    Step public sellingStep;

    bytes32 public merkleRoot;

    uint private constant MAX_SUPPLY = 10000;

    uint public pricePresale = 0.15 ether;  

    uint public publicSalePrice = 0.3 ether;

    uint public publicSalePricePhase2 = 0.6 ether;

    uint public publicSalePricePhase3 = 1 ether;

    bool public revealed = false;

    bool public paused = false;

    string public baseExtension = ".json";

    Counters.Counter private _nftIdCounter;

    uint public saleStartTime = 1650326745;


    uint private teamLength;

    constructor(address[] memory _team, uint[] memory _teamShares, string memory _theBaseURI, string memory _theNotRevealedURI) 
    ERC721A("MekaRim", "MRJ")
    PaymentSplitter(_team, _teamShares) {
        _nftIdCounter.increment();
        baseURI = _theBaseURI;
        notRevealedURI = _theNotRevealedURI;
        teamLength = _team.length; 
    }

    modifier callerIsUser() {
        require(tx.origin == msg.sender, "The caller is another contract");
        _;
    }

    function getBalance() public view returns (uint) {
        return address(this).balance;
    }


    function setPaused(bool _paused) external onlyOwner {
        paused = _paused;
    }

    function changePricePresale(uint _pricePresale) external onlyOwner {
        pricePresale = _pricePresale;
    }

    function changePriceSale(uint _publicSalePrice) external onlyOwner {
        publicSalePrice = _publicSalePrice;
    }
      function changePriceSalePhase2(uint _publicSalePricePhase2) external onlyOwner {
        publicSalePricePhase2 = _publicSalePricePhase2;
    }
      function changePriceSalePhase3(uint _publicSalePricePhase3) external onlyOwner {
        publicSalePricePhase3 = _publicSalePricePhase3;
    }

    function setSaleStartTime(uint _saleStartTime) external onlyOwner {
        saleStartTime = _saleStartTime;
    }

    function setBaseUri(string memory _newBaseURI) external onlyOwner {
        baseURI = _newBaseURI;
    }
    function setNotRevealURI(string memory _notRevealedURI) external onlyOwner {
        notRevealedURI = _notRevealedURI; 
    }
     function setBaseExtension(string memory _baseExtension) external onlyOwner {
        baseExtension = _baseExtension;
    }
    function reveal() external onlyOwner{
        revealed = true;
    }
      function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }
    
    function currentTime() internal view returns(uint) {
        return block.timestamp;
    }

    function setStep(uint _step) external onlyOwner {
        sellingStep = Step(_step);
    }
        function presaleMint(address _account, uint _quantity) external payable callerIsUser {
        uint price = pricePresale;
        require(price != 0, "Price is 0");
        require(sellingStep == Step.Presale, "Presale has not started yet.");
        require(totalSupply() + _quantity <= MAX_SUPPLY, "Max supply exceeded");
        require(msg.value >= price * _quantity, "Not enought funds");
        _safeMint(_account, _quantity);
        }


       function publicSaleMint(address _account, uint _quantity) external payable callerIsUser {
        uint price = publicSalePrice;
        require(price != 0, "Price is 0");
        require(sellingStep == Step.PublicSale, "Public sale is not activated");
        require(totalSupply() + _quantity <= MAX_SUPPLY, "Max supply exceeded");
        require(msg.value >= price * _quantity, "Not enought funds");
        _safeMint(_account, _quantity);
    }

           function publicSaleMintPhase2(address _account, uint _quantity) external payable callerIsUser {
        uint price = publicSalePricePhase2;
        require(price != 0, "Price is 0");
        require(sellingStep == Step.PublicSalePhase2, "Public sale phase 2 is not activated");
        require(totalSupply() + _quantity <= MAX_SUPPLY, "Max supply exceeded");
        require(msg.value >= price * _quantity, "Not enought funds");
        _safeMint(_account, _quantity);
    }

              function publicSaleMintPhase3(address _account, uint _quantity) external payable callerIsUser {
        uint price = publicSalePricePhase3;
        require(price != 0, "Price is 0");
        require(sellingStep == Step.PublicSalePhase3, "Publicsale phase 3 is not activated");
        require(totalSupply() + _quantity <= MAX_SUPPLY, "Max supply exceeded");
        require(msg.value >= price * _quantity, "Not enought funds");
        _safeMint(_account, _quantity);
    }

    function gift(address _to, uint _quantity) external onlyOwner {
        require(totalSupply() + _quantity <= MAX_SUPPLY, "Reached max Supply");
        _safeMint(_to, _quantity);
    }



  function tokenURI(uint _nftId) public view override(ERC721A, IERC721A) returns (string memory) {
        require(_exists(_nftId), "This NFT doesn't exist.");
        if(revealed == false) {

        return 
             string(abi.encodePacked(notRevealedURI, _nftId.toString(), baseExtension));
            

        }
        
        string memory currentBaseURI = _baseURI();
        return 
            bytes(currentBaseURI).length > 0 
            ? string(abi.encodePacked(currentBaseURI, _nftId.toString(), baseExtension))
            : "";
    }


      function withdrawMoney() external onlyOwner nonReentrant {
    (bool success, ) = msg.sender.call{value: address(this).balance}("");
    require(success, "Transfer failed.");
  }

    receive() override external payable {
        revert('Only if you mint');
    }

}
