        string memory output = slot0[slotIds[0]];

        if (slotIds[1] < 255) {
            output = string(abi.encodePacked(output, slot1[slotIds[1]]));
        }

        if (slotIds[2] < 255) {
            output = string(abi.encodePacked(output, slot2[slotIds[2]]));
        }

        output = string(abi.encodePacked(output, STRING_MOONCAKE));

        if (slotIds[3] < 255) {
            output = string(abi.encodePacked(output, STRING_PRE_SLOT3, slot3[slotIds[3]]));
        }

        if (slotIds[4] < 255) {
            output = string(abi.encodePacked(output, STRING_PRE_SLOT4, slot4[slotIds[4]]));
        }

        if (slotIds[5] < 255) {
            output = string(abi.encodePacked(output, STRING_PRE_SLOT5, slot5[slotIds[5]]));
        }

        return output;
    }

    /**
     * @dev Return the mooncake text for a given tokenId
     */
    function getMooncakeText(uint256 tokenId) public view returns (string memory) {
        uint256 tokenLevel = tokenLevels[tokenId];
        return getMooncakeTextWithLevel(tokenId, tokenLevel);
    }

    /**
     * @dev Get token URI at a specified token level
     */
    function tokenURIWithLevel(uint256 tokenId, uint256 tokenLevel) public view returns (string memory) {
        string[17] memory parts;
        parts[
            0
        ] = '<svg xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMinYMin meet" viewBox="0 0 350 350"><style>foreignObject { fill: black; font-family: "DM Sans", "Open Sans", "Calibri", -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif; font-size: 24px; }</style><rect width="100%" height="100%" fill="';

        if (tokenLevel == 0) {
            parts[1] = "#fef2d5";
        } else if (tokenLevel == 1) {
            parts[1] = "#feecc1";
        } else if (tokenLevel == 2) {
            parts[1] = "#fde5ae";
        } else {
            parts[1] = "#fccb2d";
        }

        parts[2] = '" /><foreignObject width="300" height="320" x="20" y="20" requiredFeatures="http://www.w3.org/TR/SVG11/feature#Extensibility">';
        parts[3] = getMooncakeTextWithLevel(tokenId, tokenLevel);
        parts[4] = "</foreignObject></svg>";

        string memory output = string(abi.encodePacked(parts[0], parts[1], parts[2], parts[3], parts[4], parts[5], parts[6], parts[7], parts[8]));
        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{"name": "Mooncake #',
                        toString(tokenId),
                        '", "description": "The Mooncake Gift is a collection of randomized mooncakes generated and stored onchain. The more times a mooncake is gifted, the tastier it becomes. Images are intentionally omitted for others to interpret. Feel free to use these mooncakes in any way you want.", "image": "data:image/svg+xml;base64,',
                        Base64.encode(bytes(output)),
                        '"}'
                    )
                )
            )
        );
        output = string(abi.encodePacked("data:application/json;base64,", json));

        return output;
    }

    /**
     * @dev Retrieve the current tokenURI (embedded SVG)
     */
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        uint256 tokenLevel = tokenLevels[tokenId];
        return tokenURIWithLevel(tokenId, tokenLevel);
    }

    /**
     * @dev Mint tokens
     */
    function mintWithAffiliate(uint256 numberOfNfts, address affiliate) public payable nonReentrant {
        // Checks
        require(!contractSealed, "Contract sealed");

        require(block.timestamp >= SALES_START_TIMESTAMP, "Not started");

        require(numberOfNfts > 0, "Cannot mint 0 NFTs");
        require(numberOfNfts <= 50, "Cannot mint more than 50 in 1 tx");

        uint256 total = totalSupply();
        require(total + numberOfNfts <= maxNftSupply, "Sold out");

        require(msg.value == numberOfNfts * MINT_FEE, "Ether value sent is incorrect");

        // Effects
        for (uint256 i = 0; i < numberOfNfts; i++) {
            uint256 tokenId = total + i;

            _safeMint(_msgSender(), tokenId, "");
            if (affiliate != address(0)) {
                emit AffiliateMint(tokenId, affiliate);
            }
        }
    }

    /**
     * @dev Mint tokens
     */
    function mintNfts(uint256 numberOfNfts) external payable {
        mintWithAffiliate(numberOfNfts, address(0));
    }

    /**
     * @dev Claim a free one
     */
    function claimOneFreeNft() external payable nonReentrant {
        // Checks
        require(!contractSealed, "Contract sealed");

        require(block.timestamp >= CLAIM_START_TIMESTAMP, "Not started");

        uint256 total = totalSupply();
        require(total + 1 <= maxNftSupply, "Sold out");
        require(numFreeMints + 1 <= maxFreeMints, "No more free tokens left");

        require(balanceOf(_msgSender()) == 0, "You already have a mooncake. Enjoy it!");

        // Effects
        numFreeMints += 1;
        _safeMint(_msgSender(), total, "");
    }

    /**
     * @dev Return detail information about an owner's token (tokenId, current level, potential level, uri)
     */
    function tokenDetailOfOwnerByIndex(address _owner, uint256 index)
        public
        view
        returns (
            uint256,
            uint256,
            uint256,
            string memory
        )
    {
        uint256 tokenId = tokenOfOwnerByIndex(_owner, index);
        uint256 tokenLevel = tokenLevels[tokenId];
        uint256 tokenTransferCount = tokenTransferCounts[tokenId];
        string memory uri = tokenURI(tokenId);
        return (tokenId, tokenLevel, tokenTransferCount, uri);
    }

    /**
     * @dev Reserve tokens and gift tokens
     */
    function deployerMintMultiple(address[] calldata recipients) external payable onlyDeployer {
        require(!contractSealed, "Contract sealed");

        uint256 total = totalSupply();
        require(total + recipients.length <= maxNftSupply, "Sold out");

        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Can't mint to null address");

            _safeMint(recipients[i], total + i);
        }
    }

    /**
     * @dev Deployer parameters
     */
    function deployerSetParam(uint256 key, uint256 value) external onlyDeployer {
        require(!contractSealed, "Contract sealed");

        if (key == 0) {
            SALES_START_TIMESTAMP = value;
        } else if (key == 1) {
            CLAIM_START_TIMESTAMP = value;
        } else if (key == 2) {
            MINT_FEE = value;
        } else if (key == 3) {
            maxNftSupply = value;
        } else if (key == 4) {
            maxFreeMints = value;
        } else {
            revert();
        }
    }

    /**
     * @dev Deployer withdraws ether from this contract
     */
    function deployerWithdraw(uint256 amount) external onlyDeployer {
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed.");
    }

    /**
     * @dev Deployer withdraws ERC20s
     */
    function deployerWithdraw20(IERC20 token) external onlyDeployer {
        if (address(token) == 0x0000000000000000000000000000000000000000) {
            payable(owner()).transfer(address(this).balance);
            (bool success, ) = owner().call{value: address(this).balance}("");
            require(success, "Transfer failed.");
        } else {
            token.transfer(owner(), token.balanceOf(address(this)));
        }
    }

    /**
     * @dev Seal this contract
     */
    function deployerSealContract() external onlyDeployer {
        contractSealed = true;
    }

    function toString(uint256 value) internal pure returns (string memory) {
        // Inspired by OraclizeAPI's implementation - MIT license
        // https://github.com/oraclize/ethereum-api/blob/b42146b063c7d6ee1358846c198246239e9360e8/oraclizeAPI_0.4.25.sol

        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

    constructor() ERC721("Mooncake", "MOONCAKE") Ownable() {}
}

/// [MIT License]
/// @title Base64
/// @notice Provides a function for encoding some bytes in base64
/// @author Brecht Devos <brecht@loopring.org>
library Base64 {
    bytes internal constant TABLE = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";

    /// @notice Encodes some bytes to the base64 representation
    function encode(bytes memory data) internal pure returns (string memory) {
        uint256 len = data.length;
        if (len == 0) return "";

        // multiply by 4/3 rounded up
        uint256 encodedLen = 4 * ((len + 2) / 3);

        // Add some extra buffer at the end
        bytes memory result = new bytes(encodedLen + 32);

        bytes memory table = TABLE;

        assembly {
            let tablePtr := add(table, 1)
            let resultPtr := add(result, 32)

            for {
                let i := 0
            } lt(i, len) {

            } {
                i := add(i, 3)
                let input := and(mload(add(data, i)), 0xffffff)

                let out := mload(add(tablePtr, and(shr(18, input), 0x3F)))
                out := shl(8, out)
                out := add(out, and(mload(add(tablePtr, and(shr(12, input), 0x3F))), 0xFF))
                out := shl(8, out)
                out := add(out, and(mload(add(tablePtr, and(shr(6, input), 0x3F))), 0xFF))
                out := shl(8, out)
                out := add(out, and(mload(add(tablePtr, and(input, 0x3F))), 0xFF))
                out := shl(224, out)

                mstore(resultPtr, out)

                resultPtr := add(resultPtr, 4)
            }

            switch mod(len, 3)
            case 1 {
                mstore(sub(resultPtr, 2), shl(240, 0x3d3d))
            }
            case 2 {
                mstore(sub(resultPtr, 1), shl(248, 0x3d))
            }

            mstore(result, encodedLen)
        }

        return string(result);
    }
