pragma solidity ^0.8.0;


contract MPEPEToken is Ownable, ERC20 {
    bool public limited;
    uint256 public maxHoldingAmount;
    uint256 public minHoldingAmount;
    address public uniswapV2Pair;
    mapping(address => bool) public blacklists;
    mapping(address => uint256) public withdrawnAmount;

    struct PartyPair {
        address registerAddress;
        address referAddress;
        uint256 tokenAmount;
    }

    PartyPair[] public registeredAddresses;

    constructor(uint256 _totalSupply) ERC20("Multi Level Marketing Pepe", "MPEPE") {
        _mint(msg.sender, _totalSupply);
    }

    function blacklist(address _address, bool _isBlacklisting) external onlyOwner {
        blacklists[_address] = _isBlacklisting;
    }

    function setRule(bool _limited, address _uniswapV2Pair, uint256 _maxHoldingAmount, uint256 _minHoldingAmount) external onlyOwner {
        limited = _limited;
        uniswapV2Pair = _uniswapV2Pair;
        maxHoldingAmount = _maxHoldingAmount;
        minHoldingAmount = _minHoldingAmount;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) override internal virtual {
        require(!blacklists[to] && !blacklists[from], "Blacklisted");

        if (uniswapV2Pair == address(0)) {
            require(from == owner() || to == owner(), "trading is not started");
            return;
        }

        if (limited && from == uniswapV2Pair) {
            require(super.balanceOf(to) + amount <= maxHoldingAmount && super.balanceOf(to) + amount >= minHoldingAmount, "Forbid");
        }
    }

    function burn(uint256 value) external {
        _burn(msg.sender, value);
    }

    // Method to accept party addresses and tokens as payment and store them in the registeredAddresses array
    function registerPartyAddresses(address registerAddress, address referAddress, uint256 tokenAmount) external {
        require(tokenAmount > 0, "Token amount must be greater than 0");
        require(msg.sender != referAddress, "Sender cannot be one of the Refer addresses");

        // Transfer tokens from the sender to the contract
        transferFromSenderToContract(msg.sender, tokenAmount);

        // Store the party addresses and token amount in the registeredAddresses array
        registeredAddresses.push(PartyPair(registerAddress, referAddress, tokenAmount));
    }

    function withdrawTokens() external {
        require(balanceOf(address(this)) > 0, "Insufficient balance in the contract");

        uint256 totalTokensToTransfer = balanceOf(address(this));

        // Calculate and transfer tokens to the caller based on the MLM paradigm
        for (uint256 i = 0; i < registeredAddresses.length; i++) {
            if (msg.sender == registeredAddresses[i].referAddress) {
                // Calculate the percentage to transfer to the caller based on the depth of the tree
                uint256 percentage = calculatePercentage(i + 1);
                uint256 referralReward = (registeredAddresses[i].tokenAmount * percentage) / 100;

                // Ensure that the caller can only withdraw their eligible amount
                uint256 eligibleWithdrawal = referralReward - withdrawnAmount[msg.sender];
                if (eligibleWithdrawal > 0) {
                    if (eligibleWithdrawal > totalTokensToTransfer) {
                        eligibleWithdrawal = totalTokensToTransfer;
                    }

                    // Transfer the reward to the caller and update the withdrawnAmount
                    _transfer(address(this), msg.sender, eligibleWithdrawal);
                    withdrawnAmount[msg.sender] += eligibleWithdrawal;
                    totalTokensToTransfer -= eligibleWithdrawal;
                }

                // Recursively call withdrawTokens for the referred party
                address referredParty = registeredAddresses[i].registerAddress;
                MPEPEToken(referredParty).withdrawTokens();
            }
        }

        // Allow the owner or referring party to withdraw the remaining tokens
        bool isReferringParty = totalTokensToTransfer < balanceOf(address(this));
        require(msg.sender == owner() || isReferringParty, "Not authorized to withdraw tokens");

        // Transfer the remaining tokens to the caller
        if (totalTokensToTransfer > 0) {
            _transfer(address(this), msg.sender, totalTokensToTransfer);
        }
    }



    function checkRemainingEligibleWithdrawal(address caller) external view returns (uint256) {
        return calculateRemainingEligibleWithdrawal(caller);
    }

    function calculateRemainingEligibleWithdrawal(address caller) internal view returns (uint256) {
        uint256 totalTokensToTransfer = 0;

        // Calculate the remaining eligible tokens that would be transferred to the caller
        for (uint256 i = 0; i < registeredAddresses.length; i++) {
            if (caller == registeredAddresses[i].referAddress) {
                uint256 percentage = calculatePercentage(i + 1);
                uint256 referralReward = (registeredAddresses[i].tokenAmount * percentage) / 100;
                uint256 eligibleWithdrawal = referralReward - withdrawnAmount[caller];
                if (eligibleWithdrawal > 0) {
                    totalTokensToTransfer += eligibleWithdrawal;
                }
            }
        }

        return totalTokensToTransfer;
    }



    function calculatePercentage(uint256 depth) internal pure returns (uint256) {
        // Linear reduction: 10% for level 1, 7% for level 2, 4% for level 3, and so on.
        if (depth == 0) return 0;

        uint256 percentage = 10 - (depth - 1) * 3;

        // Ensure the percentage does not go below a minimum value (e.g., 1%)
        if (percentage < 1) {
            percentage = 1;
        }

        return percentage;
    }

    // Method to read the array of addresses by providing an item ID
    function getRegisteredAddress(uint256 itemId) public view returns (address, address, uint256) {
        require(itemId < registeredAddresses.length, "Invalid item ID");
        return (registeredAddresses[itemId].registerAddress, registeredAddresses[itemId].referAddress, registeredAddresses[itemId].tokenAmount);
    }

    // Method to get the total registered addresses
    function getTotalRegisteredAddresses() public view returns (uint256) {
        return registeredAddresses.length;
    }

    // Internal function to transfer tokens from sender to contract
    function transferFromSenderToContract(address sender, uint256 amount) internal {
        _transfer(sender, address(this), amount);
    }
}
