                rewardRates[i] = reward[i].div(rewardsDuration);
            }
        } else {
            uint256 remaining = periodFinish.sub(block.timestamp);

            uint256 leftover;
            for (uint256 i = 0; i < rewardsTokens.length; i++) {
                leftover = remaining.mul(rewardRates[i]);
                rewardRates[i] = reward[i].add(leftover).div(rewardsDuration);
            }
        }

        lastUpdateTime = block.timestamp;
        periodFinish = block.timestamp.add(rewardsDuration);
        emit RewardAdded(reward);
    }

    // Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner {
        require(tokenAddress != address(stakingToken), "Cannot withdraw the staking token");
        IERC20Metadata(tokenAddress).safeTransfer(owner(), tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    function setRewardsDuration(uint256 _rewardsDuration) external onlyOwner {
        require(
            block.timestamp > periodFinish,
            "Previous rewards period must be complete before changing the duration for the new period"
        );
        rewardsDuration = _rewardsDuration;
        emit RewardsDurationUpdated(rewardsDuration);
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward(address account) {
        rewardsPerTokenStored = rewardsPerToken();
        lastUpdateTime = lastTimeRewardApplicable();
        if (account != address(0)) {
            uint256[] memory accountEarned = earned(account);
            for (uint256 i = 0; i < rewardsTokens.length; i++) {
                rewards[account][i] = accountEarned[i];
                userRewardPerTokenPaid[account][i] = rewardsPerTokenStored[i];
            }
        }
        _;
    }

    /* ========== EVENTS ========== */

    event RewardAdded(uint256[] reward);
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed token, address indexed user, uint256 reward);
    event RewardsDurationUpdated(uint256 newDuration);
    event Recovered(address token, uint256 amount);
}

contract StakingRewardFactory is Ownable {
    using SafeERC20 for IERC20Metadata;
    using Address for address;

    address public DFX_MULTISIG;

    event NewStakingReward(
        address indexed rewardToken,
        address indexed stakingToken,
        address indexed stakingContract
    );
    event NewStakingRewardMulti(
        address[] indexed rewardTokens,
        address indexed stakingToken,
        address indexed stakingContract
    );

    constructor() public {}

    function setMultiSig(address _sig) external onlyOwner {
        DFX_MULTISIG = _sig;
    }

    function newStakingReward(
        address _rewardToken,
        address _stakingToken
    ) external onlyOwner returns (address) {
        require(_rewardToken.isContract(), "invalid rewardToken");
        require(_stakingToken.isContract(), "invalid stakingToken");
        StakingRewards stakingReward = new StakingRewards(
            _rewardToken,
            _stakingToken
        );
        stakingReward.transferOwnership(DFX_MULTISIG);
        emit NewStakingReward(
            _rewardToken,
            _stakingToken,
            address(stakingReward)
        );
        return address(stakingReward);
    }

    function newStakingRewardMulti(
        address[] memory _rewardTokens,
        address _stakingToken
    ) external onlyOwner returns (address) {
        uint totalRewardTokens = _rewardTokens.length;
        require(totalRewardTokens > 0, "reward token required");
        require(_stakingToken.isContract(), "invalid stakingToken");
        IERC20Metadata[] memory rewardTokens = new IERC20Metadata[](
            totalRewardTokens
        );
        for (uint i = 0; i < totalRewardTokens; ++i) {
            require(_rewardTokens[i].isContract(), "invalid-rewardToken");
            rewardTokens[i] = IERC20Metadata(_rewardTokens[i]);
        }
        IERC20Metadata stakingToken = IERC20Metadata(_stakingToken);
        StakingRewardsMulti multi = new StakingRewardsMulti(
            rewardTokens,
            stakingToken
        );
        multi.transferOwnership(DFX_MULTISIG);
        emit NewStakingRewardMulti(
            _rewardTokens,
            _stakingToken,
            address(multi)
        );
        return address(multi);
    }
}
