    "contracts/EzToknMainnet/Token.sol": {
      "content": "// SPDX-License-Identifier: MIT

// Deployed via EzTokn: Smart Contracts made EZ.
// Website: https://eztokn.io/

pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract Token is ERC20, Ownable, ReentrancyGuard {

    using SafeMath for uint256;

    IUniswapV2Factory public constant UNISWAP_FACTORY =
    IUniswapV2Factory(0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f);

    IUniswapV2Router02 public constant UNISWAP_ROUTER = 
    IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);

    address public immutable UNISWAP_V2_PAIR;

    bool public limitsActive = true;
    bool public tradingActive;
    bool public swapEnabled;
    bool private swapping;

    uint8 private platformFee;

    uint256 public maxWalletBalance;
    uint256 public maxTransactionAmount;

    uint256 public swapThreshold;
    uint256 public sellTax;
    uint256 public buyTax;

    address public taxFeeReceiver;
    address public platformWallet;

    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public _isExcludedMaxTransactionAmount;

    struct ContractSpecifications {
        string name;
        string symbol;
        uint256 supply;
        uint8 maxWallet;
        uint8 maxTransaction;
        uint8 platformFee;
        uint8 buyTax;
        uint8 sellTax;
    }

    ContractSpecifications public specifications;

    event EnabledTrading(bool tradingActive);
    event LimitsRemoved();
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event UpdatedMaxTxnAmount(uint256 newAmount);
    event UpdatedMaxWalletAmount(uint256 newAmount);
    event UpdatedTaxReceiverWallet(address indexed newWallet);
    event MaxTransactionExclusion(address _address, bool excluded);


    constructor(
        string memory _name,
        string memory _symbol,
        address _platformAddress,
        uint256 _tokenSupply,
        uint8 _maxWalletPercentage,
        uint8 _maxTxPercentage,
        uint8 _platformFee,
        uint8 _buyTax,
        uint8 _sellTax
    )ERC20(_name, _symbol){
        require(
            _buyTax >= _platformFee && _sellTax >= platformFee,
            "Error: Buy/sell tax must be equal or greater than platform fee!"
        );
        require(
            _buyTax <= 100 && _sellTax <= 100 && _platformFee <= 100, 
            "Error: Invalid percentage!"
        );

        specifications = ContractSpecifications({
            name: _name,
            symbol: _symbol,
            supply: _tokenSupply,
            maxWallet: _maxWalletPercentage,
            maxTransaction: _maxTxPercentage,
            platformFee: _platformFee,
            buyTax: _buyTax,
            sellTax: _sellTax
        });

        UNISWAP_V2_PAIR = UNISWAP_FACTORY.createPair(
            address(this),
            UNISWAP_ROUTER.WETH()
        );

        uint256 supply = _tokenSupply * 1 ether;

        platformFee = _platformFee;
        buyTax = _buyTax;
        sellTax = _sellTax;

        swapThreshold = supply.mul(50).div(10000);
        maxTransactionAmount = supply.mul(_maxTxPercentage).div(100);
        maxWalletBalance = supply.mul(_maxWalletPercentage).div(100);

        taxFeeReceiver = tx.origin;
        platformWallet = _platformAddress;

        _approve(address(this), address(UNISWAP_ROUTER), ~uint256(0));
        _approve(tx.origin, address(UNISWAP_ROUTER), type(uint256).max);

        _excludeFromMaxTransaction(address(UNISWAP_ROUTER), true);

        _excludeFromMaxTransaction(tx.origin, true);
        _excludeFromMaxTransaction(address(this), true);
        _excludeFromMaxTransaction(address(0xdead), true);
        excludeFromFees(tx.origin, true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(0xdead), true);

        _mint(tx.origin, supply);
        transferOwnership(tx.origin);
    }

    receive() external payable {}


    function _excludeFromMaxTransaction(
        address updAds,
        bool isExcluded
    ) private {
        _isExcludedMaxTransactionAmount[updAds] = isExcluded;
        emit MaxTransactionExclusion(updAds, isExcluded);
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }


    function setMaxWalletAmount(uint256 _newMaxWalletPercentage) external onlyOwner{

        require(
            _newMaxWalletPercentage > 0 && _newMaxWalletPercentage < 11,
            "Error: Invalid percentage!"
        );

        uint256 supply = totalSupply();
        maxWalletBalance = supply.mul(_newMaxWalletPercentage).div(100);

        emit UpdatedMaxWalletAmount(maxWalletBalance);

    }

    function setMaxTransactionAmount(uint256 _newMaxTransactionPercentage) external onlyOwner{

        require(
            _newMaxTransactionPercentage > 0 && _newMaxTransactionPercentage < 11,
            "Error: Invalid percentage!"
        );

        uint256 supply = totalSupply();
        maxTransactionAmount = supply.mul(_newMaxTransactionPercentage).div(100);

        emit UpdatedMaxTxnAmount(maxTransactionAmount);

    }

    function setSwapBackThreshold(uint256 _newThresholdAmountInTokens) external onlyOwner {

        uint256 totalSup = totalSupply();

        require(
            _newThresholdAmountInTokens >= totalSup.div(200) 
            && _newThresholdAmountInTokens <= totalSup.div(20),
            "Error: Token swap back threshold must remain between 0.5% and 5%!"
        );

        swapThreshold = _newThresholdAmountInTokens;
    }

    function setTaxes(uint256 _newBuyTax, uint256 _newSellTax) external onlyOwner {

        require(
            _newBuyTax >= platformFee && _newSellTax >= platformFee,
            "Error: Tax must be greater or equal to platform plan fee!"
        );
        require(
            _newBuyTax <= 100 && _newSellTax <= 100,
            "Error: Invalid percentage!"
        );

        buyTax = _newBuyTax;
        sellTax = _newSellTax;

    }

    function setTaxReceiverWallet(address _newTaxFeeReceiver) external onlyOwner {

        require(
            _newTaxFeeReceiver != address(0),
            "Error: _newTaxFeeReceiver address cannot be 0!"
        );

        taxFeeReceiver = payable(_newTaxFeeReceiver);
        emit UpdatedTaxReceiverWallet(_newTaxFeeReceiver);
    }

    function openTrading() public onlyOwner {
        tradingActive = true;
        swapEnabled = true;
        emit EnabledTrading(tradingActive);
    }

    function removeLimits() external onlyOwner {
        limitsActive = false;
        emit LimitsRemoved();
    }





    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "amount must be greater than 0");

        if (limitsActive) {
            if (
                from != owner() &&
                to != owner() &&
                to != address(0) &&
                to != address(0xdead)
            ) {
                if (!tradingActive) {
                    require(
                        _isExcludedMaxTransactionAmount[from] ||
                            _isExcludedMaxTransactionAmount[to],
                        "ERROR: Trading is not active."
                    );
                    require(from == owner(), "ERROR: Trading is enabled");
                }

                //when buy
                if (
                    from == UNISWAP_V2_PAIR && !_isExcludedMaxTransactionAmount[to]
                ) {
                    require(
                        amount <= maxTransactionAmount,
                        "ERROR: Buy transfer amount exceeds the max buy."
                    );
                    require(
                        amount + balanceOf(to) <= maxWalletBalance,
                        "ERROR: Cannot Exceed max wallet"
                    );
                }
                //when sell
                else if (
                    to == UNISWAP_V2_PAIR && !_isExcludedMaxTransactionAmount[from]
                ) {
                    require(
                        amount <= maxTransactionAmount,
                        "ERROR: Sell transfer amount exceeds the max sell."
                    );
                } else if (
                    !_isExcludedMaxTransactionAmount[to] &&
                    !_isExcludedMaxTransactionAmount[from]
                ) {
                    require(
                        amount + balanceOf(to) <= maxWalletBalance,
                        "ERROR: Cannot Exceed max wallet"
                    );
                }
            }
        }

        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapThreshold;

        if (
            canSwap &&
            swapEnabled &&
            !swapping &&
            !(from == UNISWAP_V2_PAIR) &&
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to]
        ) {
            swapping = true;
            swapBack();
            swapping = false;
        }

        bool takeFee = true;

        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        uint256 fees = 0;


        if (takeFee) {
            // Sell
            if (to == UNISWAP_V2_PAIR && sellTax > 0) {
                fees = (amount * sellTax) / 100;
            }
            // Buy
            else if (from == UNISWAP_V2_PAIR && buyTax > 0) {
                fees = (amount * buyTax) / 100;
            }

            if (fees > 0) {
                super._transfer(from, address(this), fees);
            }

            amount -= fees;
        }

        super._transfer(from, to, amount);
    }


    function swapTokensForEth(uint256 tokenAmount) private {

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = UNISWAP_ROUTER.WETH();

        UNISWAP_ROUTER.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function swapBack() private nonReentrant {

        bool success;
        swapTokensForEth(swapThreshold);
        uint256 ethBalance = address(this).balance;

        if(platformFee > 0){
            uint256 ethForPlatform = ethBalance.mul(platformFee).div(sellTax);
            (success, ) = address(platformWallet).call{value: ethForPlatform}("");
        }

        (success, ) = address(taxFeeReceiver).call{value: address(this).balance}("");

    }


    function withdrawToken(IERC20 _token, uint256 _amount) external onlyOwner {

        require(
            _token.balanceOf(address(this)) >= _amount,
            "ERROR: Insufficient balance to complete txn !"
        );

        _token.transfer(msg.sender, _amount);
    }


    // NOTE: ARGUMENT IN ETHER (1e18)
    function airdropTokens(
        address[] calldata addresses,
        uint256[] calldata amounts
    ) external onlyOwner {
        require(
            addresses.length == amounts.length,
            "ERROR: Array sizes must be equal !"
        );
        uint256 i = 0;
        while (i < addresses.length) {
            uint256 _amount = amounts[i] * 1e18;
            _transfer(msg.sender, addresses[i], _amount);
            i += 1;
        }
    }



}"
