         require(amount == 100, "Amount must be exactly 100");

        uint256 adjustedAmount = amount * (10**uint256(18));
        erc20Token.transferFrom(msg.sender, address(this), adjustedAmount);

        erc1155Token.safeTransferFrom(address(this), msg.sender, erc1155TokenId, 1, "");
    }
}
