// Creator: chisim

// @title:      Circle Line NFT
// @twitter:    https://twitter.com/@circlelinenft 
// @url:        https://www.omensmultiverse.com/circleline 

/*
 *  ██████╗██╗██████╗  ██████╗██╗     ███████╗    ██╗     ██╗███╗   ██╗███████╗    ███╗   ██╗███████╗████████╗
 * ██╔════╝██║██╔══██╗██╔════╝██║     ██╔════╝    ██║     ██║████╗  ██║██╔════╝    ████╗  ██║██╔════╝╚══██╔══╝
 * ██║     ██║██████╔╝██║     ██║     █████╗      ██║     ██║██╔██╗ ██║█████╗      ██╔██╗ ██║█████╗     ██║   
 * ██║     ██║██╔══██╗██║     ██║     ██╔══╝      ██║     ██║██║╚██╗██║██╔══╝      ██║╚██╗██║██╔══╝     ██║   
 * ╚██████╗██║██║  ██║╚██████╗███████╗███████╗    ███████╗██║██║ ╚████║███████╗    ██║ ╚████║██║        ██║   
 *  ╚═════╝╚═╝╚═╝  ╚═╝ ╚═════╝╚══════╝╚══════╝    ╚══════╝╚═╝╚═╝  ╚═══╝╚══════╝    ╚═╝  ╚═══╝╚═╝        ╚═╝   
 *
 *     THE CIRCLE LINE NFT COLLECTION 2023
 *     A Omens Multiverse project in collaboration with Taipan Films 
 */

pragma solidity 0.8.17;

import "erc721a/contracts/extensions/ERC721AQueryable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract CircleLineNFT is ERC721AQueryable, Ownable, ReentrancyGuard {
    using ECDSA for bytes32;
    address private _freeMintSignerAddress;
    address private _preSaleSignerAddress;
    uint256 public MAX_SUPPLY = 5000;
    uint256 public constant MAX_PER_WALLET = 5;
    uint256 public constant MINT_PRICE = 0.10 ether;
    uint256 public constant MINT_PRICE_PRESALE = 0.05 ether;
    string private _baseTokenURI;
    string private _hiddenTokenURI = "ipfs://QmXqkH4C1qF66Dpmxca68qPXm7KsdY3VjCxunn4FDhvgho";
    bool public preSaleActive = false;
    bool public publicSaleActive = false;
    bool public revealed = false;
    bool public paused = true;
    mapping(address => uint256) private mintedList;
    mapping(address => bool) private freeMintedList;

    constructor(address freeMintSignerAddress, address preSaleSignerAddress) ERC721A("Circle Line NFT", "CLNFT") {
         _freeMintSignerAddress = freeMintSignerAddress;
         _preSaleSignerAddress = preSaleSignerAddress;
    }

    function _startTokenId() internal view virtual override returns (uint256) {
        return 1;
    }

    modifier onlySender() {
        require(tx.origin == msg.sender, "Only externally owned accounts allowed");
        _;
    }

    function tokenURI(uint256 tokenId) public view virtual override(IERC721A, ERC721A) returns (string memory)
    {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");        
        if (!revealed) {
            return _hiddenTokenURI;
        }
        return bytes(_baseTokenURI).length > 0 ? string(abi.encodePacked(_baseTokenURI, Strings.toString(tokenId), ".json")) : "";
    }

    function freeMint(bytes calldata _signature) external payable onlySender nonReentrant
    {
        require(!paused, "Transactions are paused");
        require(preSaleActive, "Pre-Sale is not active");
        require(!freeMintedList[msg.sender], "You already claimed a free mint");

        require(_freeMintSignerAddress == keccak256(abi.encodePacked(
                "\x19Ethereum Signed Message:\n32", bytes32(uint256(uint160(msg.sender)))
            )).recover(_signature), "You are not in the Free Mint whitelist");

        _safeMint(msg.sender, 1);
        freeMintedList[msg.sender] = true;
        mintedList[msg.sender] += 1;
    }

    function preSaleMint(bytes calldata _signature, uint256 quantity) external payable onlySender nonReentrant
    {
        require(!paused, "Transactions are paused");
        require(preSaleActive, "Pre-Sale is not active");
        require(quantity > 0, "Quantity must be more than 0");
        require(quantity <= MAX_PER_WALLET, "Exceed quantity allowed per batch");
        require(totalSupply() + quantity <= MAX_SUPPLY, "Exceed collection size");
        require(msg.value >= MINT_PRICE_PRESALE * quantity, "Insufficient funds");
        require(mintedList[msg.sender] + quantity <= MAX_PER_WALLET, "Exceed quantity allowed per wallet");

        require(_preSaleSignerAddress == keccak256(abi.encodePacked(
                "\x19Ethereum Signed Message:\n32", bytes32(uint256(uint160(msg.sender)))
            )).recover(_signature), "You are not in the Pre-Sale whitelist");

        _safeMint(msg.sender, quantity);
        mintedList[msg.sender] += quantity;
    }

    function mint(uint256 quantity) external payable onlySender nonReentrant
    {
        uint256 supply = totalSupply();
        require(!paused, "Transactions are paused");
        require(publicSaleActive, "Public sale is not active");
        require(supply < MAX_SUPPLY, "We are sold out");
        require(quantity > 0, "Quantity must be more than 0");
        require(quantity <= MAX_PER_WALLET, "Exceed quantity allowed per batch");
        require(supply + quantity <= MAX_SUPPLY, "Exceed collection size");
        require(msg.value >= MINT_PRICE * quantity, "Insufficient funds");
        require(mintedList[msg.sender] + quantity <= MAX_PER_WALLET, "Exceed quantity allowed per wallet");

        _safeMint(msg.sender, quantity);
        mintedList[msg.sender] += quantity;
    }

    function isFreeMinted(address _account) public view onlySender returns (bool) {
        return(freeMintedList[_account]);
    }

    function batchMint(uint256 quantity) external onlyOwner {
        require(quantity > 0, "Quantity must be more than 0");
        require(totalSupply() + quantity <= MAX_SUPPLY, "Exceed collection size");

        _safeMint(owner(), quantity);
        mintedList[owner()] += quantity;
    }

    function setSupply(uint256 _supply) external onlyOwner {
        require(_supply > 0, "Quantity must be more than 0");
        require(MAX_SUPPLY <= 5000, "Exceed supply available");
        MAX_SUPPLY = _supply;
    }

    function setPaused(bool _status) external onlyOwner {
        paused = _status;
    }

    function setPreSale(bool _status) external onlyOwner {
        preSaleActive = _status;
    }

    function setPublicSale(bool _status) external onlyOwner {
        publicSaleActive = _status;
    }

    function setReveal(bool _status) external onlyOwner {
        revealed = _status;
    }

    function setBaseURI(string calldata _baseURI) external onlyOwner {
        _baseTokenURI = _baseURI;
    }

    function withdraw() external onlyOwner {
        require(address(this).balance > 0, "Nothing to withdraw");        
        (bool success, ) = payable(owner()).call{value: address(this).balance}("");
		require(success, "Withdraw failed");
    }

}"
    },
