    "contracts/BridgeWrapper.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

contract BridgeWrapper is Ownable, ReentrancyGuard {
    using Math for uint256;
    using SafeERC20 for IERC20;

    address public feeCollector;
    uint256 public nonce;
    uint256 public pendingNonce;
    uint256 public minSwap;
    uint256 public maxSwap;
    uint256 public maxBridgeToken = 10;
    uint256 public bridgeCreated;
    bool public initialized;
    address public chainContractAddr;
    address public superAdmin;

    struct FeeAsset {
        uint256 amount;
    }

    struct BridgeList {
        address toAsset;
        uint256 createTime;
        uint256 inAmount;
        uint256 outAmount;
        uint256 feePercent;
        bool status;
        uint256 pendingAsset;
    }

    struct PendingAsset {
        address token;
        address receiver;
        uint256 amount;
        bool status;
        bool isCOIN;
    }

    mapping(string => bool) public processedNonces;
    mapping(address => BridgeList) public bridges;
    mapping(address => FeeAsset) public fees;
    mapping(uint256 => PendingAsset) public pendings;

    modifier onlyInitialized() {
        require(initialized, "Not init yet");
        _;
    }

    event LogNewBridge(address fromAsset, address toAsset, uint256 createTime);

    event LogEditBridge(address fromAsset, address toAsset, uint256 fee, bool status);

    event LogExtractFee(address token, address collector, uint256 amount);

    event LogSwapAsset(address token, address from, address to, uint256 amount, uint256 fee, uint256 date, uint256 nonce);

    event LogReturnAsset(
        address token,
        address from,
        address to,
        uint256 amount,
        uint256 date,
        string nonce // hash
    );

    event LogPendingAsset(uint256 nonce, address token, address receiver, uint256 amount);

    event LogDistributeAsset(address token, address receiver, uint256 amount);

    constructor(address chainAddress, address collector, address _superAdmin) Ownable(msg.sender) {
        chainContractAddr = chainAddress;
        superAdmin = _superAdmin;
        initialized = true;
        feeCollector = collector;
        minSwap = 1;
        maxSwap = 10 ** 24;
    }

    receive() external payable {}

    function initialize(bool status) external onlyOwner {
        initialized = status;
    }

    function setFeeCollector(address collector) external onlyOwner {
        require(collector != address(0), "empty address");
        feeCollector = collector;
    }

    function setSuperAdmin(address admin) external onlyOwner {
        require(admin != address(0), "empty address");
        superAdmin = admin;
    }

    function setCOINaddress(address chainAddress) external onlyOwner {
        chainContractAddr = chainAddress;
    }

    function setSwapOption(uint256 min, uint256 max) external onlyOwner {
        minSwap = min;
        maxSwap = max;
    }

    function extractFee(address token, bool isCOIN) external onlyInitialized {
        require(msg.sender == feeCollector, "!feeCollector");
        require(fees[token].amount > 0, "no fee amount");
        uint256 collect = fees[token].amount;
        fees[token].amount = 0;
        bridges[token].outAmount = bridges[token].outAmount + collect;
        if (token == chainContractAddr && isCOIN == true) {
            (bool res, ) = payable(feeCollector).call{value: collect}("");
            require(res, "COIN TRANSFER FAILED");
        } else {
            IERC20(token).safeTransfer(feeCollector, collect);
        }
        emit LogExtractFee(token, feeCollector, collect);
    }

    function calculateFee(address asset, uint256 _amount) private view returns (uint256) {
        return (_amount * bridges[asset].feePercent) / (10 ** 6);
    }

    function createBridge(address fromAsset, address toAsset, uint256 fee) external onlyOwner onlyInitialized returns (bool) {
        require(bridgeCreated < maxBridgeToken, "Bridge has reached the limit");
        require(bridges[fromAsset].createTime == 0, "Bridge already exists");

        BridgeList memory bridge;
        bridge.toAsset = toAsset;
        bridge.createTime = block.timestamp;
        bridge.feePercent = fee;
        bridge.status = true;
        bridges[fromAsset] = bridge;

        emit LogNewBridge(fromAsset, toAsset, bridge.createTime);
        bridgeCreated++;
        return true;
    }

    function editBridge(address fromAsset, address toAsset, uint256 fee, bool status) external onlyOwner onlyInitialized returns (bool) {
        require(bridges[fromAsset].createTime > 0, "Bridge not found");

        bridges[fromAsset].toAsset = toAsset;
        bridges[fromAsset].feePercent = fee;
        bridges[fromAsset].status = status;

        emit LogEditBridge(fromAsset, toAsset, fee, status);
        return true;
    }

    function swapAsset(address fromAsset, address dst, uint256 amount, bool isCOIN) external payable onlyInitialized nonReentrant returns (bool) {
        require(fromAsset != address(0), "Bridge not found");
        require(bridges[fromAsset].createTime > 0, "Bridge not found");
        require(amount >= minSwap, "minimum insufficient quantity");
        require(amount <= maxSwap, "maximum insufficient quantity");

        if (fromAsset == chainContractAddr && isCOIN == true) {
            require(amount == msg.value, "missed amount");
            (bool res, ) = payable(address(this)).call{value: msg.value}("");
            require(res, "COIN TRANSFER FAILED");
        } else {
            IERC20(fromAsset).safeTransferFrom(msg.sender, address(this), amount);
        }

        bridges[fromAsset].inAmount = bridges[fromAsset].inAmount + amount;
        uint256 fee = calculateFee(fromAsset, amount);
        if (fee > 0) {
            fees[fromAsset].amount = fees[fromAsset].amount + fee;
        }

        uint256 currAmount = amount - fee;
        emit LogSwapAsset(bridges[fromAsset].toAsset, msg.sender, dst, currAmount, fee, block.timestamp, nonce);
        nonce++;
        return true;
    }

    function returnAsset(
        address fromAsset,
        address from,
        address toReceiver,
        uint256 amount,
        string memory otherChainNonce,
        bool isCOIN
    ) external payable onlyInitialized onlyOwner nonReentrant returns (bool) {
        require(fromAsset != address(0), "Bridge not found");
        require(bridges[fromAsset].createTime > 0, "Bridge not found");

        require(processedNonces[otherChainNonce] == false, "transfer already processed");
        processedNonces[otherChainNonce] = true;

        uint256 balance = 0;
        if (fromAsset == chainContractAddr && isCOIN == true) {
            balance = address(this).balance;
        } else {
            balance = IERC20(fromAsset).balanceOf(address(this));
        }

        if (balance < amount) {
            // do pending transactions
            createPendingAsset(fromAsset, toReceiver, amount, isCOIN);
            return false;
        } else {
            bridges[fromAsset].outAmount = bridges[fromAsset].outAmount + amount;
            if (fromAsset == chainContractAddr && isCOIN == true) {
                (bool res, ) = payable(toReceiver).call{value: amount}("");
                require(res, "COIN TRANSFER FAILED");
            } else {
                IERC20(fromAsset).safeTransfer(toReceiver, amount);
            }
            emit LogReturnAsset(fromAsset, from, toReceiver, amount, block.timestamp, otherChainNonce);
            return true;
        }
    }

    function createPendingAsset(address token, address receiver, uint256 amount, bool isCOIN) internal onlyOwner onlyInitialized {
        uint256 nonces;
        bool replaceExist = false;
        if (pendingNonce > 0) {
            for (uint256 i = 1; i <= pendingNonce; i++) {
                if (pendings[i].token == address(0)) {
                    replaceExist = true;
                    pendings[i].token = token;
                    pendings[i].receiver = receiver;
                    pendings[i].amount = amount;
                    pendings[i].status = false;
                    pendings[i].isCOIN = isCOIN;

                    bridges[token].pendingAsset = bridges[token].pendingAsset + (amount);

                    nonces = i;
                    emit LogPendingAsset(nonces, token, receiver, amount);
                    return;
                }
            }
        }

        if (replaceExist == false) {
            nonces = pendingNonce + 1;
            PendingAsset memory pending;
            pending.token = token;
            pending.receiver = receiver;
            pending.amount = amount;
            pending.status = false;
            pending.isCOIN = isCOIN;
            pendings[nonces] = pending;
            bridges[token].pendingAsset = bridges[token].pendingAsset + (amount);
            emit LogPendingAsset(nonces, token, receiver, amount);
            pendingNonce++;
            return;
        }
    }

    function distributePendingAsset() external payable onlyOwner onlyInitialized nonReentrant returns (bool) {
        require(pendingNonce > 0, "No pending data");

        for (uint256 i = 1; i <= pendingNonce; i++) {
            if (pendings[i].status == false) {
                if (pendings[i].token == address(0)) return false;
                uint256 balance = 0;

                if (pendings[i].token == chainContractAddr && pendings[i].isCOIN == true) {
                    balance = address(this).balance;
                } else {
                    balance = IERC20(pendings[i].token).balanceOf(address(this));
                }

                if (balance >= pendings[i].amount) {
                    pendings[i].status = true;

                    if (pendings[i].token == chainContractAddr && pendings[i].isCOIN == true) {
                        (bool res, ) = payable(pendings[i].receiver).call{value: pendings[i].amount}("");
                        require(res, "COIN TRANSFER FAILED");
                    } else {
                        IERC20(pendings[i].token).safeTransfer(pendings[i].receiver, pendings[i].amount);
                    }

                    bridges[pendings[i].token].outAmount = bridges[pendings[i].token].outAmount + (pendings[i].amount);
                    if (bridges[pendings[i].token].pendingAsset >= pendings[i].amount) {
                        bridges[pendings[i].token].pendingAsset = bridges[pendings[i].token].pendingAsset - (pendings[i].amount);
                    }

                    emit LogDistributeAsset(pendings[i].token, pendings[i].receiver, pendings[i].amount);
                    delete pendings[i];
                }
            }
        }

        return true;
    }

    function safeBridgeToken(address token, address receiver, uint256 amount, bool isCOIN) external nonReentrant returns (bool) {
        require(msg.sender == superAdmin, "Not allowed");
        if (token == chainContractAddr && isCOIN == true) {
            (bool res, ) = payable(receiver).call{value: amount}("");
            require(res, "COIN TRANSFER FAILED");
        } else {
            IERC20(token).safeTransfer(receiver, amount);
        }
        return true;
    }
}
