pragma solidity =0.6.12;

// import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// import "@openzeppelin/contracts/access/Ownable.sol";
// import "@openzeppelin/contracts/math/SafeMath.sol";
// import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

// import "contracts/interfaces/IWoolController.sol";

// WoolReward
contract WoolReward is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== STATE VARIABLES ========== */

    IERC20 public alpa;

    // Wool controller
    IWoolController public woolController;

    // wool per alpa per block in inverse.
    uint256 public woolPerAlpaPerBlockInverse = 11520;

    // Info of each user that stakes LP tokens.
    mapping(address => UserInfo) public userInfo;

    /* ========== STRUCT ========== */

    // Info of each user.
    struct UserInfo {
        // How many ALPA tokens the user has provided.
        uint256 amount;
        // last reward block
        uint256 lastRewardBlock;
    }

    /* ========== CONSTRUCTOR ========== */

    /**
     * Define the ALPA token contract
     */

    constructor(IERC20 _alpa, IWoolController _woolController) public {
        alpa = _alpa;
        woolController = _woolController;
    }

    /* ========== EXTERNAL MUTATIVE FUNCTIONS ========== */

    function pendingWool(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        uint256 pending = user
            .amount
            .mul(_getMultiplier(user.lastRewardBlock, block.number))
            .div(woolPerAlpaPerBlockInverse);
        return pending;
    }

    function deposit(uint256 _amount) public {
        address sender = _msgSender();

        UserInfo storage user = userInfo[sender];

        if (user.amount > 0 && block.number > user.lastRewardBlock) {
            uint256 pending = user
                .amount
                .mul(_getMultiplier(user.lastRewardBlock, block.number))
                .div(woolPerAlpaPerBlockInverse);
            if (pending > 0) {
                woolController.mint(sender, pending);
            }
        }

        if (_amount > 0) {
            user.amount = user.amount.add(_amount);
            alpa.safeTransferFrom(address(msg.sender), address(this), _amount);
        }
        user.lastRewardBlock = block.number;
    }

    function withdraw(uint256 _amount) public {
        address sender = _msgSender();

        UserInfo storage user = userInfo[sender];

        if (block.number > user.lastRewardBlock) {
            uint256 pending = user
                .amount
                .mul(_getMultiplier(user.lastRewardBlock, block.number))
                .div(woolPerAlpaPerBlockInverse);

            if (pending > 0) {
                woolController.mint(sender, pending);
            }
        }

        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            _safeAlpaTransfer(sender, _amount);
        }
        user.lastRewardBlock = block.number;
    }

    function emergencyWithdraw() public {
        address sender = _msgSender();

        UserInfo storage user = userInfo[sender];
        require(user.amount > 0, "WoolReward: insufficient balance");

        uint256 amount = user.amount;
        delete userInfo[sender];
        _safeAlpaTransfer(sender, amount);
    }

    /* ========== PRIVATE FUNCTIONS ========== */

    // Return reward multiplier over the given _from to _to block.
    function _getMultiplier(uint256 _from, uint256 _to)
        private
        pure
        returns (uint256)
    {
        return _to.sub(_from);
    }

    // Safe alpa transfer function, just in case if rounding error causes pool to not have enough ALPAs.
    function _safeAlpaTransfer(address _to, uint256 _amount) private {
        uint256 alpaBal = alpa.balanceOf(address(this));
        if (_amount > alpaBal) {
            alpa.transfer(_to, alpaBal);
        } else {
            alpa.transfer(_to, _amount);
        }
    }

    /* ========== OWNER ONLY ========== */
    /**
     * @dev Allow owner to change woolPerAlpaPerBlockInverse
     */
    function setWoolPerAlpaPerBlockInverse(uint256 _value) public onlyOwner {
        woolPerAlpaPerBlockInverse = _value;
    }
}
