     /**
     *NETWORK 1 ETHEREUM COORDINATOR
     *0x271682DEB8C4E0901D1a1550aD2e64D568E69909
     */

    constructor(uint64 subscriptionId) 
        VRFConsumerBaseV2(0x271682DEB8C4E0901D1a1550aD2e64D568E69909)
        ConfirmedOwner(msg.sender)      {
        COORDINATOR = VRFCoordinatorV2Interface(0x271682DEB8C4E0901D1a1550aD2e64D568E69909);
        s_subscriptionId = subscriptionId;
    }

    function bottlePop(uint256[] calldata bottleTokenNums) public payable onlyBottleOwner(bottleTokenNums) {
        if(bottleTokenNums.length == 0){revert NotEnough("checkBottleAmt");}
        if(!raffleOn){revert NoDice("RaffleOff");}
        for (uint8 i = 0; i < bottleTokenNums.length;){
            //burn bottle
            if(bottleTokenNums[i] < 839){
                _bottle.burn(bottleTokenNums[i]);
            } else {
                _bottle2.burn(bottleTokenNums[i]);
            }
            unchecked{
                ++i;
            }
        }
        //mint cap(s)
        _cap.mintLCaps(bottleTokenNums.length, msg.sender);
    }

    //COLA nftx
    function bottleCoinPop(uint32 amount,uint256 which) public payable {
        if(!raffleOn){revert NoDice("RaffleOff");}
        if(amount == 0){revert NotEnough("checkBottles");}
        //burn nftx bottle coins
        if(which == 1){
            _tcolx.transferFrom(msg.sender,address(0),amount*1e18);
        } else if (which == 2){
            _tcolx2.transferFrom(msg.sender,address(0),amount*1e18);
        }
        //mintcaps
        _cap.mintLCaps(uint256(amount), msg.sender);
    }

    //milady?
    function checkMIL() public view returns(uint256 mil) {
        return _tmilx.balanceOf(address(this));
    }

    // Assumes the subscription is funded sufficiently.
    function requestRandomWords()
        internal
        returns (uint256 requestId)
    {
        // Will revert if subscription is not set and funded.
        requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit*2,
            2
        );
        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false
        });
        requestIds.push(requestId);
        lastRequestId = requestId;
        emit RequestSent(requestId, 2);
        return requestId;
    }

    //meat and potatos of the raffle here in fulfilment
    //bulk and reg
    //200k gas limit
    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        if(s_requests[_requestId].exists){
            RequestStatus memory request = s_requests[_requestId];
            chance[0] = _randomWords[0]%odds;
            chance[1] = _randomWords[1]%odds;
            request.fulfilled = true;
            emit RequestFulfilled( _requestId, _randomWords);
            emit miladyWinner(solve());
            kingCap = solve();
        } else {
            revert RequestFail("RequestNotFound");
        }
    }

    function sendIt(address recip) internal {
        //milady
        _tmilx.transfer(recip,1010000000000000000);
    }

    //admin functions
    //

    function configureVrf(uint32 newGas, bytes32 newLane) public onlyOwner {
        callbackGasLimit = newGas;
        keyHash = newLane;
    }

    function addCOLA2(address newCOLA) public onlyOwner {
        _tcolx2 = IERC20Uniswap(newCOLA);
    }

    function goSwitch() public onlyOwner {
        raffleOn = !raffleOn;
    }

    function withdrawMIL() public onlyOwner {
        require(
            _tmilx.transfer(msg.sender,_tmilx.balanceOf(address(this))),
            "Unable to transfer"
        );
    }

    function withdraw() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function getRequestStatus(
        uint256 _requestId
    ) external view returns (bool fulfilled, uint256[] memory randomWords) {
        require(s_requests[_requestId].exists, "request not found");
        RequestStatus memory request = s_requests[_requestId];
        return (request.fulfilled, request.randomWords);
    }

    function checkBottleOwner(uint256[] calldata bottleTokenNums) public view {
        for (uint8 i=0; i < bottleTokenNums.length;) {
            if(bottleTokenNums[i] > 838){
                if(_bottle2.ownerOf(bottleTokenNums[i]) != msg.sender){ revert NoDice("NotYourBottles");}
            } else {
                if(_bottle.ownerOf(bottleTokenNums[i]) != msg.sender){ revert NoDice("NotYourBottles");}
            }
            unchecked {
                ++i;
            }
        }
    }

    modifier onlyBottleOwner(uint256[] calldata bottleTokenNums) virtual {
        checkBottleOwner(bottleTokenNums);
        _;
    }

}
