// File: contracts/ExchangePool.sol

pragma solidity ^0.8.0;
pragma abicoder v2;


interface ILockPoolHONO {
    function getHONOSPlitterRate() external view returns (uint256, uint256, uint256, address, address, address);
    function getHONOSPlitterRateAfterXHhonoPoolDepleted() external view returns (uint256, uint256, uint256, address, address, address);
}
interface ILockPoolETH {
    function getETHSplitterRate() external view returns (uint256, uint256, address, address);
    function getETHSPlitterRateAfterXHhonoPoolDepleted() external view returns (uint256, uint256, address, address);
}
interface iHONO
{
    function mint() external payable;
    function burn(uint256 _amount) external;
    function ETHToHONO(uint256 value) external view returns (uint256);
    function HONOToETH(uint256 value) external view returns (uint256);
}

contract ExchangePool is Ownable, Pausable, ReentrancyGuard {        
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    mapping(uint => address) private addressHistory;
    uint private currentIndexForAddressHistory;
    mapping(address => bool) public softban;
    address immutable public DEAD_ADDRESS = 0x000000000000000000000000000000000000dEaD;
    modifier onlyPermissionedAddresses() {
        require(permissionedAddresses[msg.sender] || msg.sender == owner() || msg.sender == distributor, "You cant");
        _;
    }
    mapping(address => bool) public permissionedAddresses;

    IERC20 public xHONO;
    IERC20 public HONO;
    ILockPoolETH public lockPoolETH;
    ILockPoolHONO public lockPoolHONO;
    address public distributor;
    uint256 public exchangeFee = 25;
    uint256 public maxHonoToXHono = 499995000000000000000000;
    uint256 public totalHonoToXHonoReceived = 0;
    uint256 public totalHonoToXHonoConverted = 0;
    // EVENTS
    event Exchange(IERC20 _xHONO, 
    IERC20 _HONO, 
    uint256 _amount, 
    uint256 _outputAmount, 
    address _sender);

    constructor(IERC20 _xHONO, 
    IERC20 _HONO, 
    address _defaultDistributor, uint256 _maxHonoToXHono,  uint256 _totalHonoToXHonoReceived,  uint256 _totalHonoToXHonoConverted) {
        xHONO = _xHONO;
        HONO = _HONO;
        maxHonoToXHono = _maxHonoToXHono;
        totalHonoToXHonoReceived = _totalHonoToXHonoReceived;
        totalHonoToXHonoConverted = _totalHonoToXHonoConverted;

        if(_defaultDistributor!=0x0000000000000000000000000000000000000000)
        {
            distributor = _defaultDistributor;
            permissionedAddresses[_defaultDistributor] = true;
        }
    }

    function updateDistributor(address _distributor) external onlyOwner {
        distributor = _distributor;
    }

    function updateFee(uint256 _exchangeFee) external onlyOwner {
        exchangeFee = _exchangeFee;
    }

    function updateLockPoolETH(ILockPoolETH _lockPool) external onlyOwner {
        lockPoolETH = _lockPool;
    }

    function updateLockPoolHONO(ILockPoolHONO _lockPool) external onlyOwner {
        lockPoolHONO = _lockPool;
    }

    function updatexHONO(IERC20 _xHONO) external onlyOwner {
        xHONO = _xHONO;
    }

    function updateHONO(IERC20 _HONO) external onlyOwner {
        HONO = _HONO;
    }

    function updatetotalHonoToXHonoReceived(uint256 _totalHonoToXHonoReceived) external onlyOwner {
        totalHonoToXHonoReceived = _totalHonoToXHonoReceived;
    }

    function getMaxOutputAmount() public view returns (uint256) {
        if(totalHonoToXHonoConverted >= maxHonoToXHono)
        {
            return 0;
        }

        if(maxHonoToXHono - totalHonoToXHonoConverted < HONO.balanceOf(address(this)))
        {
            return maxHonoToXHono - totalHonoToXHonoConverted;
        }
        
        return HONO.balanceOf(address(this));
    }

    function exchange(uint256 _amount) external payable whenNotPaused nonReentrant {
        require(!isLast5Address(msg.sender) && !softban[msg.sender], "Error: converted recently");
        require(!softban[msg.sender], "Error: soft banned");
        require(xHONO.balanceOf(msg.sender) >= _amount, "token balance too low");
        uint256 maxOutput = getMaxOutputAmount();
        require(_amount <= maxOutput, "Exceed max amount!");
        xHONO.safeTransferFrom(msg.sender, DEAD_ADDRESS, _amount);
        HONO.safeTransfer(msg.sender, _amount);
        uint256 fee = iHONO(address(HONO)).HONOToETH(_amount)*25/1000;
        require(fee <= msg.value, "Not enuf fee");
        sendETH(distributor, msg.value);
        totalHonoToXHonoConverted = totalHonoToXHonoConverted + _amount;
        storeAddress(msg.sender);
        emit Exchange(xHONO, HONO, _amount, _amount, msg.sender);
    }
    
    function storeAddress(address newAddress) internal {
        addressHistory[currentIndexForAddressHistory] = newAddress;
        currentIndexForAddressHistory = (currentIndexForAddressHistory + 1) % 5;
    }

    function isLast5Address(address newAddress) internal view returns (bool) {
        if(addressHistory[0] == newAddress)
            return true;
        if(addressHistory[1] == newAddress)
            return true;
        if(addressHistory[2] == newAddress)
            return true;
        if(addressHistory[3] == newAddress)
            return true;
        if(addressHistory[4] == newAddress)
            return true;
        return false;
    }

    function getAddress(uint index) external view returns (address) {
        require(index < 5, "Index out of bounds");
        return addressHistory[(currentIndexForAddressHistory + 1 + index) % 5];
    }

    // Owner can drain tokens that are sent here by mistake
    function drainToken(IERC20 _token, uint256 _amount, address _to) external onlyOwner {
        _token.safeTransfer(_to, _amount);
    }
    
    receive() external payable {
        if(permissionedAddresses[msg.sender] == true || msg.sender == distributor)
        {
            uint256 preMintBalance = HONO.balanceOf(address(this));
            iHONO(address(HONO)).mint{value: msg.value}();
            uint256 amount = HONO.balanceOf(address(this)) - preMintBalance;
            
            (uint256 daoRatio, uint256 xhonoRatio, address daoAddress, address xhonoPoolAddress) = lockPoolETH.getETHSplitterRate();
            
            if(totalHonoToXHonoReceived >= maxHonoToXHono)
            {
                (daoRatio, xhonoRatio, daoAddress, xhonoPoolAddress) = lockPoolETH.getETHSPlitterRateAfterXHhonoPoolDepleted();
            }

            uint256 daoAmount = amount * daoRatio / (daoRatio + xhonoRatio);
            uint256 xHONOAmount = amount - daoAmount;

            if(totalHonoToXHonoReceived + xHONOAmount > maxHonoToXHono && xhonoPoolAddress == address(this) && totalHonoToXHonoReceived < maxHonoToXHono)
            {
                xHONOAmount = maxHonoToXHono - totalHonoToXHonoReceived;
                daoAmount = amount - xHONOAmount;
                totalHonoToXHonoReceived = maxHonoToXHono;
            }

            if(daoAmount != 0)
            {
                HONO.transfer(daoAddress,daoAmount);
            }

            if(xHONOAmount != 0)
            {
                if(xhonoPoolAddress != address(this))
                {
                    HONO.transfer(xhonoPoolAddress,xHONOAmount);
                }
                else
                {
                    totalHonoToXHonoReceived = totalHonoToXHonoReceived + xHONOAmount;
                }
            }
        }
    }

    function deposit(uint256 amount) external onlyPermissionedAddresses{
        HONO.safeTransferFrom(msg.sender, address(this), amount);
        (uint256 nftTreasuryRatio, uint256 xhonoRatio, uint256 burnRatio, address nftAddress, address xhonoPoolAddress, address receiver3) = lockPoolHONO.getHONOSPlitterRate();
        
        if(totalHonoToXHonoReceived >= maxHonoToXHono)
        {
           (nftTreasuryRatio, xhonoRatio, burnRatio, nftAddress, xhonoPoolAddress, receiver3) = lockPoolHONO.getHONOSPlitterRateAfterXHhonoPoolDepleted();
        }

        uint256 nftTreasuryAmount = amount * nftTreasuryRatio / (nftTreasuryRatio + xhonoRatio + burnRatio);
        uint256 burnAmount = amount * burnRatio / (nftTreasuryRatio + xhonoRatio + burnRatio);
        uint256 xHONOAmount = amount - burnAmount - nftTreasuryAmount;

        if(totalHonoToXHonoReceived + xHONOAmount > maxHonoToXHono 
        && xhonoPoolAddress == address(this)
        && totalHonoToXHonoReceived < maxHonoToXHono)
        {
            uint256 extrasAmount = totalHonoToXHonoReceived + xHONOAmount  - maxHonoToXHono;
            nftTreasuryAmount = nftTreasuryAmount + extrasAmount*nftTreasuryRatio/(nftTreasuryRatio+burnRatio);
            burnAmount = burnAmount + extrasAmount - nftTreasuryAmount;
            xHONOAmount = xHONOAmount - extrasAmount;
            totalHonoToXHonoReceived = maxHonoToXHono;
        }

        if(nftTreasuryAmount != 0)
        {
            IERC20(HONO).transfer(nftAddress,nftTreasuryAmount);
        }
        if(burnAmount != 0)
        {
            if(receiver3 == DEAD_ADDRESS)
            {
                iHONO(address(HONO)).burn(burnAmount);
            }
            else
            {
                IERC20(HONO).transfer(receiver3,burnAmount);
            }
        }
        //the rest keep in the pool
        if(xHONOAmount != 0)
        {
            if(xhonoPoolAddress == address(this))
            {
                totalHonoToXHonoReceived = totalHonoToXHonoReceived + xHONOAmount;
            }
            else
            {
                IERC20(HONO).transfer(xhonoPoolAddress, xHONOAmount);
            }
            
        }
    }

    function sendETH(address reciever, uint256 amount) internal
    {
        (bool success1, ) = reciever.call{value: amount}("");
        require(success1, "Can't send ETH to reciever");
    }

    function banExchangeAddress(address _address, bool allow) external onlyOwner {
        softban[_address] = allow;
    }

    function updateMinterAddress(address _botaddress, bool allow) external onlyOwner {
        permissionedAddresses[_botaddress] = allow;
    }
}
