    function setApprovalForAll(address operator, bool approved) public virtual override {
        require (getLocked(operator, msg.sender) == false || approved == false, "Can not approve locked token");
        super.setApprovalForAll(operator, approved);
    }

    function approve(address to, uint256 tokenId) public payable virtual override {
        if(to != address(0)){
            address holder = ownerOf(tokenId);
            LockStatus status = _tokenLockStatus[tokenId];
            require (status != LockStatus.AllLock, "Can not approve locked token");
            if (status == LockStatus.CalLock){
                uint256 level = _getCALLevel(holder, tokenId);
                require (_getLocked(to, status, level) == false, "Can not approve locked token");
            } else if (status == LockStatus.UnSet){
                require (getLocked(to,holder) == false, "Can not approve locked token");
            }
        }
        super.approve(to, tokenId);
    }

    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 /*quantity*/
    ) internal virtual override {
        // 転送やバーンにおいては、常にstartTokenIdは TokenIDそのものとなります。
        if (from != address(0)) {
            // トークンがロックされている場合、転送を許可しない
            require(getTokenLocked(to, startTokenId) == false , "LOCKED");
        }
    }

    function _afterTokenTransfers(
        address from,
        address /*to*/,
        uint256 startTokenId,
        uint256 /*quantity*/
    ) internal virtual override {
        // 転送やバーンにおいては、常にstartTokenIdは TokenIDそのものとなります。
        if (from != address(0)) {
            // ロックをデフォルトに戻す。（デフォルトは、 contractのLock status）
            delete _tokenLockStatus[startTokenId];
            delete _tokenCALLevel[startTokenId];
        }
    }


    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override
        returns (bool)
    {
        return
            interfaceId == type(IERC721AntiScam).interfaceId ||
            super.supportsInterface(interfaceId);
    }

}
