pragma solidity 0.8.19;

import "./ERC20.sol";
import "./ERC20Burnable.sol";
import "./Ownable.sol";
import "./Mintable.sol";

contract DATEN2 is ERC20, ERC20Burnable, Ownable, Mintable {

    address public marketingAddress;
    uint16 public marketingFee;

    address public developmentAddress;
    uint16 public developmentFee;

    address public businessdevelopmentAddress;
    uint16 public businessdevelopmentFee;

    uint16 public autoBurnFee;

    mapping (address => bool) public isExcludedFromFees;

    uint16 public totalFee;

    event marketingAddressUpdated(address marketingAddress);
    event marketingFeeUpdated(uint16 marketingFee);

    event developmentAddressUpdated(address developmentAddress);
    event developmentFeeUpdated(uint16 developmentFee);

    event businessdevelopmentAddressUpdated(address businessdevelopmentAddress);
    event businessdevelopmentFeeUpdated(uint16 businessdevelopmentFee);

    event autoBurnFeeUpdated(uint16 autoBurnFee);

    event ExcludeFromFees(address indexed account, bool isExcluded);

    constructor()
        ERC20(unicode"DATEN2", unicode"DATEN2") 
        Mintable(990000000)
    {
        address supplyRecipient = 0xc23AA8a9eb4A2594B7345903699063E61dce5eeB;

        marketingAddressSetup(0x875E24557e56651391aa8a63Ea2797dc9a8dD3b7);
        marketingFeeSetup(900);

        developmentAddressSetup(0x9f70e2C81EA08c811e672c8ad66a8d06180b8Fd9);
        developmentFeeSetup(500);

        businessdevelopmentAddressSetup(0xE1dDAd5acF3e49c40576C76Ff52727a3250d3249);
        businessdevelopmentFeeSetup(300);

        autoBurnFeeSetup(0);

        excludeFromFees(supplyRecipient, true);
        excludeFromFees(address(this), true); 

        _mint(supplyRecipient, 330000000 * (10 ** decimals()) / 10);
        _transferOwnership(0xc23AA8a9eb4A2594B7345903699063E61dce5eeB);
    }

    receive() external payable {}

    function decimals() public pure override returns (uint8) {
        return 18;
    }

    function _sendInTokens(address from, address to, uint256 amount) private {
        super._transfer(from, to, amount);
    }

    function marketingAddressSetup(address _newAddress) public onlyOwner {
        require(_newAddress != address(0), "TaxesClassic: Wallet tax recipient cannot be a 0x0 address");
        marketingAddress = _newAddress;

        excludeFromFees(_newAddress, true);

        emit marketingAddressUpdated(_newAddress);
    }

    function marketingFeeSetup(uint16 _newFee) public onlyOwner {
        totalFee = totalFee - marketingFee + _newFee;
        require(totalFee <= 2500, "TaxesClassic: Cannot exceed max total fee of 25%");

        marketingFee = _newFee;

        emit marketingFeeUpdated(_newFee);
    }

    function developmentAddressSetup(address _newAddress) public onlyOwner {
        require(_newAddress != address(0), "TaxesClassic: Wallet tax recipient cannot be a 0x0 address");
        developmentAddress = _newAddress;

        excludeFromFees(_newAddress, true);

        emit developmentAddressUpdated(_newAddress);
    }

    function developmentFeeSetup(uint16 _newFee) public onlyOwner {
        totalFee = totalFee - developmentFee + _newFee;
        require(totalFee <= 2500, "TaxesClassic: Cannot exceed max total fee of 25%");

        developmentFee = _newFee;

        emit developmentFeeUpdated(_newFee);
    }

    function businessdevelopmentAddressSetup(address _newAddress) public onlyOwner {
        require(_newAddress != address(0), "TaxesClassic: Wallet tax recipient cannot be a 0x0 address");
        businessdevelopmentAddress = _newAddress;

        excludeFromFees(_newAddress, true);

        emit businessdevelopmentAddressUpdated(_newAddress);
    }

    function businessdevelopmentFeeSetup(uint16 _newFee) public onlyOwner {
        totalFee = totalFee - businessdevelopmentFee + _newFee;
        require(totalFee <= 2500, "TaxesClassic: Cannot exceed max total fee of 25%");

        businessdevelopmentFee = _newFee;

        emit businessdevelopmentFeeUpdated(_newFee);
    }

    function autoBurnFeeSetup(uint16 _newFee) public onlyOwner {
        totalFee = totalFee - autoBurnFee + _newFee;
        require(totalFee <= 2500, "TaxesClassic: Cannot exceed max total fee of 25%");

        autoBurnFee = _newFee;

        emit autoBurnFeeUpdated(_newFee);
    }

    function excludeFromFees(address account, bool isExcluded) public onlyOwner {
        isExcludedFromFees[account] = isExcluded;

        emit ExcludeFromFees(account, isExcluded);
    }

    function _takeFees(address from, uint256 amount) private returns (uint256) {

        uint256 marketingTokens = amount * marketingFee / 10000;
        if (marketingTokens > 0) _sendInTokens(from, marketingAddress, marketingTokens);

        uint256 developmentTokens = amount * developmentFee / 10000;
        if (developmentTokens > 0) _sendInTokens(from, developmentAddress, developmentTokens);

        uint256 businessdevelopmentTokens = amount * businessdevelopmentFee / 10000;
        if (businessdevelopmentTokens > 0) _sendInTokens(from, businessdevelopmentAddress, businessdevelopmentTokens);

        uint256 autoBurnTokens = amount * autoBurnFee / 10000;
        if (autoBurnTokens > 0) _burn(from, autoBurnTokens);

        return amount - marketingTokens - developmentTokens - businessdevelopmentTokens - autoBurnTokens;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (amount > 0 && !isExcludedFromFees[from] && !isExcludedFromFees[to])
            amount = _takeFees(from, amount);

        super._transfer(from, to, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        override
    {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _afterTokenTransfer(address from, address to, uint256 amount)
        internal
        override
    {
        if (from == address(0)) {
        }

        super._afterTokenTransfer(from, to, amount);
    }
}
