// File src/strategies/strategy-convex-farm-base.sol

pragma solidity ^0.6.7;



abstract contract StrategyConvexFarmBase is StrategyBase {
    address public convexBooster = 0xF403C135812408BFbE8713b5A23a04b3D48AAE31;
    uint256 public poolId;

    address public constant cvx = 0x4e3FBD56CD56c3e72c1403e103b45Db9da5B9D2B;
    address public constant crv = 0xD533a949740bb3306d119CC777fa900bA034cd52;

    constructor(
        address _lp,
        uint256 _poolId,
        address _governance,
        address _strategist,
        address _controller,
        address _timelock
    )
        public
        StrategyBase(_lp, _governance, _strategist, _controller, _timelock)
    {
        poolId = _poolId;
    }

    function getCrvRewardContract() public view returns (address) {
        (, , , address crvRewards, , ) = IConvexBooster(convexBooster).poolInfo(
            poolId
        );
        return crvRewards;
    }

    function balanceOfPool() public view virtual override returns (uint256) {
        return IBaseRewardPool(getCrvRewardContract()).balanceOf(address(this));
    }

    function get_crv_earned() public view virtual returns (uint256) {
        return IBaseRewardPool(getCrvRewardContract()).earned(address(this));
    }

    function get_cvx_earned() public view virtual returns (uint256) {
        uint256 crv_earned = get_crv_earned();

        uint256 supply = IConvexToken(cvx).totalSupply();
        if (supply == 0) {
            return crv_earned;
        }
        uint256 reductionPerCliff = IConvexToken(cvx).reductionPerCliff();
        uint256 totalCliffs = IConvexToken(cvx).totalCliffs();
        uint256 cliff = supply.div(reductionPerCliff);

        uint256 maxSupply = IConvexToken(cvx).maxSupply();

        if (cliff < totalCliffs) {
            uint256 reduction = totalCliffs.sub(cliff);
            uint256 _amount = crv_earned;

            _amount = _amount.mul(reduction).div(totalCliffs);
            //supply cap check
            uint256 amtTillMax = maxSupply.sub(supply);
            if (_amount > amtTillMax) {
                _amount = amtTillMax;
            }
            return _amount;
        }
        return 0;
    }

    function deposit() public virtual override {
        uint256 _want = IERC20(want).balanceOf(address(this));
        if (_want > 0) {
            IERC20(want).safeApprove(convexBooster, 0);
            IERC20(want).safeApprove(convexBooster, _want);

            IConvexBooster(convexBooster).deposit(poolId, _want, true);
        }
    }

    function _withdrawSome(uint256 _amount)
        internal
        virtual
        override
        returns (uint256)
    {
        IBaseRewardPool(getCrvRewardContract()).withdrawAndUnwrap(
            _amount,
            false
        );
        return _amount;
    }
}


// File src/strategies/convex/strategy-convex-cvxfxs-lp.sol

pragma solidity ^0.6.7;

contract StrategyConvexCvxfxsLp is StrategyConvexFarmBase {
    address public constant lpToken =
        0xF3A43307DcAFa93275993862Aae628fCB50dC768;
    uint256 public constant cvxfxsPoolId = 72;
    address public constant pool = 0xd658A338613198204DCa1143Ac3F01A722b5d94A;
    address public constant fxs = 0x3432B6A60D23Ca0dFCa7761B7ab56459D9C964D0;

    uint24 public constant poolFee = 10000;

    constructor(
        address _governance,
        address _strategist,
        address _controller,
        address _timelock
    )
        public
        StrategyConvexFarmBase(
            lpToken,
            cvxfxsPoolId,
            _governance,
            _strategist,
            _controller,
            _timelock
        )
    {
        IERC20(cvx).safeApprove(univ3Router, uint256(-1));
        IERC20(crv).safeApprove(univ3Router, uint256(-1));
        IERC20(fxs).safeApprove(pool, uint256(-1));
    }

    function getName() external pure override returns (string memory) {
        return "StrategyConvexCvxfxsLp";
    }

    function get_cvx_earned()
        public
        view
        override
        returns (uint256 cvxRewards)
    {
        address cvxRewardsContract = IBaseRewardPool(getCrvRewardContract())
            .extraRewards(0);
        cvxRewards = IBaseRewardPool(cvxRewardsContract).earned(address(this));
    }

    function get_fxs_earned()
        public
        view
        returns (uint256 fxsRewards)
    {
        address fxsRewardsContract = IBaseRewardPool(getCrvRewardContract())
            .extraRewards(1);
        fxsRewards = IBaseRewardPool(fxsRewardsContract).earned(address(this));
    }

    function getHarvestable()
        public
        view
        returns (
            uint256,
            uint256,
            uint256
        )
    {
        return (get_crv_earned(), get_cvx_earned(), get_fxs_earned());
    }

    function harvest() public override onlyBenevolent {
        IBaseRewardPool(getCrvRewardContract()).getReward(address(this), true);

        uint256 _cvx = IERC20(cvx).balanceOf(address(this));

        if (_cvx > 0) {
            ISwapRouter(univ3Router).exactInput(
                ISwapRouter.ExactInputParams({
                    path: abi.encodePacked(cvx, poolFee, weth, poolFee, fxs),
                    recipient: address(this),
                    deadline: block.timestamp + 300,
                    amountIn: _cvx,
                    amountOutMinimum: 0
                })
            );
        }
        uint256 _crv = IERC20(crv).balanceOf(address(this));

        if (_crv > 0) {
            ISwapRouter(univ3Router).exactInput(
                ISwapRouter.ExactInputParams({
                    path: abi.encodePacked(crv, poolFee, weth, poolFee, fxs),
                    recipient: address(this),
                    deadline: block.timestamp + 300,
                    amountIn: _crv,
                    amountOutMinimum: 0
                })
            );
        }
        uint256 _fxs = IERC20(fxs).balanceOf(address(this));
        if (_fxs > 0) {
            uint256[2] memory amounts = [_fxs, 0];
            ICurveFi_4(pool).add_liquidity(amounts, 0);
        }

        _distributePerformanceFeesAndDeposit();
    }
}
