    "contracts/BlockStarStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity =0.8.15;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol"; 


contract BlockStarStaking is OwnableUpgradeable, ReentrancyGuardUpgradeable {
    using SafeMath for uint256;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    struct UserInfo {
        uint256 amount;
        uint256 lockupDuration;
        uint256 returnPer;
        uint256 starttime;
        uint256 endtime;
        bool claimed;
    }

    struct PoolInfo {
        uint256 lockupDuration;
        uint256 returnPer;
    }

    IERC20Upgradeable public token;
    bool public started;
    bool private withdrawStarted;
    uint256 public emergencyWithdrawFess;

    mapping(address => UserInfo[]) private _userinfo;
    mapping(uint256 => PoolInfo) public pooldata;
    mapping(address => uint256) public rewardEarned;
    mapping(address => uint256) public userBalance;
    mapping(address => bool) public blocklist;


    uint256 public totalStake;
    uint256 public totalWithdrawal;
    uint256 public totalRewardsDistribution;
    uint256 public daysec;


    function initialize() public initializer {
         __Ownable_init();
        __ReentrancyGuard_init();
        token = IERC20Upgradeable(0x4216f7B5833ED81CEeBCDF39f5ACD1e7F6F9B734);
        started = true;
        withdrawStarted = true;
        daysec = 86400;
        emergencyWithdrawFess = 2500;

        pooldata[30].lockupDuration = 30;
        pooldata[30].returnPer = 100;

        pooldata[90].lockupDuration = 90;
        pooldata[90].returnPer = 300;

        pooldata[180].lockupDuration = 180;
        pooldata[180].returnPer = 700;

        pooldata[365].lockupDuration = 365;
        pooldata[365].returnPer = 1500;
    }

    

    event Deposit(
        address indexed user,
        uint256 indexed lockupDuration,
        uint256 amount,
        uint256 returnPer
    );
    event Withdraw(
        address indexed user,
        uint256 amount,
        uint256 reward,
        uint256 total
    );
    event WithdrawAll(address indexed user, uint256 amount);

    receive() external payable {}

    function addPool(uint256 _lockupDuration, uint256 _returnPer)
        external
        onlyOwner
    {
        PoolInfo storage pool = pooldata[_lockupDuration];
        pool.lockupDuration = _lockupDuration;
        pool.returnPer = _returnPer;
    }

    function addCustomStake(
            address[] memory userAddress, 
            uint256[] memory _amount,
            uint256[] memory _lockupDuration,
            uint256[] memory _returnPer,
            uint256[] memory _starttime,
            uint256[] memory _endtime
        ) public onlyOwner{
            require(userAddress.length == _amount.length , "length mismatch");
            require(userAddress.length == _lockupDuration.length , "length mismatch");
            require(userAddress.length == _returnPer.length , "length mismatch");
            require(userAddress.length == _starttime.length , "length mismatch");
            require(userAddress.length == _endtime.length , "length mismatch");
            for(uint i=0;i<userAddress.length;i++){
                _userinfo[userAddress[i]].push(
                    UserInfo(
                        _amount[i],
                        _lockupDuration[i],
                        _returnPer[i],
                        _starttime[i],
                        _endtime[i],
                        false
                    )
                );

                totalStake += _amount[i];
                userBalance[userAddress[i]] += _amount[i];
            }
    }

    function setEmergencyWithdrawFess(uint256 _fees) public onlyOwner {
        emergencyWithdrawFess = _fees;
    }

    function setToken(IERC20Upgradeable _token) external onlyOwner {
        token = _token;
    }

    function setDaysec(uint256 _sec) public onlyOwner{
        daysec = _sec;
    }

    function toggleStaking(bool _start) external onlyOwner {
        started = _start;
    }

    function toggleWithdraw(bool _start) external onlyOwner {
        withdrawStarted = _start;
    }

    function addUserBlockList(address _address , bool _status) external onlyOwner{
        blocklist[_address] = _status;
    }

    function getUserDeposite(address _userAddress)
        public
        view
        returns (UserInfo[] memory)
    {
        return _userinfo[_userAddress];
    }

    function pendingRewards(address _user) external view returns (uint256) {
        UserInfo[] storage userdata = _userinfo[_user];
        uint256 len = userdata.length;
        uint256 total = 0;
        for (uint256 i = 0; i < len; i++) {
            if (
                block.timestamp > userdata[i].endtime &&
                userdata[i].amount > 0 &&
                !userdata[i].claimed
            ) {
                total += userdata[i].amount.mul(userdata[i].returnPer).mul(userdata[i].lockupDuration).div(10000*365);
            }
        }
        return total;
    }

    function pendingWithdraw(address _user) external view returns (uint256) {
        UserInfo[] storage userdata = _userinfo[_user];
        uint256 len = userdata.length;
        uint256 total = 0;
        for (uint256 i = 0; i < len; i++) {
            if (
                block.timestamp > userdata[i].endtime &&
                userdata[i].amount > 0 &&
                !userdata[i].claimed
            ) {
                total += userdata[i].amount;
            }
        }
        return total;
    }

    function deposit(uint256 _amount, uint256 _lockupDuration) external {
        require(address(token) != address(0), "Token Not Set Yet");
        require(started == true, "Not Stared yet!");
        require(_amount > 0, "Amount must be greater than Zero!");
        PoolInfo storage pool = pooldata[_lockupDuration];

        require(
            pool.lockupDuration > 0 && pool.returnPer > 0,
            "No Pool exist With Locktime !"
        );

        token.safeTransferFrom(address(msg.sender), address(this), _amount);

        _userinfo[msg.sender].push(
            UserInfo(
                _amount,
                _lockupDuration,
                pool.returnPer,
                block.timestamp,
                block.timestamp + (_lockupDuration.mul(daysec)),
                false
            )
        );

        totalStake += _amount;
        userBalance[msg.sender] += _amount;
        emit Deposit(msg.sender, _lockupDuration, _amount, pool.returnPer);
    }

    function withdraw() external nonReentrant {
        require(withdrawStarted, "currently withdraw is not avalible!!");
        require(address(token) != address(0), "Token Not Set Yet");
        require(!blocklist[msg.sender] , "you have not allowed to withdraw, please try again later");
        UserInfo[] storage userdata = _userinfo[msg.sender];
        uint256 len = userdata.length;
        uint256 total_reward = 0;
        uint256 total_amount = 0;
        for (uint256 i = 0; i < len; i++) {
            if (
                block.timestamp > userdata[i].endtime &&
                userdata[i].amount > 0 &&
                !userdata[i].claimed
            ) {
                uint256 amount = userdata[i].amount;
                uint256 reward = amount.mul(userdata[i].returnPer).mul(userdata[i].lockupDuration).div(10000*365);
                userdata[i].claimed = true;
                total_reward += reward;
                total_amount += amount;
                totalWithdrawal += amount;
                userBalance[msg.sender] -= amount;
                totalRewardsDistribution += reward;
            }
        }

        require(token.balanceOf(address(this)) >= total_amount.add(total_reward) , "Currently Withdraw not Avalible");
        require(total_amount.add(total_reward) > 0, "Withdraw amount must be greater than zero");
        token.transfer(address(msg.sender), total_amount.add(total_reward));
        rewardEarned[msg.sender] += total_reward;

        emit Withdraw(msg.sender, total_amount, total_reward, total_amount);
    }

    function getNextRewardTime(address _user) external view returns (uint256) {
        UserInfo[] storage userdata = _userinfo[_user];
        uint256 len = userdata.length;
        uint256 next = 0;

        if (len > 0) {
            next = userdata[0].endtime;
        }

        for (uint256 i = 0; i < len; i++) {
            if (
                userdata[i].starttime < block.timestamp &&
                userdata[i].amount > 0 &&
                userdata[i].endtime >= block.timestamp &&
                userdata[i].endtime < next &&
                !userdata[i].claimed
            ) {
                next = userdata[i].endtime;
            }
        }

        return next;
    }

    function emergencyWithdraw() external nonReentrant {
        require(withdrawStarted, "currently withdraw is not avalible!!");
        require(address(token) != address(0), "Token Not Set Yet");
        require(!blocklist[msg.sender] , "you have not allowed to withdraw, please try again later");
       
        UserInfo[] storage userdata = _userinfo[msg.sender];
        uint256 len = userdata.length;
        uint256 withdraw_total = 0;

        for (uint256 i = 0; i < len; i++) {
            if (userdata[i].amount > 0 && !userdata[i].claimed) {
                uint256 fees = userdata[i].amount.mul(emergencyWithdrawFess).div(10000);
                uint256 amount = userdata[i].amount;
                uint256 total = amount.sub(fees);
                userdata[i].claimed = true;
                userBalance[msg.sender] -= amount;
                withdraw_total += total;
                totalWithdrawal += amount;
            }
        }

        require(
            withdraw_total > 0,
            "Withdraw amount must be greater than zero"
        );
        require(token.balanceOf(address(this)) >= withdraw_total , "Currently Withdraw not Avalible");
        token.transfer(address(msg.sender), withdraw_total);
        emit WithdrawAll(msg.sender, withdraw_total);
    }

    function withdrawBNB(address payable _reciever, uint256 _amount)
        public
        onlyOwner
    {
        _reciever.transfer(_amount);
    }

    function transferAnyERC20Token(
        address tokenAddress,
        uint256 tokens
    ) public onlyOwner {
        IERC20Upgradeable(tokenAddress).transfer(address(msg.sender), tokens);
    }
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
