{{
  "language": "Solidity",
  "sources": {
    "contracts/LalaNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol"; // signatures
import "@openzeppelin/contracts/access/Ownable.sol"; // ownership
import "@openzeppelin/contracts/access/AccessControl.sol"; // roles
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol"; // a standard for royalties that may in the future be widely supported
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Pausable.sol";
import "operator-filter-registry/src/DefaultOperatorFilterer.sol";
import "./lib/LibPart.sol";
import "./lib/LibRoyaltiesV2.sol";
import "./lib/RoyaltiesV2.sol";
import "./lib/ILalaRevenue.sol";

pragma solidity ^0.8.13;

contract LalaNFT is
  DefaultOperatorFilterer,
  ERC721Pausable,
  EIP712,
  Ownable,
  AccessControl,
  IERC2981,
  RoyaltiesV2,
  ILalaRevenue
{
  using ECDSA for bytes32;
  using Strings for uint256;
  using Address for address payable;
  bytes4 private constant _INTERFACE_ID_ERC2981 = 0x2a55205a;
  uint32 private constant _MAX_SHARES = 100000000; // 100% of all the shares available for the drop (combined for all tiers)
  uint16 private constant _PCT_SCALE_FACTOR = 10000; // 0-100*100 = 100%

  string private _URI; // base URI
  bytes private _encyptedURI; // encypted base URI
  string private _contractURI; // Contract-level metadata
  uint256 private _totalShares; // how many holders we have in the shares array
  Share[] private _shares; // array of shares per token
  uint32 public _soldShares; // 0-100*1000000, when 100*1000000 no more tokens can be issued anymore

  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE"); // role responsible for giving out the vouchers
  bytes32 public constant MAINTAINER_ROLE = keccak256("MAINTAINER_ROLE"); // role responsible for updating contract data

  address payable private _merchant; // the address receiving the funds flying into this contract

  uint16 private _royaltiesAmount = 500; // 5% of all sales go to the royalty pool (scaled up to 0/10000) - this is the default
  address payable private _royaltiesReceiver; // the address receiving the funds flying into this contract (if not set, merchant will receive royalties)

  uint16 private _maxNumberOfVouchers = 350; // the hard limit should be arround 400, but just to be safe

  event ContractURIChanged(string contractURI);
  event RoyaltiesReceiverChanged(address newAccount);

  constructor(
    address minter,
    address payable merchant,
    string memory uri, // base URI
    bytes memory encryptedUri, // encrypted base URI
    string memory contractUri, // Contract-level metadata
    string memory name, // name of the pool
    string memory symbolName, // what the ERC721 token will be called
    string memory version, // e.g. "1"
    address payable royaltiesReceiver,
    uint16 royaltiesAmount
  ) ERC721(name, symbolName) EIP712(name, version) {
    _merchant = merchant;
    _URI = uri;
    _encyptedURI = encryptedUri;
    _contractURI = contractUri;
    _totalShares = 0;
    _soldShares = 0;
    _royaltiesReceiver = royaltiesReceiver;
    _royaltiesAmount = royaltiesAmount;

    _setupRole(MINTER_ROLE, minter);
    _setupRole(MAINTAINER_ROLE, minter);
    _setupRole(MAINTAINER_ROLE, _msgSender());
  }

  // represents an un-minted NFT, which has not yet been recorded into the blockchain.
  // a signed voucher can be redeemed for a real NFT using the redeem function.
  struct NFTVoucher {
    Share[] tokens; // must be unique - if another token with this ID already exists, the redeem function will revert
    uint256 price;
    address buyer; // the wallet that this voucher is for
  }

  // represents a bought share of the total revenue stream
  struct Share {
    uint32 share; // from 0-100 %, scaled to 0/_MAX_SHARES
    uint256 tokenId;
  }

  // set merchant address
  function setMerchant(address payable merchant)
    public
    onlyRole(MAINTAINER_ROLE)
  {
    _merchant = merchant;
  }

  // Returns the Uniform Resource Identifier (URI) for `tokenId` token
  function tokenURI(uint256 tokenId)
    public
    view
    virtual
    override
    returns (string memory)
  {
    require(_exists(tokenId), "ERC721: invalid token ID");

    string memory baseURI = _baseURI();
    return
      bytes(baseURI).length > 0
        ? string(abi.encodePacked(baseURI, tokenId.toString(), ".json"))
        : "";
  }

  // Returns the Uniform Resource Identifier (URI) for computing {tokenURI}
  function _baseURI() internal view virtual override returns (string memory) {
    return _URI;
  }

  // Returns a URL for the storefront-level metadata for your contract.
  function contractURI() public view returns (string memory) {
    return _contractURI;
  }

  function setContractURI(string memory contractURI_)
    public
    onlyRole(MAINTAINER_ROLE)
  {
    _contractURI = contractURI_;

    emit ContractURIChanged(contractURI_);
  }

  // set royalties sum
  function setRoyaltiesAmount(uint16 amount) public onlyRole(MAINTAINER_ROLE) {
    require(amount <= _PCT_SCALE_FACTOR, "Royalties cannot be more than 100%");
    _royaltiesAmount = amount;
  }

  // get royalties sum
  function getRoyaltiesAmount() public view returns (uint16) {
    return _royaltiesAmount;
  }

  // set the royalties receiver
  function setRoyaltiesReceiver(address payable royaltiesReceiver)
    public
    onlyRole(MAINTAINER_ROLE)
  {
    _royaltiesReceiver = royaltiesReceiver;

    emit RoyaltiesReceiverChanged(royaltiesReceiver);
  }

  // get the royalties receiver
  function getRoyaltiesReceiver() public view returns (address payable) {
    return _royaltiesReceiver;
  }

  // full ETH balance of the contract
  function getBalance() public view returns (uint256) {
    return address(this).balance;
  }

  // withdraw ETH from the contract
  function withdraw() public onlyRole(MAINTAINER_ROLE) {
    _merchant.transfer(address(this).balance);
  }

  // Redeems an NFTVoucher for an actual NFT, creating it in the process.
  function redeem(
    NFTVoucher calldata voucher, // an NFTVoucher that describes the NFT to be redeemed.
    bytes memory signature // an EIP712 signature of the voucher, produced by the NFT creator.
  ) public payable {
    // make sure signature is valid and get the address of the signer
    address signer = _verify(voucher, signature);

    // make sure that the signer is authorized to mint NFTs
    require(hasRole(MINTER_ROLE, signer), "Signature invalid or unauthorized");

    // make sure that the redeemer is paying enough to cover the buyer's cost
    require(msg.value >= voucher.price, "Insufficient funds to redeem");

    // make sure the voucher was created for sender's wallet
    require(
      msg.sender == voucher.buyer,
      "Voucher is issued for a different wallet"
    );

    require(
      _maxNumberOfVouchers >= voucher.tokens.length,
      "Too many tokens to mint"
    );

    // loop over tokenId
    for (uint256 i = 0; i < voucher.tokens.length; i++) {
      require(
        voucher.tokens[i].share + _soldShares <= _MAX_SHARES,
        "No more shares available"
      );
      // add share to total share sum
      _soldShares = _soldShares + voucher.tokens[i].share;
      //Share memory share = Share(voucher.tokens[i].share, voucher.tokens[i]);
      _shares.push(voucher.tokens[i]);
      _totalShares = _totalShares + 1;

      // transfer the token to the redeemer
      _mint(msg.sender, voucher.tokens[i].tokenId);
    }

    uint256 royaltyAmount = (msg.value * _royaltiesAmount) / _PCT_SCALE_FACTOR;

    if (royaltyAmount > 0 && _royaltiesReceiver != address(0)) {
      _royaltiesReceiver.transfer(royaltyAmount);
    }
  }

  // Verifies the signature for a given NFTVoucher, returning the address of the signer.
  // Will revert if the signature is invalid. Does not verify that the signer is authorized to mint NFTs.
  function _verify(
    NFTVoucher calldata voucher, // an NFTVoucher describing an unminted NFT.
    bytes memory signature // an EIP712 signature of the given voucher.
  ) private view returns (address) {
    bytes32 digest = _hash(voucher);
    return digest.toEthSignedMessageHash().recover(signature);
  }

  // Returns a hash of the given NFTVoucher, prepared using EIP712 typed data hashing rules.
  function _hash(NFTVoucher calldata voucher) public view returns (bytes32) {
    return
      _hashTypedDataV4(
        keccak256(
          abi.encode(
            keccak256(
              "NFTVoucher(Share[] tokens,uint256 price,address buyer)Share(uint32 share,uint256 tokenId)"
            ),
            voucher.tokens,
            voucher.price,
            voucher.buyer
          )
        )
      );
  }

  // -------------------- Revenue distribution -------------------- //

  // Returns the revenue distribution for a given amount of revenue. The returned array contains the addresses of the shareholders and the amount of revenue to be distributed to each shareholder.
  // The amount of revenue to be distributed to each shareholder is calculated by multiplying the total amount of revenue by the share of each shareholder.
  // ammount parameter is in USDC (6 decimals) i.e. 1000000 = 1 USDC
  function getDistribution(uint256 amount)
    external
    view
    returns (DistributionShare[] memory)
  {
    DistributionShare[] memory values = new DistributionShare[](_totalShares);
    for (uint256 i = 0; i < _totalShares; i++) {
      Share memory share = _shares[i];
      // tokens are sold, so we need to call ownerOf
      address shareholder = ownerOf(share.tokenId);
      uint256 shareAmount = (amount * share.share) / _MAX_SHARES;

      values[i] = DistributionShare(shareholder, shareAmount);
    }
    return values;
  }

  // get total shares
  function getTotalShares() public view returns (uint256) {
    return _totalShares;
  }

  // get share by index
  function getShare(uint256 index) public view returns (Share memory) {
    return _shares[index];
  }

  // -------------------- Delayed drop reveal -------------------- //

  // Lets an account with `MAINTAINER_ROLE` role reveal the NFT drop
  function reveal(bytes calldata key)
    external
    onlyRole(MAINTAINER_ROLE)
    returns (string memory)
  {
    require(_encyptedURI.length > 0, "Encrypted URI is not set");

    _URI = string(encryptDecrypt(_encyptedURI, key));

    return _URI;
  }

  // See: https://ethereum.stackexchange.com/questions/69825/decrypt-message-on-chain
  function encryptDecrypt(bytes memory data, bytes calldata key)
    private
    pure
    returns (bytes memory result)
  {
    // Store data length on stack for later use
    uint256 length = data.length;

    assembly {
      // Set result to free memory pointer
      result := mload(0x40)
      // Increase free memory pointer by lenght + 32
      mstore(0x40, add(add(result, length), 32))
      // Set result length
      mstore(result, length)
    }

    // Iterate over the data stepping by 32 bytes
    for (uint256 i = 0; i < length; i += 32) {
      // Generate hash of the key and offset
      bytes32 hash = keccak256(abi.encodePacked(key, i));

      bytes32 chunk;
      assembly {
        // Read 32-bytes data chunk
        chunk := mload(add(data, add(i, 32)))
      }
      // XOR the chunk with hash
      chunk ^= hash;
      assembly {
        // Write 32-byte encrypted chunk
        mstore(add(result, add(i, 32)), chunk)
      }
    }
  }

  // -------------------- Resell royalties -------------------- //

  // necessary override
  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(AccessControl, ERC721, IERC165)
    returns (bool)
  {
    if (interfaceId == LibRoyaltiesV2._INTERFACE_ID_ROYALTIES) {
      return true;
    }
    if (interfaceId == _INTERFACE_ID_ERC2981) {
      return true;
    }
    return super.supportsInterface(interfaceId);
  }

  // Returns token royalties with royalty overrides
  function getRoyalties(uint256) public view returns (LibPart.Part[] memory) {
    LibPart.Part[] memory tokenRoyalties = new LibPart.Part[](1);
    tokenRoyalties[0].value = _royaltiesAmount;

    if (_royaltiesReceiver != address(0)) {
      tokenRoyalties[0].account = payable(_royaltiesReceiver);
    } else {
      tokenRoyalties[0].account = payable(_merchant);
    }

    return tokenRoyalties;
  }

  // really the only function IERC2981 standard defines;
  // only supports one royalty per token
  function royaltyInfo(
    uint256,
    uint256 salePrice // whatever the unit, we just return the percentage of royalty
  ) external view override returns (address receiver, uint256 royaltyAmount) {
    return (
      _royaltiesReceiver,
      (salePrice * _royaltiesAmount) / _PCT_SCALE_FACTOR
    );
  }

  // special function for rarible royalties - the only one their contract needs;
  // supports multiple royalties per token
  function getRaribleV2Royalties(uint256 tokenId)
    external
    view
    override
    returns (LibPart.Part[] memory)
  {
    return getRoyalties(tokenId);
  }

  // -------------------- Pausable token transfers -------------------- //

  // Lets an account with `MAINTAINER_ROLE` pauses all token transfers.
  function pause() public virtual onlyRole(MAINTAINER_ROLE) {
    _pause();
  }

  // Lets an account with `MAINTAINER_ROLE` unpauses all token transfers.
  function unpause() public virtual onlyRole(MAINTAINER_ROLE) {
    _unpause();
  }

  // approval methods
  function setApprovalForAll(address operator, bool approved)
    public
    override
    onlyAllowedOperatorApproval(operator)
  {
    super.setApprovalForAll(operator, approved);
  }

  function approve(address operator, uint256 tokenId)
    public
    override
    onlyAllowedOperatorApproval(operator)
  {
    super.approve(operator, tokenId);
  }

  function transferFrom(
    address from,
    address to,
    uint256 tokenId
  ) public override onlyAllowedOperator(from) {
    super.transferFrom(from, to, tokenId);
  }

  function safeTransferFrom(
    address from,
    address to,
    uint256 tokenId
  ) public override onlyAllowedOperator(from) {
    super.safeTransferFrom(from, to, tokenId);
  }

  function safeTransferFrom(
    address from,
    address to,
    uint256 tokenId,
    bytes memory data
  ) public override onlyAllowedOperator(from) {
    super.safeTransferFrom(from, to, tokenId, data);
  }
}
"
    },
