// Giove-Withdraw-Main-Contract

pragma solidity ^0.8.0;

contract Giove_Withdraw is Pausable, Ownable {
    address public operator;

    struct user_data {
        address user;
        uint256 amt;
        uint256 claim_time;
    }

    mapping(address => user_data) public user_details;

    event WithdrawEvent(
        address indexed user,
        address source,
        uint256 amount,
        uint16 time
    );

    event DepositEvent(IERC20 source, uint256 amount, uint16 time);

    modifier onlyOperator() {
        require(_msgSender() == operator, "only Operator");
        _;
    }

    constructor(address _owner, address _operator) Ownable(_owner) {
        operator = _operator;
    }

    receive() external payable {}

    function addToken(IERC20 _token, uint256 _amount) external onlyOwner {
        tokenSafeTransferFrom(_token, _msgSender(), address(this), _amount);
        emit DepositEvent(_token, _amount, uint16(block.timestamp));
    }

    function setOperator (address _operator) external onlyOwner {
        operator = _operator;
    }

    function emergencyWithdraw(
        address _source,
        address _user,
        uint256 _amount
    ) external onlyOwner {
        if (_source == address(0)) payable(_user).transfer(_amount);
        else tokenSafeTransfer(IERC20(_source), _user, _amount);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function claim(
        address _source,
        address _user,
        uint256 _amt
    ) external whenNotPaused onlyOperator {
        user_data storage store_data = user_details[_msgSender()];
        require(_amt > 0 && _user != address(0), "claim: Invalid params");

        if (_source == address(0)) payable(_user).transfer(_amt);
        else tokenSafeTransfer(IERC20(_source), _user, _amt);

        store_data.user = _msgSender();
        store_data.amt = _amt;
        store_data.claim_time = block.timestamp;

        emit WithdrawEvent(_user, _source, _amt, uint16(block.timestamp));
    }

    function tokenSafeTransferFrom(
        IERC20 token,
        address from,
        address to,
        uint256 amount
    ) internal {
        bool success;

        assembly {
            // Get a pointer to some free memory.
            let freeMemoryPointer := mload(0x40)

            // Write the abi-encoded calldata into memory, beginning with the function selector.
