    "contracts/PackMinter.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import { IERC721 } from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { PackNFT } from "./PackNFT.sol";

/**
 * >>> Join the Resistance <<<
 * >>>   https://nfa.gg/   <<<
 * @title   NonFungibleArcade Pack Minter
 * @notice  This contract allows users to purchase packs of items and passes.
 * @author  BowTiedPickle
 */
contract PackMinter is Ownable {
    using SafeERC20 for IERC20;

    /**
     * @param   price           The price of the pack
     * @param   maxAmount       The maximum number of packs that can be minted
     * @param   mintedAmount    The number of packs that have been minted
     * @param   maxPerUser      The maximum number of packs a user can purchase if pass gating is not active
     * @param   startTime       The start time of the pack in Unix epoch seconds
     * @param   endTime         The end time of the pack in Unix epoch seconds
     * @param   passAmount      The number of passes to mint per pack
     * @param   usePassGating   Whether or not to use held pass token-gating
     */
    struct Pack {
        uint48 price;
        uint16 maxAmount;
        uint16 mintedAmount;
        uint16 maxPerUser;
        uint48 startTime;
        uint48 endTime;
        uint8 passAmount;
        uint8 itemAmount;
        bool usePassGating;
    }

    /// @notice All packs that can be purchased
    mapping(uint256 => Pack) public packs;

    /**
     * @param   passesPurchased     The number of passes purchased by the user
     * @param   chargesUsed         The number of charges used against token-gated passes purchased by the user
     * @param   packsPurchased      The number of each pack purchased by the user
     */
    struct UserInfo {
        uint256 passesPurchased;
        uint256 chargesUsed;
        mapping(uint256 => uint256) packsPurchased;
    }

    /// @notice Packs purchased and passes purchased by each user
    mapping(address => UserInfo) public users;

    /// @notice Number of gated packs a pass ID has claimed
    mapping(uint256 => uint256) public passChargesUsed;

    /// @notice USDC token address
    IERC20 public immutable USDC;
    /// @notice ArcadePass contract address
    IERC721 public immutable pass;
    /// @notice Pack NFT address
    PackNFT public immutable packNFT;

    /// @notice The number of passes to sell
    uint256 public passesToSell;

    /// @notice The number of passes that have been sold
    uint256 public passesSold;

    /// @notice The number of item packs to sell
    uint256 public itemPacksToSell;

    /// @notice The number of item packs that have been sold
    uint256 public itemPacksSold;

    /// @notice The number of packs that can be configured
    uint256 public configuredPacks;

    /// @notice The maximum number of passes a user can purchase across all pack types
    uint256 public maxPassesPerUser;

    /// @notice Charges per pass when pass gating is active in a Pack
    uint256 public chargesPerPass = 3;

    /**
     * @notice  Creates a new PackMinter contract.
     * @param   _owner              The owner of the contract
     * @param   _USDC               The USDC token address
     * @param   _pass               The ArcadePass contract address
     * @param   _pack               The Pack NFT address
     * @param   _passesToSell       The number of passes to sell
     * @param   _itemPacksToSell    The number of item packs to sell
     * @param   _configuredPacks    The number of packs that can be configured
     * @param   _maxPassesPerUser   The maximum number of passes a user can purchase across all pack types
     */
    constructor(
        address _owner,
        address _USDC,
        address _pass,
        address _pack,
        uint256 _passesToSell,
        uint256 _itemPacksToSell,
        uint256 _configuredPacks,
        uint256 _maxPassesPerUser
    ) {
        if (_owner == address(0) || _USDC == address(0) || _pass == address(0) || _pack == address(0)) revert PackMinter__ZeroAddress();
        USDC = IERC20(_USDC);
        pass = IERC721(_pass);
        packNFT = PackNFT(_pack);

        passesToSell = _passesToSell;
        itemPacksToSell = _itemPacksToSell;
        configuredPacks = _configuredPacks;
        maxPassesPerUser = _maxPassesPerUser;

        _transferOwnership(_owner);
    }

    // ---------- User ----------

    /**
     * @param   packId     The pack id to mint
     * @param   amount     The number of this pack to mint
     * @param   passIds    The pass ids to use for pass-gated packs. Ignored for non-pass-gated packs.
     */
    struct MintInput {
        uint256 packId;
        uint16 amount;
        uint256[] passIds;
    }

    /**
     * @notice  Allows a user to purchase packs.
     * @param   _inputs     Array of MintInput structs
     */
    function mint(MintInput[] calldata _inputs) external {
        uint256 totalCost;
        uint256 totalPasses;
        uint256 totalItems;
        uint256 chargesRequired;
        uint256 totalPacks;

        UserInfo storage user = users[msg.sender];
        for (uint256 i; i < _inputs.length; ) {
            if (_inputs[i].packId >= configuredPacks) revert PackMinter__InvalidPackId(_inputs[i].packId);
            if (_inputs[i].amount == 0) {
                unchecked {
                    ++i;
                }
                continue;
            }

            if (block.timestamp < packs[_inputs[i].packId].startTime || block.timestamp >= packs[_inputs[i].packId].endTime)
                revert PackMinter__InvalidTiming();

            if (packs[_inputs[i].packId].usePassGating) {
                chargesRequired += _inputs[i].amount;
                for (uint256 j; j < _inputs[i].passIds.length; ) {
                    if (pass.ownerOf(_inputs[i].passIds[j]) != msg.sender) revert PackMinter__CallerIsNotPassOwner();

                    uint256 chargesRemaining = chargesPerPass - passChargesUsed[_inputs[i].passIds[j]];
                    if (chargesRemaining >= chargesRequired) {
                        passChargesUsed[_inputs[i].passIds[j]] += chargesRequired;
                        chargesRequired = 0;
                        break;
                    } else {
                        passChargesUsed[_inputs[i].passIds[j]] += chargesRemaining;
                        chargesRequired -= chargesRemaining;
                    }
                    unchecked {
                        ++j;
                    }
                }
            } else {
                if (user.packsPurchased[_inputs[i].packId] + _inputs[i].amount > packs[_inputs[i].packId].maxPerUser)
                    revert PackMinter__MaxPacksPerUserExceeded();
            }

            if (packs[_inputs[i].packId].mintedAmount + _inputs[i].amount > packs[_inputs[i].packId].maxAmount)
                revert PackMinter__MaxAmountPerPackExceeded();

            packs[_inputs[i].packId].mintedAmount += _inputs[i].amount;
            user.packsPurchased[_inputs[i].packId] += _inputs[i].amount;

            totalCost += packs[_inputs[i].packId].price * _inputs[i].amount;
            totalPasses += packs[_inputs[i].packId].passAmount * _inputs[i].amount;
            totalItems += packs[_inputs[i].packId].itemAmount * _inputs[i].amount;
            totalPacks += _inputs[i].amount;
            emit PackPurchased(_inputs[i].packId, msg.sender, _inputs[i].amount);

            unchecked {
                ++i;
            }
        }

        if (totalCost == 0) revert PackMinter__NoPacksSelected();

        if (totalPasses > 0) {
            if (user.passesPurchased + totalPasses > maxPassesPerUser) revert PackMinter__MaxPassesPerUserExceeded();
            if (passesSold + totalPasses > passesToSell) revert PackMinter__PassesToSellExceeded();

            user.passesPurchased += totalPasses;
            passesSold += totalPasses;
        }

        if (totalItems > 0) {
            if (itemPacksSold + totalItems > itemPacksToSell) revert PackMinter__ItemPacksToSellExceeded();
            itemPacksSold += totalItems;
        }

        if (chargesRequired > chargesPerPass * user.passesPurchased - user.chargesUsed) revert PackMinter__PassChargesInsufficient();
        if (chargesRequired > 0) user.chargesUsed += chargesRequired;

        packNFT.mint(msg.sender, totalPacks);

        USDC.safeTransferFrom(msg.sender, address(this), totalCost);
    }

    // ---------- Admin ----------

    /**
     * @notice  Sets the number of packs that can be configured.
     * @param   _configuredPacks    The number of packs that can be configured
     */
    function setConfiguredPacks(uint256 _configuredPacks) external onlyOwner {
        emit NewConfiguredPacks(configuredPacks, _configuredPacks);
        configuredPacks = _configuredPacks;
    }

    /**
     * @param   packId          The pack id to set
     * @param   price           The price of the pack
     * @param   maxAmount       The maximum number of packs that can be minted
     * @param   maxPerUser      The maximum number of packs a user can purchase if pass gating is not active
     * @param   startTime       The start time of the pack in Unix epoch seconds
     * @param   endTime         The end time of the pack in Unix epoch seconds
     * @param   passAmount      The number of passes to mint per pack
     * @param   usePassGating   Whether or not to use held pass token-gating
     */
    struct PackConfig {
        uint48 packId;
        uint48 price;
        uint16 maxAmount;
        uint16 maxPerUser;
        uint48 startTime;
        uint48 endTime;
        uint8 passAmount;
        uint8 itemAmount;
        bool usePassGating;
    }

    /**
     * @notice  Sets the configuration for a pack.
     * @param   _pack   The pack data to set
     */
    function setupPack(PackConfig calldata _pack) external onlyOwner {
        _setupPack(_pack);
    }

    /**
     * @notice  Sets the configuration for multiple packs.
     * @param   _packs      The array of pack data to set
     */
    function setupPacks(PackConfig[] calldata _packs) external onlyOwner {
        for (uint256 i; i < _packs.length; ) {
            _setupPack(_packs[i]);
            unchecked {
                ++i;
            }
        }
    }

    function _setupPack(PackConfig calldata _pack) internal {
        if (_pack.packId >= configuredPacks) revert PackMinter__InvalidPackId(_pack.packId);
        if (_pack.startTime >= _pack.endTime) revert PackMinter__InvalidTiming();
        Pack storage pack = packs[_pack.packId];
        pack.price = _pack.price;
        pack.maxAmount = _pack.maxAmount;
        pack.maxPerUser = _pack.maxPerUser;
        pack.startTime = _pack.startTime;
        pack.endTime = _pack.endTime;
        pack.passAmount = _pack.passAmount;
        pack.itemAmount = _pack.itemAmount;
        pack.usePassGating = _pack.usePassGating;
    }

    /**
     * @notice  Sets the maximum number of passes a user can purchase.
     * @param   _maxPassesPerUser   The maximum number of passes a user can purchase
     */
    function setMaxPassesPerUser(uint256 _maxPassesPerUser) external onlyOwner {
        emit NewMaxPassesPerUser(maxPassesPerUser, _maxPassesPerUser);
        maxPassesPerUser = _maxPassesPerUser;
    }

    /**
     * @notice  Sets the number of charges per pass when pass gating is active in a Pack.
     * @param   _chargesPerPass     The number of charges per pass when pass gating is active in a Pack
     */
    function setChargesPerPass(uint256 _chargesPerPass) external onlyOwner {
        emit NewChargesPerPass(chargesPerPass, _chargesPerPass);
        chargesPerPass = _chargesPerPass;
    }

    /**
     * @notice  Sets the number of passes to sell
     * @param   _passesToSell     The number of passes to sell
     */
    function setPassesToSell(uint256 _passesToSell) external onlyOwner {
        emit NewPassesToSell(passesToSell, _passesToSell);
        passesToSell = _passesToSell;
    }

    /**
     * @notice  Sets the number of item packs to sell
     * @param   _itemPacksToSell     The number of item packs to sell
     */
    function setItemPacksToSell(uint256 _itemPacksToSell) external onlyOwner {
        emit NewItemPacksToSell(itemPacksToSell, _itemPacksToSell);
        itemPacksToSell = _itemPacksToSell;
    }

    /**
     * @notice  Withdraws all USDC from the contract.
     */
    function withdraw() external onlyOwner {
        USDC.safeTransfer(msg.sender, USDC.balanceOf(address(this)));
    }

    // ---------- View ----------

    /**
     * @notice  Returns the number of passes a user has purchased.
     * @param   _user   The user to check
     * @return  The number of passes the user has purchased
     */
    function getPassesPurchased(address _user) external view returns (uint256) {
        return users[_user].passesPurchased;
    }

    /**
     * @notice  Returns the number of charges a user has used against token-gated passes.
     * @param   _user   The user to check
     * @return  The number of charges the user has used against token-gated passes
     */
    function getChargesUsed(address _user) external view returns (uint256) {
        return users[_user].chargesUsed;
    }

    /**
     * @notice  Returns the number of packs a user has purchased.
     * @param   _user   The user to check
     * @return  An array with the number of packs the user has purchased at each pack id
     */
    function getPacksPurchased(address _user) external view returns (uint256[] memory) {
        UserInfo storage user = users[_user];
        uint256[] memory packsPurchased = new uint256[](configuredPacks);
        for (uint256 i; i < configuredPacks; ) {
            packsPurchased[i] = user.packsPurchased[i];
            unchecked {
                ++i;
            }
        }
        return packsPurchased;
    }

    // ---------- Events ----------

    event PackPurchased(uint256 indexed packId, address indexed buyer, uint256 amount);

    event NewMaxPassesPerUser(uint256 oldMaxPassesPerUser, uint256 newMaxPassesPerUser);
    event NewPassesToSell(uint256 oldPassesToSell, uint256 newPassesToSell);
    event NewItemPacksToSell(uint256 oldItemPacksToSell, uint256 newItemPacksToSell);
    event NewConfiguredPacks(uint256 oldConfiguredPacks, uint256 newConfiguredPacks);
    event NewChargesPerPass(uint256 oldChargesPerPass, uint256 newChargesPerPass);

    // ---------- Errors ----------

    error PackMinter__InvalidPackId(uint256 packId);
    error PackMinter__InvalidTiming();
    error PackMinter__NoPacksSelected();
    error PackMinter__ZeroAddress();
    error PackMinter__PassesToSellExceeded();
    error PackMinter__ItemPacksToSellExceeded();
    error PackMinter__MaxAmountPerPackExceeded();
    error PackMinter__MaxPassesPerUserExceeded();
    error PackMinter__MaxPacksPerUserExceeded();
    error PackMinter__PassChargesInsufficient();
    error PackMinter__CallerIsNotPassOwner();
}
"
    },
    "contracts/PackNFT.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import { ERC721A } from "erc721a/contracts/ERC721A.sol";
import { IERC721A } from "erc721a/contracts/IERC721A.sol";
import { ERC721ABurnable } from "erc721a/contracts/extensions/ERC721ABurnable.sol";
import { ERC721AQueryable } from "erc721a/contracts/extensions/ERC721AQueryable.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * >>> Join the Resistance <<<
 * >>>   https://nfa.gg/   <<<
 * @title   NonFungibleArcade Pack
 * @notice  NFTs representing packs of items and passes
 * @author  BowTiedPickle
 */
contract PackNFT is ERC721A, ERC721ABurnable, ERC721AQueryable, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    /// @notice  Base URI for token URIs
    string public baseURI;

    /**
     * @notice  Construct a new PackNFT contract
     * @param   _owner      Owner of the contract
     * @param   baseURI_    Base URI for token URIs
     */
    constructor(address _owner, string memory baseURI_) ERC721A("NFArcade Pack", "NFA-PACK") {
        if (_owner == address(0)) revert Pack__ZeroAddress();

        baseURI = baseURI_;

        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
    }

    // ---------- Minter ----------

    /**
     * @notice  Mint a new token
     * @param   _to         Address to mint to
     * @param   _amount     Amount to mint
     */
    function mint(address _to, uint256 _amount) external onlyRole(MINTER_ROLE) {
        _mint(_to, _amount);
    }

    // ---------- Admin ----------

    /**
     * @notice  Set a new base URI
     * @param   _newURI     New URI string
     */
    function setBaseURI(string memory _newURI) external onlyRole(DEFAULT_ADMIN_ROLE) {
        emit NewURI(baseURI, _newURI);
        baseURI = _newURI;
    }

    // ---------- Overrides ----------

    function supportsInterface(bytes4 interfaceId) public view virtual override(IERC721A, ERC721A, AccessControl) returns (bool) {
        return ERC721A.supportsInterface(interfaceId) || AccessControl.supportsInterface(interfaceId);
    }

    function _startTokenId() internal view virtual override returns (uint256) {
        return 1;
    }

    function tokenURI(uint256 tokenId) public view virtual override(IERC721A, ERC721A) returns (string memory) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();

        string memory baseURI_ = _baseURI();
        return bytes(baseURI_).length != 0 ? string(abi.encodePacked(baseURI_, _toString(tokenId), ".json")) : "";
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    // ---------- Events ----------

    event NewURI(string _oldURI, string _newURI);

    // ---------- Errors ----------

    error Pack__ZeroAddress();
}
"
    },
    "erc721a/contracts/ERC721A.sol": {
      "content": "// SPDX-License-Identifier: MIT
// ERC721A Contracts v4.2.3
// Creator: Chiru Labs

pragma solidity ^0.8.4;

import './IERC721A.sol';

/**
 * @dev Interface of ERC721 token receiver.
 */
interface ERC721A__IERC721Receiver {
    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4);
}

/**
 * @title ERC721A
 *
 * @dev Implementation of the [ERC721](https://eips.ethereum.org/EIPS/eip-721)
 * Non-Fungible Token Standard, including the Metadata extension.
 * Optimized for lower gas during batch mints.
 *
 * Token IDs are minted in sequential order (e.g. 0, 1, 2, 3, ...)
 * starting from `_startTokenId()`.
 *
 * Assumptions:
 *
 * - An owner cannot have more than 2**64 - 1 (max value of uint64) of supply.
 * - The maximum token ID cannot exceed 2**256 - 1 (max value of uint256).
 */
contract ERC721A is IERC721A {
    // Bypass for a `--via-ir` bug (https://github.com/chiru-labs/ERC721A/pull/364).
    struct TokenApprovalRef {
        address value;
    }

    // =============================================================
    //                           CONSTANTS
    // =============================================================

    // Mask of an entry in packed address data.
    uint256 private constant _BITMASK_ADDRESS_DATA_ENTRY = (1 << 64) - 1;

    // The bit position of `numberMinted` in packed address data.
    uint256 private constant _BITPOS_NUMBER_MINTED = 64;

    // The bit position of `numberBurned` in packed address data.
    uint256 private constant _BITPOS_NUMBER_BURNED = 128;

    // The bit position of `aux` in packed address data.
    uint256 private constant _BITPOS_AUX = 192;

    // Mask of all 256 bits in packed address data except the 64 bits for `aux`.
    uint256 private constant _BITMASK_AUX_COMPLEMENT = (1 << 192) - 1;

    // The bit position of `startTimestamp` in packed ownership.
    uint256 private constant _BITPOS_START_TIMESTAMP = 160;

    // The bit mask of the `burned` bit in packed ownership.
    uint256 private constant _BITMASK_BURNED = 1 << 224;

    // The bit position of the `nextInitialized` bit in packed ownership.
    uint256 private constant _BITPOS_NEXT_INITIALIZED = 225;

    // The bit mask of the `nextInitialized` bit in packed ownership.
    uint256 private constant _BITMASK_NEXT_INITIALIZED = 1 << 225;

    // The bit position of `extraData` in packed ownership.
