    "contracts/PlutoAvatarDelegator.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.3;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OperatorFiltererUpgradeable} from "operator-filter-registry/src/upgradeable/OperatorFiltererUpgradeable.sol";
import {whitelistWizard} from "./utils/whitelistWizard.sol";
import {NonblockingLzApp,Ownable2StepUpgradeable} from "./utils/NonblockingLzApp.sol";
import {IDelegationRegistry} from "./Interface/IDelegationRegistry.sol";
import {IERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

/// @title PlutoAvatarDelegator.sol
/// @author Untitled Labs (https://untitledlabs.io)
contract PlutoAvatarDelegator is
Initializable,
Ownable2StepUpgradeable,
whitelistWizard,
NonblockingLzApp
{
    // contract storage - minting
    uint16 public destinationChainId; // chainId of the destination chain
    uint256 public gas; // gas used for transferring the NFT cross-chain
    uint256 public increaseGasPerToken; // increase in gas per token
    uint256 public maxTotalSupply; // max supply of the NFT
    address public NFTAddress; // address of the NFT contract in the destination chain
    address public nativeAddress;
    
    mapping(bytes => bool) public usedSignatures; // mapping of signature to bool for minting
    mapping(address => uint256) public tokensMinted; // mapping of address to number of tokens minted
    mapping(address => bool) public isTokenAllowed; // mapping of address to bool for whitelisted tokens
    mapping(address => uint256) public mintPrice; // mapping of address to mint price
    
    /**
       * @notice A struct that defines a Sale
       * @params startTime Time when the Sale begins
       * @params endTime Time when the Sale ends
       * @params register Registry of minters
    */
    struct mintInfo {
        uint256 startTime;
        uint256 endTime;
        mapping(address => uint256) register;
    }
    
    mintInfo public MBC;
    
    mintInfo public Investors;
    
    mintInfo public Guaranteed;
    
    mintInfo public FCFS;
    
    mintInfo public Public;
    
    IDelegationRegistry public dc;
    
    // events
    event ChangeMintPrice(uint256 price);
    event TokenWhitelisted(address tokenAddress);
    // errors
    error Unauthorized(); // if the call is not from the authorised address
    error Underpriced(); // if the value attached is less than the minimum required value
    error Overpriced(); // if the value attached is more than the maximum required value
    error InvalidParams(); // if the function params are invalid
    error MaxSupplyReached(); // if the max supply is reached
    
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }
    
    /// @notice Initializes the pass contract
    function initialize(
        address endpointAddress,
        address _designatedSigner,
        address _dc,
        string memory _domain,
        string memory _version
    ) external initializer {
        __Wizard_init(_domain, _version, _designatedSigner);
        __Ownable2Step_init();
        __nonblockingLzApp_init(endpointAddress);
        dc = IDelegationRegistry(_dc);
        nativeAddress = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
        gas = 300000;
        increaseGasPerToken = 40000;
        maxTotalSupply = 10000;
        isTokenAllowed[nativeAddress] = true;
        
        MBC.startTime = 1682431200;
        MBC.endTime = MBC.startTime + 90 minutes;
        
        Investors.startTime = 1682431200;
        Investors.endTime = Investors.startTime + 90 minutes;
        
        Guaranteed.startTime = 1682431200;
        Guaranteed.endTime = Guaranteed.startTime + 90 minutes;
        
        FCFS.startTime = Guaranteed.endTime;
        FCFS.endTime = FCFS.startTime + 100 days;
        
        Public.startTime = Guaranteed.endTime;
        Public.endTime = FCFS.startTime + 100 days;
    }
    
    // Owner Functions
    
    /// @notice This function is used to set the conditions mintInfo
    /// @param _startTime Start time of the mintInfo
    /// @param _endTime End time of the mintInfo
    function setConditions(
        uint8 phase,
        uint256 _startTime,
        uint256 _endTime
    ) external onlyOwner {
        require(_startTime < _endTime, "Invalid times");
        unchecked{
            if (phase == 2) {
                MBC.startTime = _startTime;
                MBC.endTime = _endTime;
            } else if (phase == 1) {
                Investors.startTime = _startTime;
                Investors.endTime = _endTime;
            } else if (phase == 3) {
                Guaranteed.startTime = _startTime;
                Guaranteed.endTime = _endTime;
            } else if (phase == 4) {
                FCFS.startTime = _startTime;
                FCFS.endTime = _endTime;
            }
            else {
                Public.startTime = _startTime;
                Public.endTime = _endTime;
            }}
    }
    
    /// @notice Function to withdraw all Ether from this contract.
    /// @param to Address to which the Ether needs to be withdrawn
    function withdraw(address payable to, address payableToken) public onlyOwner {
        // get the amount of Ether stored in this contract
        uint256 amount = address(this).balance;
        
        // send all Ether to owner
        // Owner can receive Ether since the address of owner is payable
        if (payableToken == nativeAddress) {
            (bool success,) = to.call{value: amount}("");
            require(success, "Failed to send Ether");
        } else {
            IERC20Upgradeable(payableToken).transfer(to, IERC20Upgradeable(payableToken).balanceOf(address(this)));
        }
        //transfer collected matic to owner
        
    }
    
    /// @notice Function to whitelist the tokens which will be used for minting
    /// @param _tokens Array of whitelisted address of the ERC20 token
    function whitelistTokens(address[] memory _tokens) external onlyOwner {
        for (uint256 i = 0; i < _tokens.length; i++) {
            isTokenAllowed[_tokens[i]] = true;
            emit TokenWhitelisted(_tokens[i]);
        }
    }
    
    /// @notice Change the default mint price
    /// @dev Send nativeAddress in-case of setting ETH price
    /// @param price The new price
    function changeMintPrice(address tokenAddress,uint256 price) external onlyOwner {
       mintPrice[tokenAddress] = price; 
       emit ChangeMintPrice(price);
    }
    
    /// @notice Sets the max total supply
    /// @param _maxTotalSupply The new max total supply
    function setMaxTotalSupply(uint256 _maxTotalSupply) external onlyOwner {
        maxTotalSupply = _maxTotalSupply;
    }
    
    /// @notice Function to change gas and increaseGasPerToken
    function changeGas(uint256 _gas, uint256 _changeGasPerToken) external onlyOwner {
        gas = _gas;
        increaseGasPerToken = _changeGasPerToken;
    }
    
    /// @notice Function to set the chain id of the destination chain
    /// @param _destinationChainId Chain id of the destination chain
    function setDestinationChainId(uint16 _destinationChainId) external onlyOwner {
        destinationChainId = _destinationChainId;
    }
    
    /// @notice Function to set the address of the NFT contract in the destination chain
    /// @param _NFTAddress Address of the NFT contract in the destination chain
    function setNFTAddress(address _NFTAddress) external onlyOwner {
        NFTAddress = _NFTAddress;
    }
    
    /// @notice Function to set the address of the delegation registry
    /// @param _dc Address of the delegation registry
    function setDCAddress(address _dc) external onlyOwner {
        dc = IDelegationRegistry(_dc);
    }
    
    // LAYERZERO FUNCTIONS
    /// @notice Function to mint tokens on the destination chain
    /// @param signature Signature of the message
    /// @param payableTokenAddress Address of the ERC20 token
    /// @param mintAmount Amount to tokens to mint
    /// @param mintTo Address to mint the tokens to
    /// @param _vault Address of the delegateCash vault
    function payAndMintTokens(
        whitelist calldata signature,
        address payableTokenAddress,
        uint256 mintAmount,
        bytes memory mintTo,
        bytes memory _vault
    ) external payable {
        require (maxTotalSupply >= mintAmount, "Error: Max Total Supply reached");
        maxTotalSupply -= mintAmount;
        validateSignature(signature, msg.sender);
        bytes memory requester = mintTo;
        if (bytesToAddress(_vault) != address(0)) {
            bool isDelegateValid = dc.checkDelegateForContract(bytesToAddress(mintTo), bytesToAddress(_vault), NFTAddress);
            require(isDelegateValid, "invalid delegate-vault pairing");
            requester = _vault;
        }
        
        /// @dev Check if user is allowed to mint
        uint8 phase = validateMaxMintPerUser(signature, bytesToAddress(requester), mintAmount);
        
        uint16 version = 1;
        bytes memory adapterParameters = abi.encodePacked(version, gas + increaseGasPerToken * mintAmount);
        bytes memory payload = abi.encode(requester, mintAmount, phase);
        
        (uint256 messageFees,) = lzEndpoint.estimateFees(destinationChainId, address(this), payload, false, adapterParameters);
        uint256 mintingFee;
        if (payableTokenAddress == nativeAddress) {
            mintingFee = mintPrice[nativeAddress] * mintAmount;
            require (msg.value >= messageFees + mintingFee, "Error: Insufficient Fees");
            _lzSend(
                destinationChainId,
                payload,
                payable(msg.sender),
                address(0),
                adapterParameters,
                msg.value - mintingFee
            );
        }
        else {
            require (msg.value >= messageFees, "Error: Insufficient Fees");
            require(isTokenAllowed[payableTokenAddress], "Error: Token not whitelisted");
            mintingFee = mintPrice[payableTokenAddress] * mintAmount;
            IERC20Upgradeable(payableTokenAddress).transferFrom(
                msg.sender,
                address(this),
                mintingFee
            );
            _lzSend(
                destinationChainId,
                payload,
                payable(msg.sender),
                address(0),
                adapterParameters,
                msg.value
            );
        }
    }
    
    function validateMaxMintPerUser(
        whitelist calldata signature,
        address minter,
        uint256 mintAmount
    ) internal returns(uint8){
      unchecked {
            if (signature.PhaseType == 2) {
                require(MBC.startTime <= block.timestamp && MBC.endTime >= block.timestamp, "Error: Sale 1 not active");
                require(signature.Sale1MaxMint > 0, "Error: Max mint per user is 0");
                require(MBC.register[minter] + mintAmount <= signature.Sale1MaxMint,
                "Error: Max mint per user reached");
                MBC.register[minter] += mintAmount;
                return uint8(2);
            }
            else if (signature.PhaseType == 1) {
                require(Investors.startTime <= block.timestamp && Investors.endTime >= block.timestamp, "Error: Sale 2 not active");
                require(signature.Sale2MaxMint > 0, "Error: Max mint per user is 0");
                require(Investors.register[minter] + mintAmount <= signature.Sale2MaxMint,
                "Error: Max mint per user reached");
                Investors.register[minter] += mintAmount;
                return uint8(1);
            }
            else if (signature.PhaseType == 3) {
                require(Guaranteed.startTime <= block.timestamp && Guaranteed.endTime >= block.timestamp, "Error: Sale 3 not active");
                require(signature.Sale3MaxMint > 0, "Error: Max mint per user is 0");
                require(Guaranteed.register[minter] + mintAmount <= signature.Sale3MaxMint,
                "Error: Max mint per user reached");
                Guaranteed.register[minter] += mintAmount;
                return uint8(3);
            }
            else if (signature.PhaseType == 4) {
                require(FCFS.startTime <= block.timestamp && FCFS.endTime >= block.timestamp, "Error: Sale 4 not active");
                require(signature.Sale4MaxMint > 0, "Error: Max mint per user is 0");
                require(FCFS.register[minter] + mintAmount <= signature.Sale4MaxMint,
                "Error: Max mint per user reached");
                FCFS.register[minter] += mintAmount;
                return uint8(4);
            }
            else {
                require(Public.startTime <= block.timestamp && Public.endTime >= block.timestamp,
                "Error:Public not active");
                Public.register[minter] += mintAmount;
                return uint8(5);
            }
        }
    }
    
    
    // Endpoint.sol estimateFees() returns the fees for the message
    function estimateFees(
        address userAddress,
        uint16 chainId,
        uint256 mintAmount
    ) public view returns (uint256 nativeFee, uint256 zroFee) {
        return
        lzEndpoint.estimateFees(
            chainId,
            address(this),
            abi.encode(userAddress, mintAmount),
            false,
            abi.encodePacked(uint16(1),uint256(increaseGasPerToken * mintAmount))
        );
    }
    
    function _nonblockingLzReceive(
        uint16 ,
        bytes memory ,
        uint64 ,
        bytes memory
    ) internal virtual override {
        return ;
    }
    
    // Whitelist-Wizard functions
    
    function checkSignature(whitelist memory _signature, address _user) public view returns (
        bool isSignatureValid,
        bool isDesignatedSignerValid
    ) {
        if (getSigner(_signature) == designatedSigner) {
            isDesignatedSignerValid = true;
        }
        if (isValidSignature(_signature,_user)) {
            isSignatureValid = true;
        }
        return (isSignatureValid, isDesignatedSignerValid);
    }
    
    // Internal functions
    function bytesToAddress(bytes memory data) public pure returns (address) {
        require(data.length == 20, "Invalid address length");
        
        address addr;
        assembly {
            addr := mload(add(data, 20))
        }
        return addr;
    }
    
    // Getters
    /**
       * @notice This function returns the number of tokens minted by a member in a particular phase
       * @param _address Address of the member
    */
    function readRegister(address _address, uint8 phase) public view returns (uint256) {
        require(_address != address(0), "Invalid address provided");
        if (phase == 2) return MBC.register[_address];
        else if (phase == 1) return Investors.register[_address];
        else if (phase == 3) return Guaranteed.register[_address];
        else if (phase == 4) return FCFS.register[_address];
        else return Public.register[_address];
    }
}
"
    },
    "contracts/utils/BytesLib.sol": {
      "content": "// SPDX-License-Identifier: Unlicense
/*
 * @title Solidity Bytes Arrays Utils
 * @author Gonçalo Sá <goncalo.sa@consensys.net>
 *
 * @dev Bytes tightly packed arrays utility library for ethereum contracts written in Solidity.
 *      The library lets you concatenate, slice and type cast bytes arrays both in memory and storage.
 */
pragma solidity >=0.8.0 <0.9.0;


library BytesLib {
    function concat(
        bytes memory _preBytes,
        bytes memory _postBytes
    )
    internal
    pure
    returns (bytes memory)
    {
        bytes memory tempBytes;

        assembly {
        // Get a location of some free memory and store it in tempBytes as
        // Solidity does for memory variables.
            tempBytes := mload(0x40)

        // Store the length of the first bytes array at the beginning of
        // the memory for tempBytes.
            let length := mload(_preBytes)
            mstore(tempBytes, length)

        // Maintain a memory counter for the current write location in the
        // temp bytes array by adding the 32 bytes for the array length to
        // the starting location.
            let mc := add(tempBytes, 0x20)
        // Stop copying when the memory counter reaches the length of the
        // first bytes array.
            let end := add(mc, length)

            for {
            // Initialize a copy counter to the start of the _preBytes data,
            // 32 bytes into its memory.
                let cc := add(_preBytes, 0x20)
            } lt(mc, end) {
            // Increase both counters by 32 bytes each iteration.
                mc := add(mc, 0x20)
                cc := add(cc, 0x20)
            } {
            // Write the _preBytes data into the tempBytes memory 32 bytes
            // at a time.
                mstore(mc, mload(cc))
            }

        // Add the length of _postBytes to the current length of tempBytes
        // and store it as the new length in the first 32 bytes of the
        // tempBytes memory.
            length := mload(_postBytes)
            mstore(tempBytes, add(length, mload(tempBytes)))

        // Move the memory counter back from a multiple of 0x20 to the
        // actual end of the _preBytes data.
            mc := end
        // Stop copying when the memory counter reaches the new combined
        // length of the arrays.
            end := add(mc, length)

            for {
                let cc := add(_postBytes, 0x20)
            } lt(mc, end) {
                mc := add(mc, 0x20)
                cc := add(cc, 0x20)
            } {
                mstore(mc, mload(cc))
            }

        // Update the free-memory pointer by padding our last write location
        // to 32 bytes: add 31 bytes to the end of tempBytes to move to the
        // next 32 byte block, then round down to the nearest multiple of
        // 32. If the sum of the length of the two arrays is zero then add
        // one before rounding down to leave a blank 32 bytes (the length block with 0).
            mstore(0x40, and(
                add(add(end, iszero(add(length, mload(_preBytes)))), 31),
                not(31) // Round down to the nearest 32 bytes.
            ))
        }

        return tempBytes;
    }

    function concatStorage(bytes storage _preBytes, bytes memory _postBytes) internal {
        assembly {
        // Read the first 32 bytes of _preBytes storage, which is the length
        // of the array. (We don't need to use the offset into the slot
        // because arrays use the entire slot.)
            let fslot := sload(_preBytes.slot)
        // Arrays of 31 bytes or less have an even value in their slot,
        // while longer arrays have an odd value. The actual length is
        // the slot divided by two for odd values, and the lowest order
        // byte divided by two for even values.
        // If the slot is even, bitwise and the slot with 255 and divide by
        // two to get the length. If the slot is odd, bitwise and the slot
        // with -1 and divide by two.
            let slength := div(and(fslot, sub(mul(0x100, iszero(and(fslot, 1))), 1)), 2)
            let mlength := mload(_postBytes)
            let newlength := add(slength, mlength)
        // slength can contain both the length and contents of the array
        // if length < 32 bytes so let's prepare for that
        // v. http://solidity.readthedocs.io/en/latest/miscellaneous.html#layout-of-state-variables-in-storage
            switch add(lt(slength, 32), lt(newlength, 32))
            case 2 {
            // Since the new array still fits in the slot, we just need to
            // update the contents of the slot.
            // uint256(bytes_storage) = uint256(bytes_storage) + uint256(bytes_memory) + new_length
                sstore(
                    _preBytes.slot,
                // all the modifications to the slot are inside this
                // next block
                    add(
                    // we can just add to the slot contents because the
                    // bytes we want to change are the LSBs
                        fslot,
                        add(
                            mul(
                                div(
                                // load the bytes from memory
                                    mload(add(_postBytes, 0x20)),
                                // zero all bytes to the right
                                    exp(0x100, sub(32, mlength))
                                ),
                            // and now shift left the number of bytes to
                            // leave space for the length in the slot
                                exp(0x100, sub(32, newlength))
                            ),
                        // increase length by the double of the memory
                        // bytes length
                            mul(mlength, 2)
                        )
                    )
                )
            }
            case 1 {
            // The stored value fits in the slot, but the combined value
            // will exceed it.
            // get the keccak hash to get the contents of the array
                mstore(0x0, _preBytes.slot)
                let sc := add(keccak256(0x0, 0x20), div(slength, 32))

            // save new length
                sstore(_preBytes.slot, add(mul(newlength, 2), 1))

            // The contents of the _postBytes array start 32 bytes into
            // the structure. Our first read should obtain the `submod`
            // bytes that can fit into the unused space in the last word
            // of the stored array. To get this, we read 32 bytes starting
            // from `submod`, so the data we read overlaps with the array
            // contents by `submod` bytes. Masking the lowest-order
            // `submod` bytes allows us to add that value directly to the
            // stored value.

                let submod := sub(32, slength)
                let mc := add(_postBytes, submod)
                let end := add(_postBytes, mlength)
                let mask := sub(exp(0x100, submod), 1)

                sstore(
                    sc,
                    add(
                        and(
                            fslot,
                            0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff00
                        ),
                        and(mload(mc), mask)
                    )
                )

                for {
                    mc := add(mc, 0x20)
                    sc := add(sc, 1)
                } lt(mc, end) {
                    sc := add(sc, 1)
                    mc := add(mc, 0x20)
                } {
                    sstore(sc, mload(mc))
                }

                mask := exp(0x100, sub(mc, end))

                sstore(sc, mul(div(mload(mc), mask), mask))
            }
            default {
            // get the keccak hash to get the contents of the array
                mstore(0x0, _preBytes.slot)
            // Start copying to the last used word of the stored array.
                let sc := add(keccak256(0x0, 0x20), div(slength, 32))

            // save new length
                sstore(_preBytes.slot, add(mul(newlength, 2), 1))

            // Copy over the first `submod` bytes of the new data as in
            // case 1 above.
                let slengthmod := mod(slength, 32)
                let mlengthmod := mod(mlength, 32)
                let submod := sub(32, slengthmod)
                let mc := add(_postBytes, submod)
                let end := add(_postBytes, mlength)
                let mask := sub(exp(0x100, submod), 1)

                sstore(sc, add(sload(sc), and(mload(mc), mask)))

                for {
                    sc := add(sc, 1)
                    mc := add(mc, 0x20)
                } lt(mc, end) {
                    sc := add(sc, 1)
                    mc := add(mc, 0x20)
                } {
                    sstore(sc, mload(mc))
                }

                mask := exp(0x100, sub(mc, end))

                sstore(sc, mul(div(mload(mc), mask), mask))
            }
        }
    }

    function slice(
        bytes memory _bytes,
        uint256 _start,
        uint256 _length
    )
    internal
    pure
    returns (bytes memory)
    {
        require(_length + 31 >= _length, "slice_overflow");
        require(_bytes.length >= _start + _length, "slice_outOfBounds");

        bytes memory tempBytes;

        assembly {
            switch iszero(_length)
            case 0 {
            // Get a location of some free memory and store it in tempBytes as
            // Solidity does for memory variables.
                tempBytes := mload(0x40)

            // The first word of the slice result is potentially a partial
            // word read from the original array. To read it, we calculate
            // the length of that partial word and start copying that many
            // bytes into the array. The first word we copy will start with
            // data we don't care about, but the last `lengthmod` bytes will
            // land at the beginning of the contents of the new array. When
            // we're done copying, we overwrite the full first word with
            // the actual length of the slice.
                let lengthmod := and(_length, 31)

            // The multiplication in the next line is necessary
            // because when slicing multiples of 32 bytes (lengthmod == 0)
            // the following copy loop was copying the origin's length
            // and then ending prematurely not copying everything it should.
                let mc := add(add(tempBytes, lengthmod), mul(0x20, iszero(lengthmod)))
                let end := add(mc, _length)

                for {
                // The multiplication in the next line has the same exact purpose
                // as the one above.
                    let cc := add(add(add(_bytes, lengthmod), mul(0x20, iszero(lengthmod))), _start)
                } lt(mc, end) {
                    mc := add(mc, 0x20)
                    cc := add(cc, 0x20)
                } {
                    mstore(mc, mload(cc))
                }

                mstore(tempBytes, _length)

            //update free-memory pointer
            //allocating the array padded to 32 bytes like the compiler does now
                mstore(0x40, and(add(mc, 31), not(31)))
            }
            //if we want a zero-length slice let's just return a zero-length array
            default {
                tempBytes := mload(0x40)
            //zero out the 32 bytes slice we are about to return
            //we need to do it because Solidity does not garbage collect
                mstore(tempBytes, 0)

                mstore(0x40, add(tempBytes, 0x20))
            }
        }

        return tempBytes;
    }

    function toAddress(bytes memory _bytes, uint256 _start) internal pure returns (address) {
        require(_bytes.length >= _start + 20, "toAddress_outOfBounds");
        address tempAddress;

        assembly {
            tempAddress := div(mload(add(add(_bytes, 0x20), _start)), 0x1000000000000000000000000)
        }

        return tempAddress;
    }

    function toUint8(bytes memory _bytes, uint256 _start) internal pure returns (uint8) {
        require(_bytes.length >= _start + 1 , "toUint8_outOfBounds");
        uint8 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x1), _start))
        }

        return tempUint;
    }

    function toUint16(bytes memory _bytes, uint256 _start) internal pure returns (uint16) {
        require(_bytes.length >= _start + 2, "toUint16_outOfBounds");
        uint16 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x2), _start))
        }

        return tempUint;
    }

    function toUint32(bytes memory _bytes, uint256 _start) internal pure returns (uint32) {
        require(_bytes.length >= _start + 4, "toUint32_outOfBounds");
        uint32 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x4), _start))
        }

        return tempUint;
    }

    function toUint64(bytes memory _bytes, uint256 _start) internal pure returns (uint64) {
        require(_bytes.length >= _start + 8, "toUint64_outOfBounds");
        uint64 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x8), _start))
        }

        return tempUint;
    }

    function toUint96(bytes memory _bytes, uint256 _start) internal pure returns (uint96) {
        require(_bytes.length >= _start + 12, "toUint96_outOfBounds");
        uint96 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0xc), _start))
        }

        return tempUint;
    }

    function toUint128(bytes memory _bytes, uint256 _start) internal pure returns (uint128) {
        require(_bytes.length >= _start + 16, "toUint128_outOfBounds");
        uint128 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x10), _start))
        }

        return tempUint;
    }

    function toUint256(bytes memory _bytes, uint256 _start) internal pure returns (uint256) {
        require(_bytes.length >= _start + 32, "toUint256_outOfBounds");
        uint256 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x20), _start))
        }

        return tempUint;
    }

    function toBytes32(bytes memory _bytes, uint256 _start) internal pure returns (bytes32) {
        require(_bytes.length >= _start + 32, "toBytes32_outOfBounds");
        bytes32 tempBytes32;

        assembly {
            tempBytes32 := mload(add(add(_bytes, 0x20), _start))
        }

        return tempBytes32;
    }

    function equal(bytes memory _preBytes, bytes memory _postBytes) internal pure returns (bool) {
        bool success = true;

        assembly {
            let length := mload(_preBytes)

        // if lengths don't match the arrays are not equal
            switch eq(length, mload(_postBytes))
            case 1 {
            // cb is a circuit breaker in the for loop since there's
            //  no said feature for inline assembly loops
            // cb = 1 - don't breaker
            // cb = 0 - break
                let cb := 1

                let mc := add(_preBytes, 0x20)
                let end := add(mc, length)

                for {
                    let cc := add(_postBytes, 0x20)
                // the next line is the loop condition:
                // while(uint256(mc < end) + cb == 2)
                } eq(add(lt(mc, end), cb), 2) {
                    mc := add(mc, 0x20)
                    cc := add(cc, 0x20)
                } {
                // if any of these checks fails then arrays are not equal
                    if iszero(eq(mload(mc), mload(cc))) {
                    // unsuccess:
                        success := 0
                        cb := 0
                    }
                }
            }
            default {
            // unsuccess:
                success := 0
            }
        }

        return success;
    }

    function equalStorage(
        bytes storage _preBytes,
        bytes memory _postBytes
    )
    internal
    view
    returns (bool)
    {
        bool success = true;

        assembly {
        // we know _preBytes_offset is 0
            let fslot := sload(_preBytes.slot)
        // Decode the length of the stored array like in concatStorage().
            let slength := div(and(fslot, sub(mul(0x100, iszero(and(fslot, 1))), 1)), 2)
            let mlength := mload(_postBytes)

        // if lengths don't match the arrays are not equal
            switch eq(slength, mlength)
            case 1 {
            // slength can contain both the length and contents of the array
            // if length < 32 bytes so let's prepare for that
            // v. http://solidity.readthedocs.io/en/latest/miscellaneous.html#layout-of-state-variables-in-storage
                if iszero(iszero(slength)) {
                    switch lt(slength, 32)
                    case 1 {
                    // blank the last byte which is the length
                        fslot := mul(div(fslot, 0x100), 0x100)

                        if iszero(eq(fslot, mload(add(_postBytes, 0x20)))) {
                        // unsuccess:
                            success := 0
                        }
                    }
                    default {
                    // cb is a circuit breaker in the for loop since there's
                    //  no said feature for inline assembly loops
                    // cb = 1 - don't breaker
                    // cb = 0 - break
                        let cb := 1

                    // get the keccak hash to get the contents of the array
                        mstore(0x0, _preBytes.slot)
                        let sc := keccak256(0x0, 0x20)

                        let mc := add(_postBytes, 0x20)
                        let end := add(mc, mlength)

                    // the next line is the loop condition:
                    // while(uint256(mc < end) + cb == 2)
                        for {} eq(add(lt(mc, end), cb), 2) {
                            sc := add(sc, 1)
                            mc := add(mc, 0x20)
                        } {
                            if iszero(eq(sload(sc), mload(mc))) {
                            // unsuccess:
                                success := 0
                                cb := 0
                            }
                        }
                    }
                }
            }
            default {
            // unsuccess:
                success := 0
            }
        }

        return success;
    }
}"
    },
    "contracts/utils/ExcessivelySafeCall.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity >=0.7.6;

library ExcessivelySafeCall {
    uint256 constant LOW_28_MASK =
    0x00000000ffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

    /// @notice Use when you _really_ really _really_ don't trust the called
    /// contract. This prevents the called contract from causing reversion of
    /// the caller in as many ways as we can.
    /// @dev The main difference between this and a solidity low-level call is
    /// that we limit the number of bytes that the callee can cause to be
    /// copied to caller memory. This prevents stupid things like malicious
    /// contracts returning 10,000,000 bytes causing a local OOG when copying
    /// to memory.
    /// @param _target The address to call
    /// @param _gas The amount of gas to forward to the remote contract
    /// @param _maxCopy The maximum number of bytes of returndata to copy
    /// to memory.
    /// @param _calldata The data to send to the remote contract
    /// @return success and returndata, as `.call()`. Returndata is capped to
    /// `_maxCopy` bytes.
    function excessivelySafeCall(
        address _target,
        uint256 _gas,
        uint16 _maxCopy,
        bytes memory _calldata
    ) internal returns (bool, bytes memory) {
        // set up for assembly call
        uint256 _toCopy;
        bool _success;
        bytes memory _returnData = new bytes(_maxCopy);
        // dispatch message to recipient
        // by assembly calling "handle" function
        // we call via assembly to avoid memcopying a very large returndata
        // returned by a malicious contract
        assembly {
            _success := call(
                _gas, // gas
                _target, // recipient
                0, // ether value
                add(_calldata, 0x20), // inloc
                mload(_calldata), // inlen
                0, // outloc
                0 // outlen
            )
        // limit our copy to 256 bytes
            _toCopy := returndatasize()
            if gt(_toCopy, _maxCopy) {
                _toCopy := _maxCopy
            }
        // Store the length of the copied bytes
            mstore(_returnData, _toCopy)
        // copy the bytes from returndata[0:_toCopy]
            returndatacopy(add(_returnData, 0x20), 0, _toCopy)
        }
        return (_success, _returnData);
    }

    /// @notice Use when you _really_ really _really_ don't trust the called
    /// contract. This prevents the called contract from causing reversion of
    /// the caller in as many ways as we can.
    /// @dev The main difference between this and a solidity low-level call is
    /// that we limit the number of bytes that the callee can cause to be
    /// copied to caller memory. This prevents stupid things like malicious
    /// contracts returning 10,000,000 bytes causing a local OOG when copying
    /// to memory.
    /// @param _target The address to call
    /// @param _gas The amount of gas to forward to the remote contract
    /// @param _maxCopy The maximum number of bytes of returndata to copy
    /// to memory.
    /// @param _calldata The data to send to the remote contract
    /// @return success and returndata, as `.call()`. Returndata is capped to
    /// `_maxCopy` bytes.
    function excessivelySafeStaticCall(
        address _target,
        uint256 _gas,
        uint16 _maxCopy,
        bytes memory _calldata
    ) internal view returns (bool, bytes memory) {
        // set up for assembly call
        uint256 _toCopy;
        bool _success;
        bytes memory _returnData = new bytes(_maxCopy);
        // dispatch message to recipient
        // by assembly calling "handle" function
        // we call via assembly to avoid memcopying a very large returndata
        // returned by a malicious contract
        assembly {
            _success := staticcall(
                _gas, // gas
                _target, // recipient
                add(_calldata, 0x20), // inloc
                mload(_calldata), // inlen
                0, // outloc
                0 // outlen
            )
        // limit our copy to 256 bytes
            _toCopy := returndatasize()
            if gt(_toCopy, _maxCopy) {
                _toCopy := _maxCopy
            }
        // Store the length of the copied bytes
            mstore(_returnData, _toCopy)
        // copy the bytes from returndata[0:_toCopy]
            returndatacopy(add(_returnData, 0x20), 0, _toCopy)
        }
        return (_success, _returnData);
    }

    /**
     * @notice Swaps function selectors in encoded contract calls
     * @dev Allows reuse of encoded calldata for functions with identical
     * argument types but different names. It simply swaps out the first 4 bytes
     * for the new selector. This function modifies memory in place, and should
     * only be used with caution.
     * @param _newSelector The new 4-byte selector
     * @param _buf The encoded contract args
     */
    function swapSelector(bytes4 _newSelector, bytes memory _buf)
    internal
    pure
    {
        require(_buf.length >= 4);
        uint256 _mask = LOW_28_MASK;
        assembly {
        // load the first word of
            let _word := mload(add(_buf, 0x20))
        // mask out the top 4 bytes
        // /x
            _word := and(_word, _mask)
            _word := or(_newSelector, _word)
            mstore(add(_buf, 0x20), _word)
        }
    }
}"
    },
    "contracts/utils/LzApp.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import "../Interface/ILayerZeroReceiver.sol";
import "../Interface/ILayerZeroUserApplicationConfig.sol";
import "../Interface/ILayerZeroEndpoint.sol";
import "./BytesLib.sol";

/*
 * a generic LzReceiver implementation
 */
abstract contract LzApp is Ownable2StepUpgradeable, ILayerZeroReceiver, ILayerZeroUserApplicationConfig {
    using BytesLib for bytes;

    // ua can not send payload larger than this by default, but it can be changed by the ua owner
    uint constant public DEFAULT_PAYLOAD_SIZE_LIMIT = 10000;

    ILayerZeroEndpoint public lzEndpoint;
    mapping(uint16 => bytes) public trustedRemoteLookup;
    mapping(uint16 => mapping(uint16 => uint)) public minDstGasLookup;
    mapping(uint16 => uint) public payloadSizeLimitLookup;
    address public precrime;

    event SetPrecrime(address precrime);
    event SetTrustedRemote(uint16 _remoteChainId, bytes _path);
    event SetTrustedRemoteAddress(uint16 _remoteChainId, bytes _remoteAddress);
    event SetMinDstGas(uint16 _dstChainId, uint16 _type, uint _minDstGas);

    function __lzApp_init(address _endpoint) internal {
        __Ownable2Step_init();
        lzEndpoint = ILayerZeroEndpoint(_endpoint);
    }

    function lzReceive(uint16 _srcChainId, bytes calldata _srcAddress, uint64 _nonce, bytes calldata _payload) public virtual override {
        // lzReceive must be called by the endpoint for security
        require(_msgSender() == address(lzEndpoint), "LzApp: invalid endpoint caller");

        bytes memory trustedRemote = trustedRemoteLookup[_srcChainId];
        // if will still block the message pathway from (srcChainId, srcAddress). should not receive message from untrusted remote.
        require(_srcAddress.length == trustedRemote.length && trustedRemote.length > 0 && keccak256(_srcAddress) == keccak256(trustedRemote), "LzApp: invalid source sending contract");

        _blockingLzReceive(_srcChainId, _srcAddress, _nonce, _payload);
    }

    // abstract function - the default behaviour of LayerZero is blocking. See: NonblockingLzApp if you dont need to enforce ordered messaging
    function _blockingLzReceive(uint16 _srcChainId, bytes memory _srcAddress, uint64 _nonce, bytes memory _payload) internal virtual;

    function _lzSend(uint16 _dstChainId, bytes memory _payload, address payable _refundAddress, address _zroPaymentAddress, bytes memory _adapterParams, uint _nativeFee) internal virtual {
        bytes memory trustedRemote = trustedRemoteLookup[_dstChainId];
        require(trustedRemote.length != 0, "LzApp: destination chain is not a trusted source");
        _checkPayloadSize(_dstChainId, _payload.length);
        lzEndpoint.send{value: _nativeFee}(_dstChainId, trustedRemote, _payload, _refundAddress, _zroPaymentAddress, _adapterParams);
    }

    function _checkGasLimit(uint16 _dstChainId, uint16 _type, bytes memory _adapterParams, uint _extraGas) internal view virtual {
        uint providedGasLimit = _getGasLimit(_adapterParams);
        uint minGasLimit = minDstGasLookup[_dstChainId][_type] + _extraGas;
        require(minGasLimit > 0, "LzApp: minGasLimit not set");
        require(providedGasLimit >= minGasLimit, "LzApp: gas limit is too low");
    }

    function _getGasLimit(bytes memory _adapterParams) internal pure virtual returns (uint gasLimit) {
        require(_adapterParams.length >= 34, "LzApp: invalid adapterParams");
        assembly {
            gasLimit := mload(add(_adapterParams, 34))
        }
    }

    function _checkPayloadSize(uint16 _dstChainId, uint _payloadSize) internal view virtual {
        uint payloadSizeLimit = payloadSizeLimitLookup[_dstChainId];
        if (payloadSizeLimit == 0) { // use default if not set
            payloadSizeLimit = DEFAULT_PAYLOAD_SIZE_LIMIT;
        }
        require(_payloadSize <= payloadSizeLimit, "LzApp: payload size is too large");
    }

    //---------------------------UserApplication config----------------------------------------
    function getConfig(uint16 _version, uint16 _chainId, address, uint _configType) external view returns (bytes memory) {
        return lzEndpoint.getConfig(_version, _chainId, address(this), _configType);
    }

    // generic config for LayerZero user Application
    function setConfig(uint16 _version, uint16 _chainId, uint _configType, bytes calldata _config) external override onlyOwner {
        lzEndpoint.setConfig(_version, _chainId, _configType, _config);
    }

    function setSendVersion(uint16 _version) external override onlyOwner {
        lzEndpoint.setSendVersion(_version);
    }

    function setReceiveVersion(uint16 _version) external override onlyOwner {
        lzEndpoint.setReceiveVersion(_version);
    }

    function forceResumeReceive(uint16 _srcChainId, bytes calldata _srcAddress) external override onlyOwner {
        lzEndpoint.forceResumeReceive(_srcChainId, _srcAddress);
    }

    // _path = abi.encodePacked(remoteAddress, localAddress)
    // this function set the trusted path for the cross-chain communication
    function setTrustedRemote(uint16 _remoteChainId, bytes calldata _path) external onlyOwner {
        trustedRemoteLookup[_remoteChainId] = _path;
        emit SetTrustedRemote(_remoteChainId, _path);
    }

    function setTrustedRemoteAddress(uint16 _remoteChainId, bytes calldata _remoteAddress) external onlyOwner {
        trustedRemoteLookup[_remoteChainId] = abi.encodePacked(_remoteAddress, address(this));
        emit SetTrustedRemoteAddress(_remoteChainId, _remoteAddress);
    }

    function getTrustedRemoteAddress(uint16 _remoteChainId) external view returns (bytes memory) {
        bytes memory path = trustedRemoteLookup[_remoteChainId];
        require(path.length != 0, "LzApp: no trusted path record");
        return path.slice(0, path.length - 20); // the last 20 bytes should be address(this)
    }

    function setPrecrime(address _precrime) external onlyOwner {
        precrime = _precrime;
        emit SetPrecrime(_precrime);
    }

    function setMinDstGas(uint16 _dstChainId, uint16 _packetType, uint _minGas) external onlyOwner {
        require(_minGas > 0, "LzApp: invalid minGas");
        minDstGasLookup[_dstChainId][_packetType] = _minGas;
        emit SetMinDstGas(_dstChainId, _packetType, _minGas);
    }

    // if the size is 0, it means default size limit
    function setPayloadSizeLimit(uint16 _dstChainId, uint _size) external onlyOwner {
        payloadSizeLimitLookup[_dstChainId] = _size;
    }

    //--------------------------- VIEW FUNCTION ----------------------------------------
    function isTrustedRemote(uint16 _srcChainId, bytes calldata _srcAddress) external view returns (bool) {
        bytes memory trustedSource = trustedRemoteLookup[_srcChainId];
        return keccak256(trustedSource) == keccak256(_srcAddress);
    }
}"
    },
    "contracts/utils/NonblockingLzApp.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import {LzApp, Ownable2StepUpgradeable} from "./LzApp.sol";
import "./ExcessivelySafeCall.sol";

/*
 * the default LayerZero messaging behaviour is blocking, i.e. any failed message will block the channel
 * this abstract class try-catch all fail messages and store locally for future retry. hence, non-blocking
 * NOTE: if the srcAddress is not configured properly, it will still block the message pathway from (srcChainId, srcAddress)
 */
abstract contract NonblockingLzApp is LzApp {
    using ExcessivelySafeCall for address;

    function __nonblockingLzApp_init(address _endPoint) internal {
        __lzApp_init(_endPoint);
    }

    mapping(uint16 => mapping(bytes => mapping(uint64 => bytes32))) public failedMessages;

    event MessageFailed(uint16 _srcChainId, bytes _srcAddress, uint64 _nonce, bytes _payload, bytes _reason);
    event RetryMessageSuccess(uint16 _srcChainId, bytes _srcAddress, uint64 _nonce, bytes32 _payloadHash);

    // overriding the virtual function in LzReceiver
    function _blockingLzReceive(uint16 _srcChainId, bytes memory _srcAddress, uint64 _nonce, bytes memory _payload) internal virtual override {
        (bool success, bytes memory reason) = address(this).excessivelySafeCall(gasleft(), 150, abi.encodeWithSelector(this.nonblockingLzReceive.selector, _srcChainId, _srcAddress, _nonce, _payload));
        // try-catch all errors/exceptions
        if (!success) {
            _storeFailedMessage(_srcChainId, _srcAddress, _nonce, _payload, reason);
        }
    }

    function _storeFailedMessage(uint16 _srcChainId, bytes memory _srcAddress, uint64 _nonce, bytes memory _payload, bytes memory _reason) internal virtual {
        failedMessages[_srcChainId][_srcAddress][_nonce] = keccak256(_payload);
        emit MessageFailed(_srcChainId, _srcAddress, _nonce, _payload, _reason);
    }

    function nonblockingLzReceive(uint16 _srcChainId, bytes calldata _srcAddress, uint64 _nonce, bytes calldata _payload) public virtual {
        // only internal transaction
        require(_msgSender() == address(this), "NonblockingLzApp: caller must be LzApp");
        _nonblockingLzReceive(_srcChainId, _srcAddress, _nonce, _payload);
    }

    //@notice override this function
    function _nonblockingLzReceive(uint16 _srcChainId, bytes memory _srcAddress, uint64 _nonce, bytes memory _payload) internal virtual;

    function retryMessage(uint16 _srcChainId, bytes calldata _srcAddress, uint64 _nonce, bytes calldata _payload) public payable virtual {
        // assert there is message to retry
        bytes32 payloadHash = failedMessages[_srcChainId][_srcAddress][_nonce];
        require(payloadHash != bytes32(0), "NonblockingLzApp: no stored message");
        require(keccak256(_payload) == payloadHash, "NonblockingLzApp: invalid payload");
        // clear the stored message
        failedMessages[_srcChainId][_srcAddress][_nonce] = bytes32(0);
        // execute the message. revert if it fails again
        _nonblockingLzReceive(_srcChainId, _srcAddress, _nonce, _payload);
        emit RetryMessageSuccess(_srcChainId, _srcAddress, _nonce, payloadHash);
    }
}"
    },
    "contracts/utils/whitelistWizard.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;
import "@openzeppelin/contracts-upgradeable/utils/cryptography/draft-EIP712Upgradeable.sol";

abstract contract whitelistWizard is EIP712Upgradeable{

	error SignatureInvalid(); // if the call is not from the authorised address
	error SignatureCallerInvalid(); // if the signature is invalid
	error SignatureExpired(); // if the nonce is expired
	error SignatureAlreadyUsed(); // if the signature is already used


	string private SIGNING_DOMAIN;
	string private SIGNATURE_VERSION;
	uint32 private signatureValidity;
	address public designatedSigner;

	mapping (bytes => bool) private usedSignatures;

	struct whitelist {
		uint8 PhaseType;
		uint8 Sale1MaxMint;
		uint8 Sale2MaxMint;
		uint8 Sale3MaxMint;
		uint8 Sale4MaxMint;
		uint32 timestamp;
		address userAddress;
		bytes signature;
	}

	/**
		 @notice This is initializer function is used to initialize values of contracts
    */
	function __Wizard_init(string memory _domain, string memory _version, address _designedSigner) internal {
		SIGNING_DOMAIN = _domain;
		SIGNATURE_VERSION = _version;
		signatureValidity = 5 minutes;
		designatedSigner = _designedSigner;
		__EIP712_init(_domain, _version);
	}

	/**
		 @dev This function is used to get signer address of signature
         @param _whitelist whitelist object
    */
	function getSigner(whitelist memory _whitelist) public view returns (address) {
		return _verify(_whitelist);

	}

	/**
		 @dev This function is used to generate hash message
         @param _whitelist whitelist object to create hash
    */
	function _hash(whitelist memory _whitelist) internal view returns (bytes32) {
		return
		_hashTypedDataV4(
			keccak256(
				abi.encode(
					keccak256(
	"whitelist(uint8 PhaseType,uint8 Sale1MaxMint,uint8 Sale2MaxMint,uint8 Sale3MaxMint,uint8 Sale4MaxMint,uint32 timestamp,address userAddress)"
					),
					_whitelist.PhaseType,
					_whitelist.Sale1MaxMint,
					_whitelist.Sale2MaxMint,
					_whitelist.Sale3MaxMint,
					_whitelist.Sale4MaxMint,
					_whitelist.timestamp,
					_whitelist.userAddress
				)
			)
		);
	}

	/**
		 @dev This function is used to verify signature
		 @param _whitelist whitelist object to verify
    */
	function _verify(whitelist memory _whitelist) internal view returns (address) {
		bytes32 digest = _hash(_whitelist);
		return ECDSAUpgradeable.recover(digest, _whitelist.signature);
	}

	/**
		 @dev This function is used to verify and confirm a signature
		 @param _signature whitelist object to verify
		 @param _caller address of the caller
    */
	function isValidSignature(
		whitelist memory _signature,
		address _caller
	) public view returns (bool) {
		if (getSigner(_signature) != designatedSigner) {
			revert SignatureInvalid();
		}

		if (_signature.userAddress != _caller) {
			revert SignatureCallerInvalid();
		}

		if (_signature.timestamp + signatureValidity < uint32(block.timestamp)) {
			revert SignatureExpired();
		}

		if (usedSignatures[_signature.signature]) {
			revert SignatureAlreadyUsed();
		}
		return true;
	}

	//--------------------Write functions--------------------

	/**
		 @dev This function is used to validate signature
		 @param _signature whitelist object to verify
		 @param _caller address of the caller
	*/
	function validateSignature(
		whitelist memory _signature,
		address _caller
	) internal returns (bool) {
		isValidSignature(_signature, _caller);
		usedSignatures[_signature.signature] = true;
		return true;
	}

	/**
	     @dev Use onlyOwner function to set this function
		 @dev This function is used to set signer address
		 @param _designatedSigner address of the signer
	*/
	function setDesignatedSigner(address _designatedSigner) external {
		require(_designatedSigner != address(0), "Invalid signer address");
		require(_designatedSigner != designatedSigner, "Signer already set");
		designatedSigner = _designatedSigner;
	}
}

"
    },
    "operator-filter-registry/src/IOperatorFilterRegistry.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

interface IOperatorFilterRegistry {
    /**
     * @notice Returns true if operator is not filtered for a given token, either by address or codeHash. Also returns
     *         true if supplied registrant address is not registered.
     */
    function isOperatorAllowed(address registrant, address operator) external view returns (bool);

    /**
     * @notice Registers an address with the registry. May be called by address itself or by EIP-173 owner.
     */
    function register(address registrant) external;

    /**
     * @notice Registers an address with the registry and "subscribes" to another address's filtered operators and codeHashes.
     */
    function registerAndSubscribe(address registrant, address subscription) external;

    /**
     * @notice Registers an address with the registry and copies the filtered operators and codeHashes from another
     *         address without subscribing.
     */
    function registerAndCopyEntries(address registrant, address registrantToCopy) external;

    /**
     * @notice Unregisters an address with the registry and removes its subscription. May be called by address itself or by EIP-173 owner.
     *         Note that this does not remove any filtered addresses or codeHashes.
     *         Also note that any subscriptions to this registrant will still be active and follow the existing filtered addresses and codehashes.
     */
    function unregister(address addr) external;

    /**
     * @notice Update an operator address for a registered address - when filtered is true, the operator is filtered.
     */
    function updateOperator(address registrant, address operator, bool filtered) external;

    /**
     * @notice Update multiple operators for a registered address - when filtered is true, the operators will be filtered. Reverts on duplicates.
     */
    function updateOperators(address registrant, address[] calldata operators, bool filtered) external;

    /**
     * @notice Update a codeHash for a registered address - when filtered is true, the codeHash is filtered.
     */
    function updateCodeHash(address registrant, bytes32 codehash, bool filtered) external;

    /**
     * @notice Update multiple codeHashes for a registered address - when filtered is true, the codeHashes will be filtered. Reverts on duplicates.
     */
    function updateCodeHashes(address registrant, bytes32[] calldata codeHashes, bool filtered) external;

    /**
     * @notice Subscribe an address to another registrant's filtered operators and codeHashes. Will remove previous
     *         subscription if present.
     *         Note that accounts with subscriptions may go on to subscribe to other accounts - in this case,
     *         subscriptions will not be forwarded. Instead the former subscription's existing entries will still be
     *         used.
     */
    function subscribe(address registrant, address registrantToSubscribe) external;

    /**
     * @notice Unsubscribe an address from its current subscribed registrant, and optionally copy its filtered operators and codeHashes.
     */
    function unsubscribe(address registrant, bool copyExistingEntries) external;

    /**
     * @notice Get the subscription address of a given registrant, if any.
     */
    function subscriptionOf(address addr) external returns (address registrant);

    /**
     * @notice Get the set of addresses subscribed to a given registrant.
     *         Note that order is not guaranteed as updates are made.
     */
    function subscribers(address registrant) external returns (address[] memory);

    /**
     * @notice Get the subscriber at a given index in the set of addresses subscribed to a given registrant.
     *         Note that order is not guaranteed as updates are made.
     */
    function subscriberAt(address registrant, uint256 index) external returns (address);

    /**
     * @notice Copy filtered operators and codeHashes from a different registrantToCopy to addr.
     */
    function copyEntriesOf(address registrant, address registrantToCopy) external;

    /**
     * @notice Returns true if operator is filtered by a given address or its subscription.
     */
    function isOperatorFiltered(address registrant, address operator) external returns (bool);

    /**
     * @notice Returns true if the hash of an address's code is filtered by a given address or its subscription.
     */
    function isCodeHashOfFiltered(address registrant, address operatorWithCode) external returns (bool);

    /**
     * @notice Returns true if a codeHash is filtered by a given address or its subscription.
     */
    function isCodeHashFiltered(address registrant, bytes32 codeHash) external returns (bool);

    /**
     * @notice Returns a list of filtered operators for a given address or its subscription.
     */
    function filteredOperators(address addr) external returns (address[] memory);

    /**
     * @notice Returns the set of filtered codeHashes for a given address or its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredCodeHashes(address addr) external returns (bytes32[] memory);

    /**
     * @notice Returns the filtered operator at the given index of the set of filtered operators for a given address or
     *         its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredOperatorAt(address registrant, uint256 index) external returns (address);

    /**
     * @notice Returns the filtered codeHash at the given index of the list of filtered codeHashes for a given address or
     *         its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredCodeHashAt(address registrant, uint256 index) external returns (bytes32);

    /**
     * @notice Returns true if an address has registered
     */
    function isRegistered(address addr) external returns (bool);

    /**
     * @dev Convenience method to compute the code hash of an arbitrary contract
     */
    function codeHashOf(address addr) external returns (bytes32);
}
"
    },
    "operator-filter-registry/src/upgradeable/OperatorFiltererUpgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import {IOperatorFilterRegistry} from "../IOperatorFilterRegistry.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/**
 * @title  OperatorFiltererUpgradeable
 * @notice Abstract contract whose constructor automatically registers and optionally subscribes to or copies another
 *         registrant's entries in the OperatorFilterRegistry when the init function is called.
 * @dev    This smart contract is meant to be inherited by token contracts so they can use the following:
 *         - `onlyAllowedOperator` modifier for `transferFrom` and `safeTransferFrom` methods.
 *         - `onlyAllowedOperatorApproval` modifier for `approve` and `setApprovalForAll` methods.
 */
abstract contract OperatorFiltererUpgradeable is Initializable {
    /// @notice Emitted when an operator is not allowed.
    error OperatorNotAllowed(address operator);

    IOperatorFilterRegistry constant OPERATOR_FILTER_REGISTRY =
        IOperatorFilterRegistry(0x000000000000AAeB6D7670E522A718067333cd4E);

    /// @dev The upgradeable initialize function that should be called when the contract is being upgraded.
    function __OperatorFilterer_init(address subscriptionOrRegistrantToCopy, bool subscribe)
        internal
        onlyInitializing
    {
        // If an inheriting token contract is deployed to a network without the registry deployed, the modifier
        // will not revert, but the contract will need to be registered with the registry once it is deployed in
        // order for the modifier to filter addresses.
        if (address(OPERATOR_FILTER_REGISTRY).code.length > 0) {
            if (!OPERATOR_FILTER_REGISTRY.isRegistered(address(this))) {
                if (subscribe) {
                    OPERATOR_FILTER_REGISTRY.registerAndSubscribe(address(this), subscriptionOrRegistrantToCopy);
                } else {
                    if (subscriptionOrRegistrantToCopy != address(0)) {
                        OPERATOR_FILTER_REGISTRY.registerAndCopyEntries(address(this), subscriptionOrRegistrantToCopy);
                    } else {
                        OPERATOR_FILTER_REGISTRY.register(address(this));
                    }
                }
            }
        }
    }

    /**
     * @dev A helper modifier to check if the operator is allowed.
     */
    modifier onlyAllowedOperator(address from) virtual {
        // Allow spending tokens from addresses with balance
        // Note that this still allows listings and marketplaces with escrow to transfer tokens if transferred
        // from an EOA.
        if (from != msg.sender) {
            _checkFilterOperator(msg.sender);
        }
        _;
    }

    /**
     * @dev A helper modifier to check if the operator approval is allowed.
     */
    modifier onlyAllowedOperatorApproval(address operator) virtual {
        _checkFilterOperator(operator);
        _;
    }

    /**
     * @dev A helper function to check if the operator is allowed.
     */
    function _checkFilterOperator(address operator) internal view virtual {
        // Check registry code length to facilitate testing in environments without a deployed registry.
        if (address(OPERATOR_FILTER_REGISTRY).code.length > 0) {
            // under normal circumstances, this function will revert rather than return false, but inheriting or
            // upgraded contracts may specify their own OperatorFilterRegistry implementations, which may behave
            // differently
            if (!OPERATOR_FILTER_REGISTRY.isOperatorAllowed(address(this), operator)) {
                revert OperatorNotAllowed(operator);
            }
        }
    }
}
