    "@layerzerolabs/lz-evm-oapp-v2/contracts/standards/precrime/interfaces/IPreCrimeV2.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1

pragma solidity >=0.8.0;

struct PreCrimePeer {
    uint32 eid;
    bytes32 precrime;
    bytes32 oapp;
}

interface IPreCrimeV2 {
    error OnlyOffChain();

    // for simulate()
