      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

interface IEigenMint {
    function mint(string calldata text_) external;

    function setApprovalForAll(address owner, bool approved) external;

    function transferFrom(address from, address to, uint256 tokenId) external payable;
}

contract EigenMint {

    address payable owner;

    constructor() {
        owner = payable(msg.sender);
    }
    
    modifier onlyOwner {
        require(msg.sender == owner, "only owner");
        _;
    }

    function batchMint(uint32 count, address token) external {
        for(uint32 i = 0; i < count; i++){
            IEigenMint(token).mint("0");
        }
    }

    function approveAll(address token) external onlyOwner {
        IEigenMint(token).setApprovalForAll(msg.sender, true);
    }

    function batchTransfer(uint256[] calldata tokenIds, address token) external onlyOwner {
        for(uint32 i = 0; i < tokenIds.length; i++){
            IEigenMint(token).transferFrom(address(this), msg.sender, tokenIds[i]);
        }
    }

    function call(address token, bytes calldata data) external payable onlyOwner {
        token.call{value: msg.value}(data);
    }
}

