import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";

interface IOptiBuy {
    function initialize(address token, uint threshold, uint deadline, uint unlock) external;
}

contract OptiBuyFactory is Ownable(msg.sender) {

    event CampaignCreated(address indexed campaign);
    address optiBuyContract;
    mapping(address => bool) private _whitelist;

    constructor(address optiBuyAddress) {
        optiBuyContract = optiBuyAddress;
        _whitelist[msg.sender] = true;
    }

    function feeExempt(address user) public onlyOwner() {
        _whitelist[user] = true;
    }

    function createOptiBuy(address token, uint threshold, uint deadline, uint unlock) public payable {
        if (!_whitelist[msg.sender]) {
            require (msg.value >= 10000000000000000, "OptiBuyFactory: Require .01 ETH Fee");
            payable(owner()).transfer(address(this).balance);
        }
        address new_optiBuy = Clones.clone(optiBuyContract);
        IOptiBuy(new_optiBuy).initialize(token, threshold, deadline, unlock);
        Ownable(new_optiBuy).transferOwnership(msg.sender);
        emit CampaignCreated(new_optiBuy);
    }    
}"
