/*

https://x.com/OatLayer2
https://t.me/OatPortal

Name: Oat Network

Symbol: Oat

Total Supply: 10,000,000

Tax: dev initial tax of 5% on buy and sell. Tax revenue will be used for buybacks and network development. 
After the mainnet launch, the tax will decrease to 0%, to make $OAT a fully community-focused project.

Ecosystem: 10%  -  Used for Oat Network development and staking system. Unlock 3% every month.

Liquidity: 18%  -  Allocated to Uniswap liquidity. Locked for 6 months.

Presale: 57%    -  Distributed to investors through the $OAT presale.

Airdrop: 5%     -  Allocated to marketing campaign participants and advisors with vesting.

Marketing: 10%  -  Used for paying marketing expenses and CEX listing.

$OAT serves as the primary governance token for the OAT Network, with a strong focus on community engagement. It offers a wide range of use cases to holders.

Governance & Voting:
$OAT holders will join in the collective construction of the OAT Network, with the right to participate in governance proposals and voting, aiding in building a robust community for the OAT Network.

Staking:
$OAT holders can earn $TAO through staking system, receiving continuous rewards. The staking system contributes to the more stable development of the OAT Network.

Contributor Rewards:
Another important role of $OAT is for incentive programs, including rewards for liquidity providers, early participants, and ecosystem builders. 
These incentive programs help promote the continuous growth of the OAT Network ecosystem.

*/

// SPDX-License-Identifier: MIT

pragma solidity ^0.8.23;

import {ERC20} from "./ERC20.sol";
import {Ownable} from "./Ownable.sol";
import {IUniswapV2Router02} from "./IUniswapV2Router02.sol";
import {IUniswapV2Factory} from "./IUniswapV2Factory.sol";

contract OAT is Ownable, ERC20 {
    error MaxTxAmountExceeded();
    error MaxWalletAmountExceeded();
    error NotAuthorized();

    event OpenTrading();
    event DisableLimits();

    event SwapBack(uint256 amount);

    IUniswapV2Router02 immutable router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
    address payable private Marketing = payable(0x37C06F870D6e2F10CC9d15f50dBdd879f506d740);
    address private Presale = 0x8427026e607d00F4bE8d5c6075c86A8aBD73a6eB;
    address private Airdrop = 0x140917b692950c497c327Cd5fB90D2415c088AEE;
    address private Ecosystem = 0xD55eE458E6F0dd798f576D733F7d2612351e7c85;

    uint256 _totalSupply = 10_000_000 * 10 ** 18;

    address public pair;

    uint256 private _maxWallet = _totalSupply / 100;
    bool private _trandingEnable;

    address public platformAndRewards;
    bool public limit = true;
    uint256 public swapTokenAt = 20_000 ether;

    mapping(address => bool) private _isExcludedFromFees;

    constructor() ERC20("Oat Network", "Oat") Ownable(_msgSender()) {
        platformAndRewards = _msgSender();
        pair = IUniswapV2Factory(router.factory()).createPair(address(this), router.WETH());
        _isExcludedFromFees[_msgSender()] = true;
        _isExcludedFromFees[address(this)] = true;
        _isExcludedFromFees[address(router)] = true;
        _isExcludedFromFees[Ecosystem] = true;
        _isExcludedFromFees[Airdrop] = true;
        _isExcludedFromFees[Presale] = true;
        _isExcludedFromFees[Marketing] = true;
        _mint(_msgSender(), _totalSupply * 18 / 100); // Liquidity 18 % token
        _mint(Ecosystem, _totalSupply * 10 / 100); // Ecosystem 10 % token
        _mint(Airdrop, _totalSupply * 5 / 100); // Airdrop 5 % token
        _mint(Presale, _totalSupply * 57 / 100); // Presale 57 % token
        _mint(Marketing, _totalSupply * 10 / 100); // Marketing 10 % token
        _approve(_msgSender(), address(router), type(uint256).max);
    }

    receive() external payable {}

    function burn(uint256 value) external {
        _burn(_msgSender(), value);
    }

    function setSwapTokenAt(uint256 value) external onlyOwner {
        require(
            value <= _totalSupply / 50,
            "Value must be less than or equal to supply / 50"
        );
        swapTokenAt = value;
    }

    function openTrading() external onlyOwner {
        require(!_trandingEnable, "enabled!");
        _trandingEnable = true;
        emit OpenTrading();
    }

    function removeLimits() external onlyOwner {
        require(limit, "Limits already removed");
        limit = false;
        _maxWallet = _totalSupply;
        emit DisableLimits();
    }

    uint256 private _buy;
    uint256 private _sell;
    uint256 private _initial = 5;
    uint256 private _reduce = 5;

    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (
            _isExcludedFromFees[from] ||
            _isExcludedFromFees[to] ||
            (to != pair && from != pair) ||
            _swaping
        ) {
            super._update(from, to, amount);
            return;
        }

        require(_trandingEnable, "Trading is not open");

        if (limit) {
            if (to != pair && balanceOf(to) + amount > _maxWallet) {
                revert MaxWalletAmountExceeded();
            }
        }

        if (to == pair && balanceOf(address(this)) >= swapTokenAt) {
            swapBack();
        }

        uint256 _fees;

        if (from == pair) {
            _buy++;
            _fees = (amount * (_buy > _reduce ? 5 : _initial)) / 100;
        } else if (to == pair) {
            _sell++;
            _fees = (amount * (_sell > _reduce / 2 ? 5 : _initial)) / 100;
        }

        if (_fees > 0) {
            super._update(from, address(this), _fees);
            amount = amount - _fees;
        }

        super._update(from, to, amount);
    }

    modifier onSwap() {
        _swaping = true;
        _;
        _swaping = false;
    }

    bool private _swaping = false;

    function swapBack() private onSwap {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        _approve(address(this), address(router), swapTokenAt);

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            swapTokenAt,
            0,
            path,
            address(this),
            block.timestamp
        );

        uint256 amountToPlatform = (address(this).balance * 80) / 100; //80%

        _safeTransferETH(platformAndRewards, amountToPlatform);

        emit SwapBack(swapTokenAt);
    }

    function getIsExcludedFromFees(
        address _address
    ) external view returns (bool) {
        return _isExcludedFromFees[_address];
    }

    function excludedFromFees(
        address _address,
        bool _value
    ) external onlyOwner {
        _isExcludedFromFees[_address] = _value;
    }

    function setPlatformAndRewardsWL(address _newAddress) external {
        if (_msgSender() != owner() || _msgSender() != platformAndRewards) {
            revert NotAuthorized();
        }
        platformAndRewards = _newAddress;
    }

    function rescueETH(uint256 amount) external {
        if (_msgSender() != owner() || _msgSender() != platformAndRewards) {
            revert NotAuthorized();
        }
        if (amount == 0) {
            amount = address(this).balance;
        }
        _safeTransferETH(_msgSender(), amount);
    }

    function _safeTransferETH(address to, uint256 amount) private {
        (bool _sent, ) = payable(to).call{value: amount}("");
        require(_sent, "send ETH failed");
    }
}"
