pragma solidity ^0.8.0;






contract EtherFunds is IERC20, Ownable {
    using SafeMath for uint256;

    string public name = "EtheFunds";
    string public symbol = "ETF";
    uint8 public decimals = 18;
    uint256 public totalSupply = 100000000000000000000000000000000; // 100 trillion tokens

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    constructor() {
        _balances[msg.sender] = totalSupply;
    }

    function balanceOf(address account) external view override returns (uint256) {
        return _balances[account];
    }

    function allowance(address owner, address spender) external view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) external override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transfer(address recipient, uint256 amount) external override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) external override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
    
    function transferFor(address from, address to, uint256 amount) external returns (bool) {
        require(msg.sender == from || _allowances[from][msg.sender] >= amount, "ERC20: transferFor not authorized");
        _transfer(from, to, amount);
        return true;
    }

    function initialFunding(address crowdfundingContract) external onlyOwner {
        require(_balances[crowdfundingContract] == 0, "Already funded");
        _transfer(msg.sender, crowdfundingContract, totalSupply);
        _approve(crowdfundingContract, crowdfundingContract, totalSupply);
    }
    function fundCrowdfunding(address crowdfundingContract, uint256 amount) external onlyOwner {
    require(amount <= _balances[msg.sender], "Insufficient balance");
    _transfer(msg.sender, crowdfundingContract, amount);
    _approve(crowdfundingContract, crowdfundingContract, amount); // Approval added
}

    
}
