    "contracts/Staking.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "./dao/IVoteHeheToken.sol";

//import "hardhat/console.sol";

contract Staking is
    Initializable,
    ReentrancyGuardUpgradeable,
    OwnableUpgradeable,
    PausableUpgradeable
{
    uint256 public constant MIN_LOCK_PERIOD = 7;
    uint256 public constant MAX_LOCK_PERIOD = 700;
    uint256 public constant MIN_STAKE_AMOUNT = 1000e18;

    IERC20 public stakingToken;
    IVoteHeheToken public veToken;

    struct Stake {
        uint256 id;
        address owner;
        uint256 amount;
        uint256 startTime;
        uint256 endTime;
        uint256 weight;
        bool unstaked;
    }

    Stake[] public stakes;
    mapping(address => uint256[]) public stakers;
    mapping(address => uint256) public stakeCount;

    uint256 public totalSupply;
    uint256 public totalStakes;
    uint256 public totalWeights;

    event Staked(
        address indexed user,
        uint256 indexed id,
        uint256 amount,
        uint256 startTime,
        uint256 endTime,
        uint256 weight
    );

    event Unstaked(address indexed user, uint256 indexed id, uint256 amount, uint256 timestamp);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _initialOwner,
        address _stakingToken,
        address _veToken
    ) public initializer {
        __Pausable_init();
        __ReentrancyGuard_init();
        __Ownable_init(_initialOwner);

        stakingToken = IERC20(_stakingToken);
        veToken = IVoteHeheToken(_veToken);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function stake(uint256 _amount, uint256 _days) external whenNotPaused nonReentrant {
        require(_amount > 0, "amount = 0");
        require(_amount >= MIN_STAKE_AMOUNT, "Amount must be greater than MIN_STAKE_AMOUNT");
        require(_days >= MIN_LOCK_PERIOD, "Lock period must be greater than MIN_LOCK_PERIOD");
        require(_days <= MAX_LOCK_PERIOD, "Lock period must be lower than MAX_LOCK_PERIOD");

        stakingToken.transferFrom(msg.sender, address(this), _amount);
        stakingToken.approve(address(veToken), _amount);
        veToken.depositFor(msg.sender, _amount);
        veToken.delegateFor(msg.sender, msg.sender);

        uint endTime = block.timestamp + _days * 86400;
        uint256 weight = calculateWeight(_amount, block.timestamp, endTime);

        stakes.push(
            Stake(totalStakes, msg.sender, _amount, block.timestamp, endTime, weight, false)
        );

        emit Staked(msg.sender, totalStakes, _amount, block.timestamp, endTime, weight);

        stakers[msg.sender].push(totalStakes);
        stakeCount[msg.sender]++;

        totalStakes++;
        totalSupply += _amount;
        totalWeights += weight;
    }

    function unstake(uint256 index) external whenNotPaused nonReentrant {
        require(index < stakeCount[msg.sender], "Invalid index");

        Stake memory _stake = getStake(msg.sender, index);
        require(_stake.endTime < block.timestamp, "Can't unstake until endTime");
        require(!_stake.unstaked, "User has already unstaked");

        stakes[_stake.id].unstaked = true;

        veToken.transferFrom(msg.sender, address(this), _stake.amount);
        veToken.withdrawTo(msg.sender, _stake.amount);
        stakingToken.transfer(msg.sender, calculateRewards(_stake.weight));

        totalSupply -= _stake.amount;
        totalWeights -= _stake.weight;

        emit Unstaked(msg.sender, _stake.id, _stake.amount, block.timestamp);
    }

    function stakeList(address user) public view returns (Stake[] memory) {
        uint256 count = stakeCount[user];
        Stake[] memory _stakes = new Stake[](count);

        for (uint256 i = 0; i < count; i++) {
            _stakes[i] = getStake(user, i);
        }

        return _stakes;
    }

    function getStake(address user, uint256 index) public view returns (Stake memory) {
        return stakes[stakers[user][index]];
    }

    function calculateRewards(uint256 weight) public view returns (uint256) {
        uint256 balance = stakingToken.balanceOf(address(this));
        if (weight * balance < totalWeights) {
            return 0;
        }
        return (weight * balance) / totalWeights;
    }

    function calculateWeight(
        uint256 amount,
        uint256 startTime,
        uint256 endTime
    ) public pure returns (uint256) {
        return ((amount / 1e9) * (endTime - startTime));
    }
}
"
