    "contracts/Staking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/// @title MetriaStaking
/// @author jveer634
/// @notice A locked tenure staking contract for the METR token
contract MetriaStaking is Ownable, ReentrancyGuard, Pausable {
    IERC20 public stakingToken;
    IERC20 public rewardToken;

    bool public rewardsLoaded;

    uint public duration;
    uint public maxStakeAmount;
    uint public rewards;
    uint public interestRate;
    uint public totalStaked;

    struct Stake {
        uint amount;
        uint stakedAt;
        uint lastWithdrawn;
        bool isWithdrawn;
    }

    mapping(address => uint8) public stakesCount;
    mapping(address => mapping(uint8 => Stake)) public stakes;

    event RewardsLoaded(uint at);
    event Staked(address by, uint amount, uint timestamp);

    event Withdrawn(address by, uint amount, uint timestamp);
    event InterestCollected(
        address indexed user,
        uint indexed stakeId,
        uint interest
    );

    constructor(
        address _stakingToken,
        address _rewardToken,
        uint _duration,
        uint _rewards,
        uint _interestRate,
        uint _maxAmount,
        address _owner
    ) Ownable(_owner) {
        stakingToken = IERC20(_stakingToken);
        rewardToken = IERC20(_rewardToken);
        duration = _duration;
        rewards = _rewards;
        interestRate = _interestRate;
        maxStakeAmount = _maxAmount;
    }

    /// @notice to load reward tokens into the contract by the owner
    /// @dev token of amount `rewards` must be approved before calling the function
    function load() external onlyOwner {
        require(rewardsLoaded == false, "Rewards already deposited");
        rewardsLoaded = true;
        rewardToken.transferFrom(msg.sender, address(this), rewards);
        emit RewardsLoaded(block.timestamp);
    }

    /// @notice owner function to stop the staking
    /// @dev only contract owner can call this function
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice owner function to resume the staking
    /// @dev only contract owner can call this function
    function unpause() external onlyOwner {
        _unpause();
    }

    /// @notice to stake the METR tokens
    /// @dev token approval of `amount` is required
    /// @param amount amount of tokens to stake
    function stake(uint amount) external nonReentrant whenNotPaused {
        uint timestamp = block.timestamp;
        address caller = _msgSender();
        require(amount > 0, "NetriaStaking: Invalid amount");
        require(
            totalStaked + amount <= maxStakeAmount,
            "MetriaStaking: max capacity already staked"
        );

        uint8 count = stakesCount[caller];

        stakes[caller][count] = Stake({
            amount: amount,
            stakedAt: timestamp,
            lastWithdrawn: timestamp,
            isWithdrawn: false
        });

        stakesCount[caller] += 1;
        stakingToken.transferFrom(caller, address(this), amount);
        totalStaked += amount;

        emit Staked(caller, amount, timestamp);
    }

    /// @notice to withdraw the stake after the tenure is completed
    /// @dev it will return both tenure and interest accrued
    /// @param id id of the stake to unstake
    function withdraw(uint8 id) external nonReentrant whenNotPaused {
        address caller = _msgSender();
        Stake memory s = stakes[caller][id];
        require(!s.isWithdrawn, "MetriaStaking: Already Withdrawn");
        uint timestamp = block.timestamp;

        require(
            timestamp >= s.stakedAt + duration,
            "MetriaStaking: Tenure not completed"
        );

        stakes[caller][id].isWithdrawn = true;
        stakingToken.transfer(caller, s.amount);

        emit Withdrawn(caller, s.amount, timestamp);
    }

    function _calculatInterest(uint8 id) internal view returns (uint) {
        address caller = _msgSender();
        Stake memory s = stakes[caller][id];
        uint timestamp = block.timestamp;
        uint endTime = s.stakedAt + duration;

        if (s.lastWithdrawn >= endTime) return 0;

        if (endTime <= timestamp) timestamp = endTime;

        uint timeCovered = timestamp - s.lastWithdrawn;

        if (timeCovered > duration) timeCovered = duration;
        // effectiveInterestRate = (interestRate * timeCovered) / duration;
        // interest = (s.amount * effectiveInterest ) / 100;
        uint interest = (s.amount * interestRate * timeCovered) /
            (duration * 100);

        return interest;
    }

    function collectInterest(uint8 id) external nonReentrant {
        address caller = _msgSender();
        uint interest = _calculatInterest(id);
        stakes[caller][id].lastWithdrawn = block.timestamp;

        rewardToken.transfer(caller, interest);

        emit InterestCollected(caller, id, interest);
    }

    function getRewardsGenerated(uint8 id) external view returns (uint) {
        return _calculatInterest(id);
    }

    /// @notice utility function to get the total amount of tokens staked by the user
    /// @param user address of the user
    /// @return amount total amount of tokens in the active stakes of the user
    function getTotalStaked(address user) external view returns (uint) {
        uint count = stakesCount[user];
        Stake memory s;
        uint amount = 0;
        for (uint8 i = 0; i < count; i++) {
            s = stakes[user][i];
            if (!s.isWithdrawn) amount += s.amount;
        }
        return amount;
    }

    /// @notice utility functios to get all the stakes made by the user
    /// @param user address of the user
    /// @return s array of the stakes made by the user
    function getStakes(address user) external view returns (Stake[] memory) {
        uint count = stakesCount[user];
        Stake[] memory s = new Stake[](count);
        for (uint8 i = 0; i < count; i++) {
            s[i] = stakes[user][i];
        }
        return s;
    }
}
