contract P2PTradingManager is Ownable {
    // Struct to represent a trade
    struct Order {
        address seller; // Address of the seller
        address buyer; // Address of the buyer (0x0 if not yet bought)
        address tokenAddress; // Address of the ERC-20 token being traded
        uint256 tokenAmount; // Amount of the token being traded
        uint256 price; // Price of the token in ETH
        bool isCompleted; // Flag indicating if the trade is completed
        uint256 startTime; // Start time for the trade
        uint256 endTime; // End time for the trade
    }

    // Mapping of trade IDs to Trade objects
    mapping(string => Order) public orders;
    uint256 private constant orderIdLength = 3;
    uint256 public orderCount = 0;
    string private constant alphabet =
        "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxzyz0123456789";

    uint256 public ORGANIZATION_PERCENTAGE = 100;
    uint256 PERCENTAGE_DENOMINATOR = 10000;

    // Events to track trade-related activities
    event OrderCreated(
        string orderId,
        address indexed seller,
        address indexed token,
        uint256 tokenAmount,
        uint256 price,
        uint256 startTime,
        uint256 endTime
    );
    event OrderCompleted(string orderId, address indexed buyer);
    event CancelOrder(string orderId, address indexed seller);

    /**
     * @dev Modifier to restrict a function to the trade seller only.
     * @param _orderId The ID of the trade.
     */
    modifier onlySeller(string memory _orderId) {
        require(
            msg.sender == orders[_orderId].seller,
            "Only trade seller can call this"
        );
        _;
    }

    function AppendString(
        string memory a,
        string memory b
    ) internal pure returns (string memory) {
        return string.concat(a, b);
    }

    function generateOrderId() internal view returns (string memory) {
        bytes memory result = new bytes(orderIdLength);
        for (uint256 i = 0; i < orderIdLength; i++) {
            uint8 index = uint8(
                uint256(
                    keccak256(abi.encodePacked(orderCount, block.timestamp, i))
                ) % bytes(alphabet).length
            );
            result[i] = bytes(alphabet)[index];
        }

        string memory hashString = string(result);

        string memory orderId = AppendString(
            hashString,
            Strings.toString(orderCount)
        );

        return orderId;
