     * @param _startTime Start time for the trade.
     * @param _endTime End time for the trade.
     */
    function createOrder(
        address _tokenAddress,
        uint256 _tokenAmount,
        uint256 _price,
        uint256 _startTime,
        uint256 _endTime
    ) external {
        require(_price > 0, "Price must be greater than 0");
        require(_tokenAmount > 0, "Token amount must be greater than 0");
        require(_endTime > block.timestamp, "End time must be in the future");
        require(
            _endTime > _startTime,
            "End time must be greater than start time"
        );
        string memory orderId = generateOrderId();

        IERC20 token = IERC20(_tokenAddress);
        require(
            token.transferFrom(msg.sender, address(this), _tokenAmount),
            "Token transfer failed"
        );

        orders[orderId] = Order({
            seller: msg.sender,
            buyer: address(0),
            tokenAddress: _tokenAddress,
            tokenAmount: _tokenAmount,
            price: _price,
            isCompleted: false,
            startTime: _startTime,
            endTime: _endTime
        });

        emit OrderCreated(
            orderId,
            msg.sender,
            _tokenAddress,
            _tokenAmount,
            _price,
            _startTime,
            _endTime
        );
        orderCount++;
    }

    /**
     * @dev Allows a buyer to purchase a token from a trade.
     * @param _orderId The ID of the trade.
     */
    function buy(string memory _orderId) external payable {
        Order storage order = orders[_orderId];
        uint256 ethPrice = order.price;

        uint256 organizationFees = (ORGANIZATION_PERCENTAGE * order.price) /
            PERCENTAGE_DENOMINATOR;
        require(order.buyer == address(0), "Item already sold");
        require(msg.value == order.price, "Incorrect amount sent");
        require(
            block.timestamp >= order.startTime,
            "Trade has not started yet"
        );
        require(block.timestamp <= order.endTime, "Trade has expired");

        IERC20 token = IERC20(order.tokenAddress);
        require(
            token.transfer(msg.sender, order.tokenAmount),
            "Token transfer to buyer failed"
        );

        order.buyer = msg.sender;
        order.isCompleted = true;

        ethPrice = ethPrice - organizationFees;

        (bool successPriceTransfer, ) = order.seller.call{value: ethPrice}("");

        (bool suceessFeesTransfer, ) = owner().call{value: organizationFees}(
            ""
        );

        require(successPriceTransfer, "ETH transfer to seller failed");
        require(
            suceessFeesTransfer,
            "ETH fees transfer to organization failed"
        );
        emit OrderCompleted(_orderId, msg.sender);
    }

    /**
     * @dev Allows the seller to cancel an active trade order.
     * @param _orderId The ID of the trade order.
     */
    function cancelOrder(string memory _orderId) external onlySeller(_orderId) {
        Order storage order = orders[_orderId];
        require(!order.isCompleted, "Trade is already completed");

        IERC20 token = IERC20(order.tokenAddress);

        require(
            token.transfer(order.seller, order.tokenAmount),
            "Token transfer to seller failed"
        );

        order.isCompleted = true;

        emit CancelOrder(_orderId, order.seller);
    }

    /**
     * @dev Allows to check the order is expired or not
     * @param _orderId The ID of the trade order.
     */
    function isExpired(string memory _orderId) public view returns (bool) {
        return orders[_orderId].endTime < block.timestamp;
    }

    /**
     * @dev get Order by order id
     * @param _orderId The ID of the trade order.
     */
    function getOrder(
        string memory _orderId
    ) public view returns (Order memory) {
        return orders[_orderId];
    }

    function setOrganziationPercentage(uint256 percentage) external onlyOwner {
        require(percentage < 100, "value can not be 100% or greater");

        ORGANIZATION_PERCENTAGE = percentage;
    }
}
