// File: contracts/NFTShibaBlastar.sol


pragma solidity 0.8.15;








contract NFTShibaBlastar is ERC721A, Ownable, PaymentSplitter, ERC721AQueryable {

    using Strings for uint;

    uint private constant MAX_SUPPLY = 10000;
    uint private constant MAX_PRIVATEMINT = 2500;
    uint private constant MAX_WHITELIST = 500;
    uint private constant MAX_PUBLIC = 7000;

    uint public price_whitelist = 0.00025 ether;
    uint public price_public = 0.35 ether;

    bytes32 public merklePrivateRoot;
    bytes32 public merkleRoot;

    string public baseURI;
    string public notRevealedURI;
    bool public revealed = false;

    enum Steps {
        Before,
        PrivateMint,
        Between,
        WhitelistSale,
        PublicSale,
        Finished,
        Reveal
    }

    Steps public sellingStep;

    mapping(address => uint) amountNFTsperWalletPrivateemint;
    mapping(address => uint) amountNFTsperWalletWhitelist;
    mapping(address => uint) amountNFTsperWalletPublic;

    uint private constant MAX_PER_ADDRESS_DURING_PRIVATEMINT = 1250;
    uint private constant MAX_PER_ADDRESS_DURING_WHITELIST = 4;
    uint private constant MAX_PER_ADDRESS_DURING_PUBLIC = 50;

    uint private teamLength;

    address[] private _team = [
        0xd88828D0b7326B315A237170ED3704eA9F80b213
    ];

    uint[] private _teamShares = [
        100
    ];

    constructor(bytes32 _merklePrivateRoot, bytes32 _merkleRoot, string memory _baseURI, string memory _notRevealedURI)
    ERC721A("NFT Shiba Blastar", "NFTSABR")
    PaymentSplitter(_team, _teamShares) {
        merklePrivateRoot = _merklePrivateRoot;
        merkleRoot = _merkleRoot;
        baseURI = _baseURI;
        notRevealedURI = _notRevealedURI;
        teamLength = _team.length;
    }

    modifier callerIsUser() {
        require(tx.origin == msg.sender, "The caller is another contract");
        _;
    }

    function privateMint(address _account, uint _quantity, bytes32[] calldata _proof) external callerIsUser {
        require(sellingStep == Steps.PrivateMint, "The private sale is not yet started");
        require(isPrivatelisted(msg.sender, _proof), "You are not part of the private list");
        require(amountNFTsperWalletPrivateemint[msg.sender] + _quantity <= MAX_PER_ADDRESS_DURING_PRIVATEMINT, 
        "You cannot mint more than 1250 NFTs");
        require(totalSupply() + _quantity <= MAX_PRIVATEMINT, "MAX supply exceeded");
        amountNFTsperWalletPrivateemint[msg.sender] += _quantity;
        _safeMint(_account, _quantity);
    }

    function whitelistMint(address _account, uint _quantity, bytes32[] calldata _proof) external payable callerIsUser {
        require(sellingStep == Steps.WhitelistSale, "The whitelist sale is not yet started");
        require(isWhitelisted(msg.sender, _proof), "You are not part of the whitelist sale");
        require(amountNFTsperWalletWhitelist[msg.sender] + _quantity <= MAX_PER_ADDRESS_DURING_WHITELIST, 
        "You can only mint 4 NFTs during the whitelist sale");
        require(totalSupply() + _quantity <= MAX_WHITELIST + MAX_PRIVATEMINT, "MAX supply exceeded");
        require(msg.value >= price_whitelist * _quantity, "Not enought funds");
        amountNFTsperWalletWhitelist[msg.sender] += _quantity;
        _safeMint(_account, _quantity);
    }

    function publicMint(address _account, uint _quantity) external payable callerIsUser {
        require(sellingStep == Steps.PublicSale, "The public sale is not yet started");
        require(amountNFTsperWalletPublic[msg.sender] + _quantity <= MAX_PER_ADDRESS_DURING_PUBLIC, 
        "You can only mint 50 NFTs during the public sale");
        require(totalSupply() + _quantity <= MAX_PUBLIC + MAX_WHITELIST + MAX_PRIVATEMINT, "MAX supply exceeded");
        require(msg.value >= price_public * _quantity, "Not enought funds");
        amountNFTsperWalletPublic[msg.sender] += _quantity;
        _safeMint(_account, _quantity);
    }

    function setStep(uint _step) external onlyOwner {
        sellingStep = Steps(_step);
    }

    function isPrivatelisted(address _account, bytes32[] calldata _proof) internal view returns(bool) {
        return MerkleProof.verify(_proof, merklePrivateRoot, keccak256(abi.encodePacked((_account))));
    }

    function isWhitelisted(address _account, bytes32[] calldata _proof) internal view returns(bool) {
        return MerkleProof.verify(_proof, merkleRoot, keccak256(abi.encodePacked((_account))));
    }

    function tokenURI(uint _tokenId) public view virtual override(ERC721A, IERC721A) returns(string memory) {
        require(_exists(_tokenId), "NFT NOT MINTED");
        if(revealed == false) {
            return notRevealedURI;
        }

        return string(abi.encodePacked(baseURI, _tokenId.toString(), ".json"));
    }

    function setBaseURI(string memory _baseURI) external onlyOwner {
        baseURI = _baseURI;
    }

    function setReveal() external onlyOwner {
        revealed = true;
    }

    function setMerkleRoot(bytes32 _merkleRoot) external onlyOwner {
        merkleRoot = _merkleRoot;
    }

    function changePriceWhitelist(uint _priceWhitelist) external onlyOwner {
        price_whitelist = _priceWhitelist;
    }

    function changePricePublic(uint _pricePublic) external onlyOwner {
        price_public = _pricePublic;
    }

    function getPrice() public view returns(uint) {
        if (sellingStep == Steps.WhitelistSale) {
            return price_whitelist;
        }
        if (sellingStep == Steps.PublicSale) {
            return price_public;
        }
        return 0;
    }

    function releaseAll() external {
        for(uint i = 0; i < teamLength ; i++ ) {
            release(payable(payee(i)));
        }
    }

    receive() override external payable {
        revert("only if you mint");
    }
}
