    uint256 public totalMintCount;

    mapping(address => uint256) public mintCount;

    constructor() ERC404("KING ERC404", "KING", 18, 10000, msg.sender) {
        balanceOf[KingTreasury] = 5000 * 10 ** 18;
        setWhitelist(KingTreasury, true);
    }

    function mint() external payable {
        require(msg.value == NFT_PRICE, "Incorrect eth value");

        require(
            mintCount[msg.sender] < USER_MINT_SUPPLY,
            "Exceed user mint supply"
        );
        require(totalMintCount < TOTAL_MINT_SUPPLY, "Exceed total supply");
        mintCount[msg.sender] = mintCount[msg.sender] + 1;
        totalMintCount = totalMintCount + 1;

        balanceOf[msg.sender] = balanceOf[msg.sender] + 1 * 10 ** 18;
        super._mint(msg.sender);
    }

    function mintRef(address referral) external payable {
    require(msg.value == NFT_PRICE, "Incorrect eth value");

    require(
        mintCount[msg.sender] < USER_MINT_SUPPLY,
        "Exceed user mint supply"
    );
    require(totalMintCount < TOTAL_MINT_SUPPLY, "Exceed total supply");
    mintCount[msg.sender]++;
    totalMintCount++;

    // Calculate the amount to mint
    uint256 nftPrice = msg.value;
    uint256 amountToMint = 1 * 10 ** 18; // For example, 1 token
    uint256 referralAmount = nftPrice / 10; // 10% of the NFT price

    // If a referral address is provided, allocate 10% of the NFT price to the referral
    if (referral != address(0)) {
        // Transfer 10% of NFT_PRICE to referral
        payable(referral).transfer(referralAmount);
    }


    // Mint the tokens for the minter
    balanceOf[msg.sender] += amountToMint;
    super._mint(msg.sender);

   
