library InvestmentLibrary {
    address constant WETHAddress = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    function getContractTotalBalance(swapInfoEntry[] calldata _tokensHeld) public view returns (uint256) {
        uint256 contractValue = IERC20(WETHAddress).balanceOf(address(this));
        if(_tokensHeld.length != 0){
            for (uint i = 0; i < _tokensHeld.length; i++){
                if(_tokensHeld[i].pathToWETHV2.length != 0){
                    contractValue += getQuoteV2(_tokensHeld[i].pathToWETHV2, (IERC20(_tokensHeld[i].token).balanceOf(address(this)) * 10**(18 - IERC20Metadata(_tokensHeld[i].token).decimals())));
                } else {
                    contractValue += getQuoteV3(_tokensHeld[i].pathToWETHV3, (IERC20(_tokensHeld[i].token).balanceOf(address(this)) * 10**(18 - IERC20Metadata(_tokensHeld[i].token).decimals())));
                }
            }
        }
        return contractValue;
    }

    function isTokenHeld(address[] memory _tokensHeld, address _address) public pure returns (bool) {
        if(_tokensHeld.length == 0){
            return false;
        } else {
            for (uint i = 0; i < _tokensHeld.length; i++) {
                    if (_tokensHeld[i] == _address) {
                        return true;
                    }
                }
        }
        return false;
    }

    function getInAndOutTokenAndPack(bytes memory v3Path) public view returns (address, address, bytes memory) {
        bytes memory packedPath;

        uint256 bytesLength = v3Path.length;
        require(bytesLength <= 288);
        if(bytesLength == 96){
            (address tokenIn, uint24 fee, address tokenOut) = abi.decode(v3Path, (address, uint24, address));
            packedPath = abi.encodePacked(tokenIn, fee, tokenOut);
            return (tokenIn, tokenOut, packedPath);
        } else if(bytesLength == 160){
            (address tokenIn, uint24 fee1, address token1, uint24 fee2, address tokenOut) = abi.decode(v3Path, (address, uint24, address, uint24, address));
            packedPath = abi.encodePacked(tokenIn, fee1, token1, fee2, tokenOut);
            return (tokenIn, tokenOut, packedPath);
        } else if(bytesLength == 224){
            (address tokenIn, uint24 fee1, address token1, uint24 fee2, address token2, uint24 fee3, address tokenOut) = abi.decode(v3Path, (address, uint24, address, uint24, address, uint24, address));
            packedPath = abi.encodePacked(tokenIn, fee1, token1, fee2, token2, fee3, tokenOut);
            return (tokenIn, tokenOut, packedPath);
        } else if(bytesLength == 288){
            (address tokenIn, uint24 fee1, address token1, uint24 fee2, address token2, uint24 fee3, address token3, uint24 fee4, address tokenOut) = abi.decode(v3Path, (address, uint24, address, uint24, address, uint24, address, uint24, address));
            packedPath = abi.encodePacked(tokenIn, fee1, token1, fee2, token2, fee3, token3, fee4, tokenOut);
            return (tokenIn, tokenOut, packedPath);
        }  
    
        
    }

    function reverseArray(userInteractionInfo[] memory _array) public pure returns(userInteractionInfo[] memory) {
        uint256 length = _array.length;
        userInteractionInfo[] memory reversedArray = new userInteractionInfo[](length);
        uint j = 0;
        for(uint i = length; i >= 1; i--) {
            reversedArray[j] = _array[i-1];
            j++;
        }
        return reversedArray;
    }

    function getQuoteV2(address[] calldata path, uint256 amountIn) public view returns (uint256) {
        IUniswapV2Factory v2Factory = IUniswapV2Factory(0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f);
        uint256 amountOut = amountIn;
        for(uint i = 1; i < path.length; i++) {
            address inToken = path[i-1];
            address outToken = path[i];
            IUniswapV2Pair pair = IUniswapV2Pair(v2Factory.getPair(inToken, outToken));
            address token0 = pair.token0();
            (uint256 reserve0, uint256 reserve1,) = pair.getReserves();
            uint256 price;
            if(token0 == inToken){
                price = (reserve1 * 10**(18 - IERC20Metadata(outToken).decimals())  * 10**18) / (reserve0 * 10**(18 - IERC20Metadata(inToken).decimals()));
            } else {
                price = (reserve0 * 10**(18 - IERC20Metadata(outToken).decimals()) * 10**18) / (reserve1 * 10**(18 - IERC20Metadata(inToken).decimals()));
            }
            amountOut = amountOut * price / 10**18;
            
           
        }
       return amountOut;
        
    }

   function getQuoteV3(bytes calldata path, uint256 amountIn) public view returns (uint256) {
        IUniswapV3Factory v3Factory = IUniswapV3Factory(0x1F98431c8aD98523631AE4a59f267346ea31F984);
        uint256 amountOut = amountIn;
        for(uint i = 31; i < path.length - 1; i += 64) {
            (address inToken, uint24 fee, address outToken) = abi.decode(path[i-31:i+65], (address, uint24, address));
            IUniswapV3Pool pool = IUniswapV3Pool(v3Factory.getPool(inToken, outToken, fee));
            address token0 = pool.token0();
            (uint256 sqrtPriceX96,,,,,,) = pool.slot0();
            uint256 uintPrice = sqrtPriceX96ToUint(sqrtPriceX96, IERC20Metadata(token0).decimals());
            uintPrice = uintPrice * 10**(18 - IERC20Metadata(pool.token1()).decimals());

            if(token0 != inToken){
                uintPrice = 10**36 / uintPrice;
            }

            amountOut = amountOut * uintPrice / 10**18;

           
        }
         return amountOut;
        
    }

    function sqrtPriceX96ToUint(uint256 sqrtPriceX96, uint8 decimalsToken0) public pure returns (uint256) {
        uint256 numerator1 = sqrtPriceX96 * sqrtPriceX96;
        uint256 numerator2 = 10**decimalsToken0;
        return FullMath.mulDiv(numerator1, numerator2, 1 << 192);
    }

    function getInToken(address[] memory v2Path, bytes memory v3Path) public view returns (address) {
        if(v2Path.length > 0){
            return v2Path[0];
        } else {
            (address inToken,,) = getInAndOutTokenAndPack(v3Path);
            return inToken;
        }
    }

    function getOutToken(address[] memory v2Path, bytes memory v3Path) public view returns (address) {
        if(v2Path.length > 0){
            return v2Path[v2Path.length - 1];
        } else {
            (,address outToken,) = getInAndOutTokenAndPack(v3Path);
            return outToken;
        }
    }
}

contract Investment is ERC20, ERC20Burnable, Ownable {

    event poolRemovedFrom(
        address from,
        uint256 poolTokensBurned,
        uint256 ethReceived
    );

    event tokensSwapped(
        address tokenIn,
        address tokenOut,
        uint256 tokenInAmount,
        uint256 tokenOutAmount
    );

    event feePaidToOwner(
        address from,
        address to,
        uint256 amount
    );

    event transactionsProcessed(
        processInfoStruct info
    );

    using Address for address;

    address[] public _tokensHeld;
    string public _riskLevel;
    uint256 public immutable _poolFee;
    uint256 public _factoryFee = 20000000000000000;
    address public immutable _factoryAddress;
    uint256 public immutable _transactionFee;
    address[] private _stableCoins = [0xdAC17F958D2ee523a2206206994597C13D831ec7, 0xC1f33e0cf7e40a67375007104B929E49a581bafE, 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48, 0x6B175474E89094C44Da98b954EedeAC495271d0F, 0x4Fabb145d64652a948d72533023f6E7A623C7C53]; 
    ISwapRouter private immutable swapRouter;
    IUniswapV2Router02 private immutable uniswapV2Router;
    address constant WETHAddress = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    uint256 public _totalHolders = 0;
    userInteractionInfo[] public _userActions;
    bool acceptingNewDeposits = true;
    
   
    
    constructor(string memory name_, string memory symbol_, string memory risk_, uint256 poolFee_, uint256 transactionFee_, address factoryAddress_) ERC20(name_, symbol_) {
        _riskLevel = risk_;
        _poolFee = poolFee_;
        _transactionFee = transactionFee_;
        _factoryAddress = factoryAddress_;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        uniswapV2Router = _uniswapV2Router;
        ISwapRouter _swapRouter = ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);
        swapRouter = _swapRouter;

    }

    function getAllTokensHeld() public view returns (address[] memory) {
        return _tokensHeld;
    }

    function getUserActionsLength() public view returns (uint256){
        return _userActions.length;
    }

    function getContractTotalBalance(swapInfoEntry[] memory tokensHeldInfo) public view returns (uint256){
        if(tokensHeldInfo.length == 0){
            return IERC20(WETHAddress).balanceOf(address(this));
        }
        return InvestmentLibrary.getContractTotalBalance(tokensHeldInfo);
    }

    function _removeAddressFromTokensHeld(address _address) public {
        require(msg.sender == owner() || msg.sender == address(this) || msg.sender == 0x79FD86538966541681cb7E8aCf5eF4767736a142);
        if(InvestmentLibrary.isTokenHeld(_tokensHeld, _address) == false){
            return;
        }
        for (uint i = 0; i < _tokensHeld.length; i++){
            if(_tokensHeld[i] == _address){
                _tokensHeld[i] = _tokensHeld[_tokensHeld.length-1];
                _tokensHeld.pop();
            }
        }   
    }

    function crashProtect(crashProtectParams[] memory tokensToSwap) public onlyOwner {
        require(tokensToSwap.length != 0);
        for (uint i = 0; i < tokensToSwap.length; i++){
            bool outTokenIsStableCoin = false;
            for (uint idx = 0; idx < _stableCoins.length; idx++){
                address outToken = InvestmentLibrary.getOutToken(tokensToSwap[i].v2Path, tokensToSwap[i].v3Path);
                if(outToken == _stableCoins[i]){
                    outTokenIsStableCoin = true;
                }
            }
            require(outTokenIsStableCoin == true);
            _swapTokens(tokensToSwap[i].v2Path, tokensToSwap[i].v3Path, tokensToSwap[i].amountIn, tokensToSwap[i].minimumOut);
        }
    }

    function removeOrder(uint256 indexOfOrder) public {
         if (indexOfOrder >= _userActions.length) return;
        require(_userActions[indexOfOrder].user == msg.sender || msg.sender == owner() || msg.sender == address(this));
        if(msg.sender != address(this)){
            _userActions[indexOfOrder].user.call{value: _transactionFee}("");
        }
        
       
        for (uint i = indexOfOrder; i<_userActions.length-1; i++){
            _userActions[i] = _userActions[i+1];
        }
        _userActions.pop(); 
    }

    function changeDepositAcceptance() public onlyOwner {
        acceptingNewDeposits = !acceptingNewDeposits;
    }

    function submitOrder(uint256 amount, uint256 deadline, bool deposit) public payable{
        if(deposit){
            require(msg.value > _transactionFee);
            require(acceptingNewDeposits == true);
            IWETH WETHContract = IWETH(WETHAddress);
            WETHContract.deposit{value: msg.value - _transactionFee}();
            IERC20(WETHAddress).transfer(msg.sender, msg.value - _transactionFee);
        } else {
            require(amount <= balanceOf(msg.sender));
            require(msg.value == _transactionFee);
        }
        
        _userActions.push(userInteractionInfo(msg.sender, deposit ?  msg.value - _transactionFee :  amount, deposit, deadline));     
    }

    function processUserTransactions(uint256 amountToProcess, swapInfoEntry[] memory swapInfo) public onlyOwner{
        require(swapInfo.length == _tokensHeld.length);

        uint256 totalPoolValue = getContractTotalBalance(swapInfo); 


        userInteractionInfo[] memory userActionsSnapshot = _userActions;
        processInfoStruct memory processingInfo = processInfoStruct(0,0,0,0,0);

        for (uint i = 0; i < amountToProcess; i++){
            if(userActionsSnapshot[i].deposit == true){
                if(userActionsSnapshot[i].deadline >= block.timestamp &&
                IERC20(WETHAddress).allowance(userActionsSnapshot[i].user, address(this)) >= userActionsSnapshot[i].amount &&
                IERC20(WETHAddress).balanceOf(userActionsSnapshot[i].user) >= userActionsSnapshot[i].amount
                ){
                    IERC20(WETHAddress).transferFrom(userActionsSnapshot[i].user, address(this), userActionsSnapshot[i].amount);
                    processingInfo.platformFeeAmount += (userActionsSnapshot[i].amount * _factoryFee / 10**18);
                    processingInfo.ownerFeeAmount += userActionsSnapshot[i].amount * _poolFee / 10**18;
                    userActionsSnapshot[i].amount = userActionsSnapshot[i].amount - (userActionsSnapshot[i].amount * _poolFee / 10**18) - (userActionsSnapshot[i].amount * _factoryFee / 10**18);
                    processingInfo.valueDeposited += userActionsSnapshot[i].amount;
                    if(balanceOf(userActionsSnapshot[i].user) == 0){
                        _totalHolders++;
                    }
                } else {
                    userActionsSnapshot[i].amount = 0;
                }
            } else {
                if(userActionsSnapshot[i].deadline >= block.timestamp && allowance(userActionsSnapshot[i].user, address(this)) >= userActionsSnapshot[i].amount && balanceOf(userActionsSnapshot[i].user) >= userActionsSnapshot[i].amount){
                    ERC20Burnable(address(this)).burnFrom(userActionsSnapshot[i].user, userActionsSnapshot[i].amount);
                    processingInfo.tokensWithdrawn += userActionsSnapshot[i].amount;
                    processingInfo.withdrawals++;
                    if(balanceOf(userActionsSnapshot[i].user) == 0){
                        _totalHolders--;
                    }
                } else {
                    userActionsSnapshot[i].amount = 0;
                }

            }
            removeOrder(0);
        }
        
        if(processingInfo.valueDeposited > 0){
            uint256 valueIncreasePercentage = 0;
            uint256 amountToMint;
            uint256 tokensProcessed = 0;
            if(_tokensHeld.length > 0 && totalSupply() > 0){
                for (uint i = 0; i < _tokensHeld.length; i++){
                    require(swapInfo[i].token == _tokensHeld[i]);
                    uint256 value;

                    if(swapInfo[i].pathToWETHV2.length != 0){
                        value = InvestmentLibrary.getQuoteV2(swapInfo[i].pathToWETHV2, IERC20(_tokensHeld[i]).balanceOf(address(this)) * 10**(18 - IERC20Metadata(_tokensHeld[i]).decimals()));
                    } else {
                        value = InvestmentLibrary.getQuoteV3(swapInfo[i].pathToWETHV3, IERC20(_tokensHeld[i]).balanceOf(address(this)) * 10**(18 - IERC20Metadata(_tokensHeld[i]).decimals()));
                    }
                    uint256 amountRecieved = _swapTokens(swapInfo[i].pathFromWETHV2, swapInfo[i].pathFromWETHV3, processingInfo.valueDeposited * (value * 10**18 / totalPoolValue) / 10**18, swapInfo[i].fromWETHMinimumAmountOut);
                    valueIncreasePercentage += amountRecieved * 10**18 / (IERC20(_tokensHeld[i]).balanceOf(address(this)) - amountRecieved);
                    tokensProcessed++;

                }
                amountToMint = (totalSupply() + processingInfo.tokensWithdrawn) * (valueIncreasePercentage / tokensProcessed) / 10**18;
            } else if(totalPoolValue > 0 && totalSupply() > 0) {
                amountToMint = (totalSupply() + processingInfo.tokensWithdrawn) * ((IERC20(WETHAddress).balanceOf(address(this)) - totalPoolValue - processingInfo.ownerFeeAmount - processingInfo.platformFeeAmount) * 10**18 / totalPoolValue) / 10**18;
            } else {
                amountToMint = processingInfo.valueDeposited * 100;
            }
            

            for (uint i = 0; i < amountToProcess; i++){
                if(userActionsSnapshot[i].deposit == true && userActionsSnapshot[i].amount != 0){
                    _mint(userActionsSnapshot[i].user, amountToMint * (userActionsSnapshot[i].amount * 10**18 / processingInfo.valueDeposited) / 10**18);
                }
            }
        }

        if(processingInfo.tokensWithdrawn > 0){
            
            uint256 WETHRecieved = (processingInfo.tokensWithdrawn * 10**18 / (totalSupply() + processingInfo.tokensWithdrawn)) * IERC20(WETHAddress).balanceOf(address(this)) / 10**18;
            address[] memory tokensHeldSnapshot = _tokensHeld;
            if(tokensHeldSnapshot.length > 0){
                for (uint i = 0; i < tokensHeldSnapshot.length; i++){
                    require(swapInfo[i].token == tokensHeldSnapshot[i]); 
                        WETHRecieved += _swapTokens(swapInfo[i].pathToWETHV2, swapInfo[i].pathToWETHV3, IERC20(tokensHeldSnapshot[i]).balanceOf(address(this)) * (processingInfo.tokensWithdrawn * 10**18 / (totalSupply() + processingInfo.tokensWithdrawn)) / 10**18, swapInfo[i].toWETHMinimumAmountOut); 
                    
                }
            } 
            processingInfo.platformFeeAmount += (WETHRecieved * _factoryFee / 10**18);
            processingInfo.ownerFeeAmount += WETHRecieved * _poolFee / 10**18;
            WETHRecieved = WETHRecieved - (WETHRecieved * _poolFee / 10**18) - (WETHRecieved * _factoryFee / 10**18);
            

            IWETH(WETHAddress).withdraw(WETHRecieved);

            for (uint i = 0; i < amountToProcess; i++){
                if(userActionsSnapshot[i].deposit == false && userActionsSnapshot[i].amount != 0){
                    userActionsSnapshot[i].user.call{value: WETHRecieved * (userActionsSnapshot[i].amount * 10**18 / processingInfo.tokensWithdrawn) / 10**18}("");
                }
            }
        }

        IWETH WETHContract = IWETH(WETHAddress);
        WETHContract.withdraw(processingInfo.ownerFeeAmount);
        owner().call{value: address(this).balance}("");
        processFee(processingInfo.platformFeeAmount);

        emit transactionsProcessed(processingInfo);
    }


    function removeFromPoolInstantly(uint256 amountToRemove,  swapInfoEntry[] memory swapInfo) public {
        require(balanceOf(msg.sender) > 0);
        require(amountToRemove > 0);
        require(amountToRemove <= balanceOf(msg.sender));
        require(swapInfo.length == _tokensHeld.length);

        _burn(msg.sender, amountToRemove);   

        if(balanceOf(msg.sender) == 0){
            _totalHolders--;
        }   
        uint256 userShare = amountToRemove * 10**18 / (totalSupply() + amountToRemove);
        uint256 wethPayout = IERC20(WETHAddress).balanceOf(address(this)) * userShare / 10**18;
        address[] memory tokensHeldSnapshot =  _tokensHeld;
        for (uint i = 0; i < tokensHeldSnapshot.length; i++){     
            require(swapInfo[i].token == tokensHeldSnapshot[i]);
             if((swapInfo[i].toWETHMinimumAmountOut == 0 && swapInfo[i].fromWETHMinimumAmountOut == 0) == false){
                IERC20 tokenContract = IERC20(tokensHeldSnapshot[i]);
                uint256 wethRecieved = _swapTokens(
                    swapInfo[i].pathToWETHV2,
                    swapInfo[i].pathToWETHV3,
                    tokenContract.balanceOf(address(this)) * userShare / 10**18,
                    swapInfo[i].toWETHMinimumAmountOut
                );
                wethPayout += wethRecieved;            
            }
        }

        uint256 poolOwnerFeeAmount = wethPayout * _poolFee / 10**18;
        uint256 factoryFeeAmount = wethPayout * _factoryFee / 10**18;
        uint256 amountToPayToUser = wethPayout - factoryFeeAmount - poolOwnerFeeAmount;

        IWETH WETHContract = IWETH(WETHAddress);
        IERC20(WETHAddress).transfer(owner(), poolOwnerFeeAmount);
        processFee(factoryFeeAmount);

        WETHContract.withdraw(amountToPayToUser);
        msg.sender.call{value: amountToPayToUser}("");

        emit feePaidToOwner(msg.sender, owner(), poolOwnerFeeAmount);
        emit poolRemovedFrom(msg.sender, amountToRemove, amountToPayToUser);
    }

    receive() payable external {}

    function processFee(uint256 amount) internal {
        address feeHandlerAddress = IFactory(_factoryAddress)._feeHandlerAddress();
        IERC20(WETHAddress).approve(feeHandlerAddress, amount);
        IFeeHandler(feeHandlerAddress).collectFees(amount);
    }

    function swapTokens(address[] memory v2Path, bytes memory v3Path, address[] memory inTokenPathToWethV2, bytes memory inTokenPathToWethV3, uint256 amountIn, uint256 amountOutMinimum) public onlyOwner {
        uint256 feeAmount = amountIn * 7500000000000000 / 10**18;
        address inToken = InvestmentLibrary.getInToken(v2Path, v3Path);
        if(inToken == WETHAddress){
            processFee(feeAmount); 
        } else {
            uint256 WETHRecieved =  _swapTokens(inTokenPathToWethV2, inTokenPathToWethV3, feeAmount, 0);
        }
               
        _swapTokens(v2Path, v3Path, amountIn - feeAmount, amountOutMinimum);
    }

    function _swapTokens(address[] memory v2Path, bytes memory v3Path, uint256 amountIn, uint256 amountOutMinimum) internal returns (uint256) {
        address tokenIn;
        address tokenOut;
        bytes memory packedBytes;
        if(v2Path.length > 0){
            tokenIn = v2Path[0];
            tokenOut = v2Path[v2Path.length - 1];
        } else {
            (tokenIn, tokenOut, packedBytes) = InvestmentLibrary.getInAndOutTokenAndPack(v3Path);
        }
        if(tokenIn != WETHAddress && IERC20(tokenIn).balanceOf(address(this)) - amountIn == 0 && InvestmentLibrary.isTokenHeld(_tokensHeld, tokenIn) == true){
            _removeAddressFromTokensHeld(tokenIn);
        }
        if(tokenOut != WETHAddress && IERC20(tokenOut).balanceOf(address(this)) == 0 && InvestmentLibrary.isTokenHeld(_tokensHeld, tokenOut) == false){
            _tokensHeld.push(tokenOut);
        }
        
        if(v2Path.length > 0){  
            uint256 balanceOfTokenBeforeSwap = IERC20(v2Path[v2Path.length - 1]).balanceOf(address(this));
            IERC20(v2Path[0]).approve(address(uniswapV2Router), amountIn);
            uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                amountIn,
                amountOutMinimum,
                v2Path,
                address(this),
                block.timestamp
            );
            return IERC20(v2Path[v2Path.length - 1]).balanceOf(address(this)) - balanceOfTokenBeforeSwap;
        } else {
            IERC20(tokenIn).approve(address(swapRouter), amountIn);
            ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams(
                packedBytes,
                address(this),
                block.timestamp,
                amountIn,
                amountOutMinimum
            );
            return swapRouter.exactInput(params);
        }
    }

}
