    /**
     * @notice Initializes the Uniswap V3 pool with a specific price.
     * @dev Calls the `initialize` function of the IUniswapV3Pool contract for the created pool, setting its initial price.
     * @param _sqrtPriceX96 The initial square root price of the pool to be set.
     */
    function initializePool(uint160 _sqrtPriceX96) external {
        IUniswapV3Pool(pool).initialize(_sqrtPriceX96);
    }

    /**
     * @dev Overrides the internal _update function to enforce a maximum transaction amount for transfers.
     * @notice Enforces a maximum transaction amount limit for transfers, except for transactions involving the owner.
     * @param from Address sending the tokens.
     * @param to Address receiving the tokens.
     * @param value Amount of tokens being transferred.
     */
    function _update(address from, address to, uint256 value) internal override {
      if(from != owner() && to != owner()){
          require(value <= maxTxAmount, "Transfer amount exceeds the maxTxAmount.");
      }
        super._update(from, to, value);
    }

    /**
     * @notice Allows the contract owner to recover tokens that are stuck in the contract.
     * @dev Transfers all tokens of a specific type from the contract to the contract owner.
     * @param _token The address of the token to recover.
     */
    function claimStuckTokens(address _token) external onlyOwner {
        IERC20 stuckToken = IERC20(_token);
        uint256 balance = stuckToken.balanceOf(address(this));
        require(stuckToken.transfer(msg.sender, balance), "Token transfer failed");
    }

    /**
     * @notice Adds liquidity to the Uniswap V3 pool for SchnitZeLcoin and ETH.
     * @dev Approves the positionManager to spend tokens, wraps ETH, and adds liquidity to the Uniswap pool through the NonfungiblePositionManager.
     * @param tokenAmount Amount of SchnitZeLcoin tokens to add as liquidity.
     * @param ethAmount Amount of ETH to add as liquidity.
     * @param deadline Time by which the transaction must be included to be valid.
     * @param tickLower Lower tick of the desired position.
     * @param tickUpper Upper tick of the desired position.
     * @param fee Fee tier for the liquidity position.
     */
    function addLiquidity(
        uint256 tokenAmount,
        uint256 ethAmount,
        uint256 deadline,
        int24 tickLower,
        int24 tickUpper,
        uint24 fee
    ) public payable {
        TransferHelper.safeApprove(address(this), address(positionManager), tokenAmount);
        IWETH9(positionManager.WETH9()).deposit{value: ethAmount}();
        TransferHelper.safeApprove(positionManager.WETH9(), address(positionManager), ethAmount);

        INonfungiblePositionManager.MintParams memory params = INonfungiblePositionManager.MintParams({
            token0: address(this) < positionManager.WETH9()? address(this) : positionManager.WETH9(),
            token1: address(this) > positionManager.WETH9() ? address(this) : positionManager.WETH9(),
            fee: fee,
            tickLower: tickLower,
            tickUpper: tickUpper,
            amount0Desired: address(this) < positionManager.WETH9() ? tokenAmount : ethAmount,
            amount1Desired: address(this) > positionManager.WETH9() ? tokenAmount : ethAmount,
            amount0Min: 0, 
            amount1Min: 0,
            recipient: owner(),
            deadline: deadline
        });
        positionManager.mint(params);
    }
}
