      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import {Ownable} from "../lib/openzeppelin-contracts/contracts/access/Ownable.sol";
import {Ownable2Step} from "../lib/openzeppelin-contracts/contracts/access/Ownable2Step.sol";

interface IENS {
    function resolver(bytes32 node) external view returns (address);
}

interface INameWrapper {
    function setSubnodeRecord(
        bytes32 parentNode,
        string memory label,
        address owner,
        address resolver,
        uint64 ttl,
        uint32 fuses,
        uint64 expiry
    ) external;

    function ens() external view returns (IENS);
}

contract DummyENSOwner is Ownable2Step {
    mapping (bytes32 => bool) ownershipGranted;

    INameWrapper constant NAME_WRAPPER = INameWrapper(0xD4416b13d2b3a9aBae7AcD5D6C2BbDBE25686401);
    bytes32 constant NAME_HASH_ETH = bytes32(0x93cdeb708b7545dc668eb9280176169d1c33cfd8ed6f04690a0bcc88a93fc4ae);

    constructor(address _owner) Ownable(_owner) {
    }

    function nameHash(string memory mainDomain) public pure returns(bytes32) {
        return keccak256(abi.encodePacked(NAME_HASH_ETH, keccak256(bytes(mainDomain))));
    }

    function setNewSubOwner(string memory mainDomain, string memory sub, address newOwner) public onlyOwner() {
        bytes32 node = nameHash(mainDomain);

        bytes32 key = keccak256(abi.encode(mainDomain, sub));
        require(! ownershipGranted[key], "ownership was already granted");

        address resolver = NAME_WRAPPER.ens().resolver(node);

        NAME_WRAPPER.setSubnodeRecord(
            node,
            sub,
            newOwner,
            resolver,
            0,
            0,
            0
        );

        ownershipGranted[key] = true;
    }

    function onERC1155Received(address, address, uint256, uint256, bytes memory) public virtual returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(address, address, uint256[] memory, uint256[] memory, bytes memory) public virtual returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

    function onERC721Received(address, address, uint256, bytes memory) public virtual returns (bytes4) {
        return this.onERC721Received.selector;
    }    
}"
