pragma solidity 0.8.17;

contract EthsMarketBridgeV2 is Initializable, OwnableUpgradeable, UUPSUpgradeable, ReentrancyGuardUpgradeable{

    using LibString for *;

    mapping(bytes32 =>  mapping(uint256 => bool)) private ethscriptions_signatures;
    mapping(bytes32 => bool) private ethscriptionsIds_state;

    uint256 public timeFrame;
    address public authorized_verifier;

    mapping(bytes32 => uint256) public withdraw_ethscriptionsIds_sign_time;

    event ethscriptions_protocol_TransferEthscriptionForPreviousOwner(
        address indexed previousOwner,
        address indexed recipient,
        bytes32 indexed ethscriptionId
    );
    event ethscriptions_protocol_TransferEthscription(address indexed recipient,bytes32 indexed ethscriptionId);

    event ethscriptions_protocol_CreateEthscription(
        address indexed initialOwner,
        string contentURI
    );

    mapping(address => bool) public controllers;

    function setController(address newController) external onlyOwner {
        controllers[newController] = true;
    }

    modifier onlyController() {
        require(controllers[msg.sender], "Error: only controller");
        _;
    }

    function ownerFinalizeWithdraw(
        address recipient,
        address dumbContractAddress,
        bytes32[] calldata ethscriptionIds,
        bytes32 withdrawalId
    ) external onlyOwner {
      string memory out = string.concat(
          'data:application/vnd.facet.tx+json;rule=esip6,{"op":"call","data":{"to":"',
          dumbContractAddress.toHexString(),
          '","function":"markWithdrawalComplete","args":{"to":"',
          recipient.toHexString(),
          '","withdrawalId":"',
          uint256(withdrawalId).toHexString(32),
          '"}}}'
      );

      emit ethscriptions_protocol_CreateEthscription(
        0x00000000000000000000000000000000000FacE7,
        string(out)
      );

      _transferEthscriptionsForPreviousOwner(
        recipient,
        recipient,
        ethscriptionIds
      );
    }

    function ownerConfirmDeposit(
        address sender,
        address dumbContractAddress,
        uint256 amount
    ) external onlyOwner {
        bytes memory out = abi.encodePacked(
            'data:application/vnd.facet.tx+json;rule=esip6,{"op":"call","data":{"to":"',
            dumbContractAddress.toHexString(),
            '","function":"bridgeIn","args":{"to":"',
            sender.toHexString(),
            '", "amount":"',
            amount.toString(),
            '"}}}'
        );

        emit ethscriptions_protocol_CreateEthscription(
          0x00000000000000000000000000000000000FacE7,
          string(out)
        );
    }

    function controllerTransferEthscriptionsForPreviousOwner(
        address previousOwner,
        address recipient,
        bytes32[] calldata ethscriptionIds
    ) external onlyController {
        _transferEthscriptionsForPreviousOwner(
            previousOwner,
            recipient,
            ethscriptionIds
        );
    }

    function controllerTransferEthscriptions(
      address recipient,
      bytes32[] calldata ethscriptionIds
    ) external onlyController {
        for (uint i = 0; i < ethscriptionIds.length;) {
            bytes32 ethscriptionId = ethscriptionIds[i];

            emit ethscriptions_protocol_TransferEthscription(
                recipient,
                ethscriptionId
            );

            unchecked {
                i += 1;
            }
        }
    }

    function _transferEthscriptionsForPreviousOwner(
        address previousOwner,
        address recipient,
        bytes32[] memory ethscriptionIds
    ) internal {
        for (uint i = 0; i < ethscriptionIds.length;) {
            bytes32 ethscriptionId = ethscriptionIds[i];

            emit ethscriptions_protocol_TransferEthscriptionForPreviousOwner(
                previousOwner,
                recipient,
                ethscriptionId
            );

            unchecked {
                i += 1;
            }
        }
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        onlyOwner
        override
    {}
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": false,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    }
  }
}}
