interface IUniswapV2Router02 is IUniswapV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountETH);

    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

contract ZENOVERSE is Context, IERC20, Ownable {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) public _isExcludedFromFee;
    mapping(address => bool) public _automatedMarketMakerPairs;

    address payable private _taxWallet =
        payable(0x511F307412543648d585908a54CfBD322d13cD15);

    uint256 public _swapTaxes = 15;

    uint8 private constant _decimals = 9;
    uint256 private constant _tTotal = 100_000_000 * 10 ** _decimals;

    string private constant _name = "Zenoverse";
    string private constant _symbol = "ZENOV";
    
    uint256 public maxTxAmount = _tTotal * 20 / 1000;
    uint256 private _taxSwap = 10000 * 10 ** _decimals;

    IUniswapV2Router02 public uniswapV2Router;
    address private uniswapV2Pair;
    bool public tradingOpen = false;
    bool private inSwap = false;
    bool public swapEnabled = false;

    // Events
    event ExcludeFromFeeUpdated(address indexed account);
    event includeFromFeeUpdated(address indexed account);
    event SwapThreshouldUpdated(uint256 indexed tokenAmount);
    event SwapBackSettingUpdated(bool indexed state);
    event TradingOpenUpdated();

    modifier lockTheSwap() {
        inSwap = true;
        _;
        inSwap = false;
    }

    constructor() {
        uniswapV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        ); // Uniswap ETH Mainnet Router

        _mint(_msgSender(), _tTotal);

        excludeFromFee(owner());
        excludeFromFee(address(this));
        excludeFromFee(_taxWallet);
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public pure override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(
            currentAllowance >= amount,
            "ERC20: transfer amount exceeds allowance"
        );
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), currentAllowance - amount);
        return true;
    }

    function _mint(address account, uint256 amount) private {
        _balances[account] = amount;
        emit Transfer(address(0), account, amount);
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        uint256 taxAmount;
        uint256 feeswap;

        if (!_isExcludedFromFee[from] && !_isExcludedFromFee[to]) {
            require(tradingOpen, "wait for trading to open");
        }

        if (
            _automatedMarketMakerPairs[from] &&
            !_isExcludedFromFee[from] &&
            !_isExcludedFromFee[to]
        ) {
            require(amount <= maxTxAmount, "Max TxAmount is 2%");
        }

        if (
            !_automatedMarketMakerPairs[from] &&
            !_isExcludedFromFee[from] &&
            !_isExcludedFromFee[to]
        ) {
            require(amount <= maxTxAmount, "Max TxAmount is 2%");
        }

        if (
            !_automatedMarketMakerPairs[to] &&
            !_isExcludedFromFee[from] &&
            !_isExcludedFromFee[to]
        ) {
            require(
                balanceOf(to) + amount <= maxTxAmount,
                "Max TxAmount is 2%"
            );
        }

        if (!swapEnabled || inSwap) {
            _balances[from] = _balances[from] - amount;
            _balances[to] = _balances[to] + amount;
            emit Transfer(from, to, amount);
        } else {
            taxAmount = amount;
            feeswap = (amount * (_swapTaxes)) / (100);

            if (_isExcludedFromFee[from]) {
                feeswap = 0;
            }

            uint256 contractTokenBalance = balanceOf(address(this));
            if (
                !inSwap &&
                !_automatedMarketMakerPairs[from] &&
                swapEnabled &&
                contractTokenBalance > 0 &&
                !_isExcludedFromFee[from]
            ) {
                swapTokensForEth(contractTokenBalance);

                uint256 contractETHBalance = address(this).balance;
                if (contractETHBalance > 0) {
                    sendETHToFee(address(this).balance);
                }
            }

            if (feeswap > 0) {
                _balances[from] = _balances[from] - (taxAmount);
                _balances[address(this)] = _balances[address(this)] + (feeswap);
                emit Transfer(from, address(this), feeswap);
            }

            _balances[to] = _balances[to] + (amount - (feeswap));
            emit Transfer(from, to, amount - (feeswap));
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private lockTheSwap {
        require(tokenAmount > 0, "amount must be greeter than 0");
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function sendETHToFee(uint256 amount) private {
        require(amount > 0, "amount must be greeter than 0");
        _taxWallet.transfer(amount);
    }

    function excludeFromFee(address account) public onlyOwner {
        require(
            _isExcludedFromFee[account] != true,
            "Account is already excluded"
        );
        _isExcludedFromFee[account] = true;
        emit ExcludeFromFeeUpdated(account);
    }

    function includeFromFee(address account) external onlyOwner {
        require(
            _isExcludedFromFee[account] != false,
            "Account is already included"
        );
        _isExcludedFromFee[account] = false;
        emit includeFromFeeUpdated(account);
    }

    function setAutomatedMarketMakerPair(
        address pair,
        bool value
    ) public onlyOwner {
        require(
            pair != uniswapV2Pair,
            "The pair cannot be removed from _automatedMarketMakerPairs"
        );

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        _automatedMarketMakerPairs[pair] = value;
    }

    function setSwapThreshould(uint256 tokenAmount) external onlyOwner {
        require(
            tokenAmount <= 1e7,
            "amount must be less than or equal to 1% of the supply"
        );
        require(
            tokenAmount >= 1e6,
            "amount must be greater than or equal to 0.1% of the supply"
        );
        _taxSwap = tokenAmount * 10 ** _decimals;
        emit SwapThreshouldUpdated(tokenAmount);
    }

    function removeTxLimits() external onlyOwner {
        _swapTaxes = 2;

        maxTxAmount = type(uint).max;
    }

    function setMaxTxLimits(uint256 amount) external onlyOwner {
        require(
            amount >= 1e6,
            "amount must be greater than or equal to 0.1% of the supply"
        );
        maxTxAmount = amount * 10 ** _decimals;
    }

    function setSwapBackSetting(bool state) external onlyOwner {
        swapEnabled = state;
        emit SwapBackSettingUpdated(state);
    }

    function addLiquidityETH() external payable onlyOwner {
        require(!tradingOpen, "trading is already open");

        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(
            address(this),
            uniswapV2Router.WETH()
        );
        uniswapV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );

        _approve(address(this), address(uniswapV2Router), _tTotal);
        _setAutomatedMarketMakerPair(uniswapV2Pair, true);

        uniswapV2Router.addLiquidityETH{value: msg.value}(
            address(this),
            balanceOf(address(this)),
            0,
            0,
            owner(),
            block.timestamp
        );
    }

    function enableTrading() external onlyOwner {
        require(!tradingOpen, "trading is already open");
        swapEnabled = true;
        tradingOpen = true;
        emit TradingOpenUpdated();
    }

    receive() external payable {}
}
